"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import  * as XLSX from "xlsx"

const PODetails = () => {
  const [poDetails, setPODetails] = useState([])

  // CSV Data for three tables
  const [poData, setPOData] = useState([])
  const [poHeaders, setPOHeaders] = useState([])
  const [grnData, setGRNData] = useState([])
  const [grnHeaders, setGRNHeaders] = useState([])
  const [indentData, setIndentData] = useState([])
  const [indentHeaders, setIndentHeaders] = useState([])

  const [showForm, setShowForm] = useState(false)
  const [editingPO, setEditingPO] = useState(null)
  const [uploadStatus, setUploadStatus] = useState("")
  const [isUploading, setIsUploading] = useState(false)
  const [activeTab, setActiveTab] = useState("po")

  // Track which CSV files have data
  const [availableTabs, setAvailableTabs] = useState({
    po: false,
    grn: false,
    indent: false,
  })

  const [formData, setFormData] = useState({
    poNumber: "",
    supplier: "",
    material: "",
    quantity: "",
    rate: "",
    deliveryDate: "",
    narration: "",
    status: "Active",
    poType: "",
  })
  const [errors, setErrors] = useState({})

  useEffect(() => {
    fetchPODetails()
    loadAllCSVData()
  }, [])

  const fetchPODetails = async () => {
    try {
      const res = await axios.get("http://localhost:5000/po")
      setPODetails(res.data)
    } catch (error) {
      console.error("Error fetching PO details:", error)
    }
  }

  const loadAllCSVData = async () => {
    const results = await Promise.all([
      loadCSVData("po", setPOData, setPOHeaders),
      loadCSVData("grn", setGRNData, setGRNHeaders),
      loadCSVData("indent", setIndentData, setIndentHeaders),
    ])

    // Update available tabs based on which CSVs have data
    const newAvailableTabs = {
      po: results[0],
      grn: results[1],
      indent: results[2],
    }
    setAvailableTabs(newAvailableTabs)

    // Set active tab to first available tab with data
    const firstAvailableTab = Object.keys(newAvailableTabs).find((tab) => newAvailableTabs[tab])
    if (firstAvailableTab && !newAvailableTabs[activeTab]) {
      setActiveTab(firstAvailableTab)
    }
  }

  const loadCSVData = async (type, setData, setHeaders) => {
    try {
      const response = await fetch(`http://localhost:8001/get-csv-data/${type}`)
      const result = await response.json()
      if (result.status === "success" && result.data.length > 0) {
        setHeaders(result.headers || [])
        setData(result.data || [])
        return true // Has data
      } else {
        setHeaders([])
        setData([])
        return false // No data
      }
    } catch (error) {
      console.log(`No ${type} data available yet`)
      setHeaders([])
      setData([])
      return false
    }
  }

  const handleJsonUpload = async (event) => {
    const file = event.target.files[0]
    if (!file) return

    if (!file.name.endsWith(".json")) {
      alert("Please select a JSON file")
      return
    }

    setIsUploading(true)
    setUploadStatus("Uploading and processing JSON file...")

    const formData = new FormData()
    formData.append("file", file)

    try {
      const response = await fetch("http://localhost:8001/upload-json/", {
        method: "POST",
        body: formData,
      })

      const result = await response.json()

      if (result.status === "success") {
        setUploadStatus("âœ… JSON processed successfully!")

        // Show processing details
        if (result.details && result.details.length > 0) {
          const details = result.details
            .map((d) => `${d.voucher_type}: ${d.rows_added} rows added (Total: ${d.total_rows})`)
            .join(", ")
          setUploadStatus(`âœ… JSON processed: ${details}`)
        }

        // Reload all CSV data
        await loadAllCSVData()

        setTimeout(() => setUploadStatus(""), 5000)
      } else {
        setUploadStatus(`âŒ Error: ${result.error}`)
        setTimeout(() => setUploadStatus(""), 5000)
      }
    } catch (error) {
      console.error("Upload error:", error)
      setUploadStatus("âŒ Upload failed. Please check if the JSON converter server is running on port 8001.")
      setTimeout(() => setUploadStatus(""), 5000)
    } finally {
      setIsUploading(false)
      event.target.value = ""
    }
  }

  const clearCSVData = async (type) => {
    try {
      const response = await fetch(`http://localhost:8001/clear-csv/${type}`, {
        method: "DELETE",
      })
      const result = await response.json()

      if (result.status === "success") {
        setUploadStatus(`âœ… ${type.toUpperCase()} data cleared`)

        // Clear the specific data and update available tabs
        if (type === "po") {
          setPOData([])
          setPOHeaders([])
          setAvailableTabs((prev) => ({ ...prev, po: false }))
        } else if (type === "grn") {
          setGRNData([])
          setGRNHeaders([])
          setAvailableTabs((prev) => ({ ...prev, grn: false }))
        } else if (type === "indent") {
          setIndentData([])
          setIndentHeaders([])
          setAvailableTabs((prev) => ({ ...prev, indent: false }))
        }

        // Switch to first available tab if current tab was cleared
        const updatedTabs = { ...availableTabs, [type]: false }
        const firstAvailableTab = Object.keys(updatedTabs).find((tab) => updatedTabs[tab])
        if (activeTab === type && firstAvailableTab) {
          setActiveTab(firstAvailableTab)
        }

        setTimeout(() => setUploadStatus(""), 2000)
      }
    } catch (error) {
      console.error("Error clearing data:", error)
    }
  }

  const downloadCSV = (type) => {
    window.open(`http://localhost:8001/download/${type}`, "_blank")
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    const formErrors = validateForm()
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors)
      return
    }

    const payload = {
      ...formData,
      quantity: Number.parseFloat(formData.quantity),
      rate: Number.parseFloat(formData.rate),
      totalAmount: Number.parseFloat(formData.quantity) * Number.parseFloat(formData.rate),
    }

    try {
      if (editingPO) {
        await axios.put(`http://localhost:5000/po/${editingPO.id}`, payload)
      } else {
        await axios.post("http://localhost:5000/po", payload)
      }

      fetchPODetails()
      resetForm()
    } catch (error) {
      console.error("Error saving PO:", error)
      alert("Error saving PO. Please try again.")
    }
  }

  const handleDelete = async (id) => {
    if (window.confirm("Delete this PO?")) {
      try {
        await axios.delete(`http://localhost:5000/po/${id}`)
        fetchPODetails()
      } catch (error) {
        console.error("Error deleting PO:", error)
        alert("Error deleting PO. Please try again.")
      }
    }
  }

  const handleEdit = (po) => {
    setFormData(po)
    setEditingPO(po)
    setShowForm(true)
  }

  const resetForm = () => {
    setFormData({
      poNumber: "",
      supplier: "",
      material: "",
      quantity: "",
      rate: "",
      deliveryDate: "",
      narration: "",
      status: "Active",
      poType: "",
    })
    setErrors({})
    setEditingPO(null)
    setShowForm(false)
  }

  const validateForm = () => {
    const newErrors = {}
    if (!formData.poNumber) newErrors.poNumber = "Required"
    if (!formData.supplier) newErrors.supplier = "Required"
    if (!formData.material) newErrors.material = "Required"
    if (!formData.quantity || formData.quantity <= 0) newErrors.quantity = "Invalid"
    if (!formData.rate || formData.rate <= 0) newErrors.rate = "Invalid"
    if (!formData.deliveryDate) newErrors.deliveryDate = "Required"
    if (!formData.poType) newErrors.poType = "Required"
    return newErrors
  }

  const exportToExcel = () => {
    const ws = XLSX.utils.json_to_sheet(poDetails)
    const wb = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(wb, ws, "PO")
    XLSX.writeFile(wb, "po_details.xlsx")
  }

  const renderCSVTable = (data, headers, type) => {
    if (data.length === 0) {
      return (
        <div className="no-data">
          <p>ðŸ“Š No {type.toUpperCase()} data available.</p>
          <p>Upload a JSON file to see {type} vouchers here.</p>
        </div>
      )
    }

    return (
      <div style={{ maxHeight: "400px", overflowY: "auto", overflowX: "auto" }}>
        <table className="po-table">
          <thead>
            <tr>
              <th>S.No</th>
              {headers.map((header, index) => (
                <th key={index}>{header}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, rowIndex) => (
              <tr key={rowIndex}>
                <td>{rowIndex + 1}</td>
                {row.map((cell, cellIndex) => (
                  <td key={cellIndex}>{cell || ""}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    )
  }

  // Check if any CSV data is available
  const hasAnyCSVData = Object.values(availableTabs).some((hasData) => hasData)

  return (
    <div className="po-wrapper">
      <div className="po-header">
        <h2>Purchase Order Management</h2>
        <div>
          <button className="btn export" onClick={exportToExcel}>
            Export Excel
          </button>
          <button className="btn add" onClick={() => setShowForm(true)}>
            Add PO
          </button>
        </div>
      </div>

      {/* Original PO Details Table */}
      <table className="po-table">
        <thead>
          <tr>
            <th>S.No</th>
            <th>PO Number</th>
            <th>Supplier</th>
            <th>Material</th>
            <th>Qty</th>
            <th>Rate</th>
            <th>Amount</th>
            <th>Delivery</th>
            <th>Type</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {poDetails.map((po, i) => (
            <tr key={po.id}>
              <td>{i + 1}</td>
              <td>{po.poNumber}</td>
              <td>{po.supplier}</td>
              <td>{po.material}</td>
              <td>{po.quantity}</td>
              <td>â‚¹{po.rate}</td>
              <td>â‚¹{po.totalAmount}</td>
              <td>{new Date(po.deliveryDate).toLocaleDateString()}</td>
              <td>{po.poType}</td>
              <td>{po.status}</td>
              <td>
                <button className="btn edit" onClick={() => handleEdit(po)}>
                  âœ
                </button>
                <button className="btn delete" onClick={() => handleDelete(po.id)}>
                  ðŸ—‘
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* JSON to CSV Conversion Section */}
      <div className="csv-section">
        <div className="po-header">
          <h3>JSON Voucher Processor</h3>
          <div>
            <input
              type="file"
              accept=".json"
              onChange={handleJsonUpload}
              style={{ display: "none" }}
              id="json-upload"
              disabled={isUploading}
            />
            <label htmlFor="json-upload" className={`btn add ${isUploading ? "disabled" : ""}`}>
              {isUploading ? "Processing..." : "ðŸ“ Upload JSON"}
            </label>
          </div>
        </div>

        {uploadStatus && (
          <div className={`upload-status ${uploadStatus.includes("âŒ") ? "error" : "success"}`}>{uploadStatus}</div>
        )}

        {/* Show tabs only if there's CSV data available */}
        {hasAnyCSVData && (
          <>
            {/* Tab Navigation - Only show tabs that have data */}
            <div className="tab-navigation">
              {availableTabs.po && (
                <button className={`tab-btn ${activeTab === "po" ? "active" : ""}`} onClick={() => setActiveTab("po")}>
                  Purchase Orders ({poData.length})
                </button>
              )}
              {availableTabs.grn && (
                <button
                  className={`tab-btn ${activeTab === "grn" ? "active" : ""}`}
                  onClick={() => setActiveTab("grn")}
                >
                  GRN ({grnData.length})
                </button>
              )}
              {availableTabs.indent && (
                <button
                  className={`tab-btn ${activeTab === "indent" ? "active" : ""}`}
                  onClick={() => setActiveTab("indent")}
                >
                  Indent ({indentData.length})
                </button>
              )}
            </div>

            {/* Tab Content */}
            <div className="tab-content">
              {activeTab === "po" && availableTabs.po && (
                <div className="table-section">
                  <div className="table-header">
                    <h4>Purchase Order Details</h4>
                    <div>
                      <button className="btn export" onClick={() => downloadCSV("po")}>
                        ðŸ“¥ Download CSV
                      </button>
                      <button className="btn delete" onClick={() => clearCSVData("po")}>
                        Clear Data
                      </button>
                    </div>
                  </div>
                  {renderCSVTable(poData, poHeaders, "purchase order")}
                </div>
              )}

              {activeTab === "grn" && availableTabs.grn && (
                <div className="table-section">
                  <div className="table-header">
                    <h4>GRN Details</h4>
                    <div>
                      <button className="btn export" onClick={() => downloadCSV("grn")}>
                        ðŸ“¥ Download CSV
                      </button>
                      <button className="btn delete" onClick={() => clearCSVData("grn")}>
                        Clear Data
                      </button>
                    </div>
                  </div>
                  {renderCSVTable(grnData, grnHeaders, "grn")}
                </div>
              )}

              {activeTab === "indent" && availableTabs.indent && (
                <div className="table-section">
                  <div className="table-header">
                    <h4>Indent Details</h4>
                    <div>
                      <button className="btn export" onClick={() => downloadCSV("indent")}>
                        ðŸ“¥ Download CSV
                      </button>
                      <button className="btn delete" onClick={() => clearCSVData("indent")}>
                        Clear Data
                      </button>
                    </div>
                  </div>
                  {renderCSVTable(indentData, indentHeaders, "indent")}
                </div>
              )}
            </div>
          </>
        )}

        {/* Show message when no CSV data is available */}
        {!hasAnyCSVData && !isUploading && (
          <div className="no-csv-data">
            <div className="no-data">
              <p>ðŸ“Š No CSV data available.</p>
              <p>Upload a JSON file containing vouchers to see data organized in tables.</p>
              <p>
                <strong>Supported voucher types:</strong> Purchase Order, GRN, Indent
              </p>
            </div>
          </div>
        )}
      </div>

      {showForm && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>{editingPO ? "Edit PO" : "Add PO"}</h3>
            <form onSubmit={handleSubmit} className="po-form">
              <input
                placeholder="PO Number"
                value={formData.poNumber}
                onChange={(e) => setFormData({ ...formData, poNumber: e.target.value })}
              />
              <input
                placeholder="Supplier"
                value={formData.supplier}
                onChange={(e) => setFormData({ ...formData, supplier: e.target.value })}
              />
              <input
                placeholder="Material"
                value={formData.material}
                onChange={(e) => setFormData({ ...formData, material: e.target.value })}
              />
              <input
                type="number"
                placeholder="Quantity"
                value={formData.quantity}
                onChange={(e) => setFormData({ ...formData, quantity: e.target.value })}
              />
              <input
                type="number"
                placeholder="Rate"
                value={formData.rate}
                onChange={(e) => setFormData({ ...formData, rate: e.target.value })}
              />
              <input
                type="date"
                value={formData.deliveryDate}
                onChange={(e) => setFormData({ ...formData, deliveryDate: e.target.value })}
              />
              <textarea
                placeholder="Narration"
                value={formData.narration}
                onChange={(e) => setFormData({ ...formData, narration: e.target.value })}
              />

              <select value={formData.poType} onChange={(e) => setFormData({ ...formData, poType: e.target.value })}>
                <option value="">Select PO Type</option>
                <option value="Local">Local</option>
                <option value="Import">Import</option>
              </select>

              <select value={formData.status} onChange={(e) => setFormData({ ...formData, status: e.target.value })}>
                <option value="Active">Active</option>
                <option value="Completed">Completed</option>
                <option value="Cancelled">Cancelled</option>
                <option value="Pending">Pending</option>
              </select>

              <div className="form-buttons">
                <button type="button" className="btn cancel" onClick={resetForm}>
                  Cancel
                </button>
                <button type="submit" className="btn save">
                  Save PO
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <style jsx>{`
        .csv-section {
          border-top: 2px solid #eee;
          padding-top: 20px;
          margin-top: 40px;
        }

        .upload-status {
          padding: 10px;
          border-radius: 4px;
          margin: 10px 0;
          font-weight: 500;
        }

        .upload-status.success {
          background-color: #d4edda;
          color: #155724;
          border: 1px solid #c3e6cb;
        }

        .upload-status.error {
          background-color: #f8d7da;
          color: #721c24;
          border: 1px solid #f5c6cb;
        }

        .btn.disabled {
          opacity: 0.6;
          cursor: not-allowed;
        }

        .tab-navigation {
          display: flex;
          border-bottom: 2px solid #eee;
          margin: 20px 0;
          flex-wrap: wrap;
        }

        .tab-btn {
          padding: 10px 20px;
          border: none;
          background: #f8f9fa;
          cursor: pointer;
          border-bottom: 3px solid transparent;
          margin-right: 5px;
          margin-bottom: 5px;
          font-weight: 500;
          border-radius: 4px 4px 0 0;
        }

        .tab-btn.active {
          background: white;
          border-bottom-color: #007bff;
          color: #007bff;
        }

        .tab-btn:hover {
          background: #e9ecef;
        }

        .table-section {
          margin-top: 20px;
        }

        .table-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 15px;
          flex-wrap: wrap;
          gap: 10px;
        }

        .table-header h4 {
          margin: 0;
          color: #333;
        }

        .table-header div {
          display: flex;
          gap: 10px;
          flex-wrap: wrap;
        }

        .no-data {
          text-align: center;
          padding: 40px;
          background: #f8f9fa;
          border-radius: 8px;
          border: 2px dashed #dee2e6;
          color: #666;
        }

        .no-data p {
          margin: 10px 0;
        }

        .no-csv-data {
          margin-top: 20px;
        }

        @media (max-width: 768px) {
          .table-header {
            flex-direction: column;
            align-items: flex-start;
          }

          .tab-navigation {
            flex-direction: column;
          }

          .tab-btn {
            margin-right: 0;
            margin-bottom: 5px;
          }
        }
      `}</style>
    </div>
  )
}

