"use client"

import { useState } from "react"

const CreateBatchSlip = () => {
  const [formData, setFormData] = useState({
    batchSlipNo: "",
    date: new Date().toISOString().split("T")[0],
    supplier: "",
    material: "",
    quantity: "",
    vehicleNo: "",
    remarks: "",
  })

  const [errors, setErrors] = useState({})

  const generateBatchSlipNo = () => {
    const date = new Date()
    const year = date.getFullYear()
    const month = String(date.getMonth() + 1).padStart(2, "0")
    const sequence = String(Math.floor(Math.random() * 1000) + 1).padStart(3, "0")
    return `BS${year}${month}${sequence}`
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    // Form submission logic will be implemented here
    console.log("Batch slip created:", formData)
  }

  const resetForm = () => {
    setFormData({
      batchSlipNo: "",
      date: new Date().toISOString().split("T")[0],
      supplier: "",
      material: "",
      quantity: "",
      vehicleNo: "",
      remarks: "",
    })
    setErrors({})
  }

  return (
    <div className="create-batch-slip">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">📝</div>
          <h2>Create Batch Slip</h2>
        </div>
        <div className="header-actions">
          <button
            className="btn btn-generate"
            onClick={() => setFormData({ ...formData, batchSlipNo: generateBatchSlipNo() })}
          >
            🔄 Generate Slip No
          </button>
        </div>
      </div>

      <div className="batch-slip-form-container">
        <form onSubmit={handleSubmit} className="batch-slip-form">
          <div className="form-grid">
            <div className="form-group">
              <label>Batch Slip No *</label>
              <input
                type="text"
                value={formData.batchSlipNo}
                onChange={(e) => setFormData({ ...formData, batchSlipNo: e.target.value })}
                placeholder="Auto-generated or enter manually"
                className={errors.batchSlipNo ? "error" : ""}
              />
              {errors.batchSlipNo && <span className="error-text">{errors.batchSlipNo}</span>}
            </div>

            <div className="form-group">
              <label>Date *</label>
              <input
                type="date"
                value={formData.date}
                onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                className={errors.date ? "error" : ""}
              />
              {errors.date && <span className="error-text">{errors.date}</span>}
            </div>

            <div className="form-group">
              <label>Supplier *</label>
              <input
                type="text"
                value={formData.supplier}
                onChange={(e) => setFormData({ ...formData, supplier: e.target.value })}
                placeholder="Enter supplier name"
                className={errors.supplier ? "error" : ""}
              />
              {errors.supplier && <span className="error-text">{errors.supplier}</span>}
            </div>

            <div className="form-group">
              <label>Material *</label>
              <input
                type="text"
                value={formData.material}
                onChange={(e) => setFormData({ ...formData, material: e.target.value })}
                placeholder="Enter material type"
                className={errors.material ? "error" : ""}
              />
              {errors.material && <span className="error-text">{errors.material}</span>}
            </div>

            <div className="form-group">
              <label>Quantity *</label>
              <input
                type="number"
                step="0.01"
                value={formData.quantity}
                onChange={(e) => setFormData({ ...formData, quantity: e.target.value })}
                placeholder="Enter quantity"
                className={errors.quantity ? "error" : ""}
              />
              {errors.quantity && <span className="error-text">{errors.quantity}</span>}
            </div>

            <div className="form-group">
              <label>Vehicle No *</label>
              <input
                type="text"
                value={formData.vehicleNo}
                onChange={(e) => setFormData({ ...formData, vehicleNo: e.target.value.toUpperCase() })}
                placeholder="Enter vehicle number"
                className={errors.vehicleNo ? "error" : ""}
              />
              {errors.vehicleNo && <span className="error-text">{errors.vehicleNo}</span>}
            </div>

            <div className="form-group full-width">
              <label>Remarks</label>
              <textarea
                value={formData.remarks}
                onChange={(e) => setFormData({ ...formData, remarks: e.target.value })}
                placeholder="Enter any remarks"
                rows="4"
              />
            </div>
          </div>

          <div className="form-actions">
            <button type="button" className="btn btn-cancel" onClick={resetForm}>
              Cancel
            </button>
            <button type="submit" className="btn btn-save">
              Create Batch Slip
            </button>
          </div>
        </form>
      </div>
    </div>
  )
}

export default CreateBatchSlip
