"use client"

import { useState, useEffect } from "react"
import * as XLSX from "xlsx"

const SupplierPaymentDetails = () => {
  const [paymentDetails, setPaymentDetails] = useState([])
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState("")
  const [filterStatus, setFilterStatus] = useState("All")

  useEffect(() => {
    fetchPaymentDetails()
  }, [])

  const fetchPaymentDetails = async () => {
    try {
      setLoading(true)
      const response = await fetch("http://localhost:5000/supplier-payment-stats")
      const data = await response.json()
      setPaymentDetails(data)
    } catch (error) {
      console.error("Error fetching payment details:", error)
    } finally {
      setLoading(false)
    }
  }

  const exportToExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(paymentDetails)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "Supplier Payments")
    XLSX.writeFile(workbook, "supplier_payment_details.xlsx")
  }

  const filteredPayments = paymentDetails.filter((payment) => {
    const matchesSearch =
      payment.supplier.toLowerCase().includes(searchTerm.toLowerCase()) ||
      payment.material.toLowerCase().includes(searchTerm.toLowerCase())

    if (filterStatus === "All") return matchesSearch

    const pendingAmount = Number.parseFloat(payment.pendingAmount)
    const isPaid = pendingAmount === 0
    const isPartiallyPaid = pendingAmount > 0 && pendingAmount < Number.parseFloat(payment.totalAmount)
    const isPending = pendingAmount === Number.parseFloat(payment.totalAmount)

    switch (filterStatus) {
      case "Paid":
        return matchesSearch && isPaid
      case "Partially Paid":
        return matchesSearch && isPartiallyPaid
      case "Pending":
        return matchesSearch && isPending
      default:
        return matchesSearch
    }
  })

  const getPaymentStatus = (totalAmount, pendingAmount) => {
    const total = Number.parseFloat(totalAmount)
    const pending = Number.parseFloat(pendingAmount)

    if (pending === 0) return { status: "Paid", class: "paid" }
    if (pending === total) return { status: "Pending", class: "pending" }
    return { status: "Partially Paid", class: "partial" }
  }

  const calculateTotals = () => {
    const totals = filteredPayments.reduce(
      (acc, payment) => {
        acc.totalAmount += Number.parseFloat(payment.totalAmount)
        acc.paidAmount += Number.parseFloat(payment.paidAmount)
        acc.pendingAmount += Number.parseFloat(payment.pendingAmount)
        return acc
      },
      { totalAmount: 0, paidAmount: 0, pendingAmount: 0 },
    )

    return totals
  }

  const totals = calculateTotals()

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading payment details...</p>
      </div>
    )
  }

  return (
    <div className="supplier-payment-details">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">üí∞</div>
          <h2>Supplier Payment Details</h2>
        </div>
        <div className="header-actions">
          <button className="btn btn-refresh" onClick={fetchPaymentDetails}>
            üîÑ Refresh
          </button>
          <button className="btn btn-export" onClick={exportToExcel}>
            üìä Export Excel
          </button>
        </div>
      </div>

      <div className="payment-summary">
        <div className="summary-cards">
          <div className="summary-card total">
            <div className="card-icon">üíº</div>
            <div className="card-content">
              <h3>Total Amount</h3>
              <p className="amount">‚Çπ{totals.totalAmount.toLocaleString()}</p>
            </div>
          </div>

          <div className="summary-card paid">
            <div className="card-icon">‚úÖ</div>
            <div className="card-content">
              <h3>Paid Amount</h3>
              <p className="amount">‚Çπ{totals.paidAmount.toLocaleString()}</p>
            </div>
          </div>

          <div className="summary-card pending">
            <div className="card-icon">‚è≥</div>
            <div className="card-content">
              <h3>Pending Amount</h3>
              <p className="amount">‚Çπ{totals.pendingAmount.toLocaleString()}</p>
            </div>
          </div>

          <div className="summary-card percentage">
            <div className="card-icon">üìä</div>
            <div className="card-content">
              <h3>Payment Progress</h3>
              <p className="amount">
                {totals.totalAmount > 0 ? Math.round((totals.paidAmount / totals.totalAmount) * 100) : 0}%
              </p>
            </div>
          </div>
        </div>
      </div>

      <div className="filters-section">
        <div className="filters-row">
          <div className="search-group">
            <input
              type="text"
              placeholder="Search by supplier or material..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          </div>

          <div className="filter-group">
            <select value={filterStatus} onChange={(e) => setFilterStatus(e.target.value)} className="filter-select">
              <option value="All">All Status</option>
              <option value="Paid">Paid</option>
              <option value="Partially Paid">Partially Paid</option>
              <option value="Pending">Pending</option>
            </select>
          </div>
        </div>
      </div>

      <div className="payment-table">
        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>Supplier</th>
              <th>Material</th>
              <th>Quantity Ordered</th>
              <th>Total Amount</th>
              <th>Paid Amount</th>
              <th>Pending Amount</th>
              <th>Payment Status</th>
              <th>Progress</th>
            </tr>
          </thead>
          <tbody>
            {filteredPayments.map((payment, index) => {
              const paymentStatus = getPaymentStatus(payment.totalAmount, payment.pendingAmount)
              const progressPercentage = (
                (Number.parseFloat(payment.paidAmount) / Number.parseFloat(payment.totalAmount)) *
                100
              ).toFixed(1)

              return (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td className="supplier-cell">{payment.supplier}</td>
                  <td className="material-cell">{payment.material}</td>
                  <td>{payment.quantityOrdered}</td>
                  <td className="amount-cell">‚Çπ{Number.parseFloat(payment.totalAmount).toLocaleString()}</td>
                  <td className="amount-cell paid">‚Çπ{Number.parseFloat(payment.paidAmount).toLocaleString()}</td>
                  <td className="amount-cell pending">‚Çπ{Number.parseFloat(payment.pendingAmount).toLocaleString()}</td>
                  <td>
                    <span className={`status ${paymentStatus.class}`}>{paymentStatus.status}</span>
                  </td>
                  <td>
                    <div className="progress-bar">
                      <div className="progress-fill" style={{ width: `${progressPercentage}%` }}></div>
                      <span className="progress-text">{progressPercentage}%</span>
                    </div>
                  </td>
                </tr>
              )
            })}
          </tbody>
        </table>

        {filteredPayments.length === 0 && (
          <div className="no-data">
            <div className="no-data-icon">üí∞</div>
            <p>No payment details found</p>
          </div>
        )}
      </div>
    </div>
  )
}

export default SupplierPaymentDetails
