"use client"

import { useState, useEffect } from "react"

const API_URL = "http://localhost:5000"

const ProjectManagement = () => {
  const [projects, setProjects] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [editingProject, setEditingProject] = useState(null)
  const [formData, setFormData] = useState({
    projectName: "",
    address: "",
    latitude: "",
    longitude: "",
  })
  const [errors, setErrors] = useState({})

  useEffect(() => {
    fetchProjects()
  }, [])

  const fetchProjects = async () => {
    const res = await fetch(`${API_URL}/projects`)
    const data = await res.json()
    setProjects(data)
  }

  const validateForm = () => {
    const newErrors = {}
    const { projectName, address, latitude, longitude } = formData

    if (!projectName.trim()) newErrors.projectName = "Project name is required"
    if (!address.trim()) newErrors.address = "Address is required"
    if (!latitude.trim()) newErrors.latitude = "Latitude is required"
    if (!longitude.trim()) newErrors.longitude = "Longitude is required"

    const lat = parseFloat(latitude)
    const lng = parseFloat(longitude)

    if (isNaN(lat) || lat < -90 || lat > 90)
      newErrors.latitude = "Latitude must be between -90 and 90"
    if (isNaN(lng) || lng < -180 || lng > 180)
      newErrors.longitude = "Longitude must be between -180 and 180"

    return newErrors
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    const formErrors = validateForm()
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors)
      return
    }

    const payload = {
      projectName: formData.projectName,
      address: formData.address,
      latitude: formData.latitude,
      longitude: formData.longitude,
    }

    try {
      if (editingProject) {
        await fetch(`${API_URL}/projects/${editingProject.id}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(payload),
        })
      } else {
        await fetch(`${API_URL}/projects`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(payload),
        })
      }

      fetchProjects()
      resetForm()
    } catch (err) {
      console.error("Error saving project:", err)
    }
  }

  const resetForm = () => {
    setFormData({ projectName: "", address: "", latitude: "", longitude: "" })
    setErrors({})
    setShowForm(false)
    setEditingProject(null)
  }

  const handleEdit = (project) => {
    setFormData({
      projectName: project.project_name,
      address: project.address,
      latitude: project.latitude,
      longitude: project.longitude,
    })
    setEditingProject(project)
    setShowForm(true)
  }

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this project?")) {
      await fetch(`${API_URL}/projects/${id}`, { method: "DELETE" })
      fetchProjects()
    }
  }

  const openInMaps = (lat, lng) => {
    window.open(`https://www.google.com/maps?q=${lat},${lng}`, "_blank")
  }

  return (
    <div className="project-management">
      <h2>🏗️ Project Management</h2>

      <div className="header-actions">
        <button onClick={() => setShowForm(true)}>➕ Add Project</button>
      </div>

      {showForm && (
        <div className="modal">
          <h3>{editingProject ? "Edit Project" : "Add Project"}</h3>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Project Name"
              value={formData.projectName}
              onChange={(e) => setFormData({ ...formData, projectName: e.target.value })}
            />
            {errors.projectName && <span>{errors.projectName}</span>}

            <textarea
              placeholder="Address"
              value={formData.address}
              onChange={(e) => setFormData({ ...formData, address: e.target.value })}
            />
            {errors.address && <span>{errors.address}</span>}

            <input
              type="number"
              placeholder="Latitude"
              value={formData.latitude}
              onChange={(e) => setFormData({ ...formData, latitude: e.target.value })}
            />
            {errors.latitude && <span>{errors.latitude}</span>}

            <input
              type="number"
              placeholder="Longitude"
              value={formData.longitude}
              onChange={(e) => setFormData({ ...formData, longitude: e.target.value })}
            />
            {errors.longitude && <span>{errors.longitude}</span>}

            <button type="submit">{editingProject ? "Update" : "Save"} Project</button>
            <button type="button" onClick={resetForm}>Cancel</button>
          </form>
        </div>
      )}

      <table>
        <thead>
          <tr>
            <th>S.No</th>
            <th>Project Name</th>
            <th>Address</th>
            <th>Coordinates</th>
            <th>Created At</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {projects.map((p, i) => (
            <tr key={p.id}>
              <td>{i + 1}</td>
              <td>{p.project_name}</td>
              <td>{p.address}</td>
              <td>
                {p.latitude}, {p.longitude}
                <button onClick={() => openInMaps(p.latitude, p.longitude)}>🗺️</button>
              </td>
              <td>{new Date(p.created_at).toLocaleDateString()}</td>
              <td>
                <button onClick={() => handleEdit(p)}>✏️</button>
                <button onClick={() => handleDelete(p.id)}>🗑️</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {projects.length === 0 && <p>No projects found.</p>}
    </div>
  )
}

export default ProjectManagement
