                    </div>

                    {/* Footer */}
                    <div style={{ fontSize: "12px" }}>
                      <p>
                        <strong>Batcher Name :</strong>
                      </p>
                      <p>{modalContent.batcherName}</p>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

