"use client"

import { useState, useEffect } from "react"
import * as XLSX from "xlsx"

const PODetails = () => {
  const [poDetails, setPODetails] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [editingPO, setEditingPO] = useState(null)
  const [formData, setFormData] = useState({
    poNumber: "",
    material: "",
    supplier: "",
    quantity: "",
    rate: "",
    poType: "",
    poReference: "",
    deliveryDate: "",
    narration: "",
    status: "Active",
  })
  const [errors, setErrors] = useState({})

  useEffect(() => {
    const savedPODetails = localStorage.getItem("poDetails")
    if (savedPODetails) {
      setPODetails(JSON.parse(savedPODetails))
    }
  }, [])

  const savePODetails = (details) => {
    localStorage.setItem("poDetails", JSON.stringify(details))
    setPODetails(details)
  }

  const generatePONumber = () => {
    const date = new Date()
    const year = date.getFullYear()
    const month = String(date.getMonth() + 1).padStart(2, "0")
    const sequence = String(poDetails.length + 1).padStart(4, "0")
    return `PO${year}${month}${sequence}`
  }

  const validateForm = () => {
    const newErrors = {}

    if (!formData.poNumber.trim()) newErrors.poNumber = "PO Number is required"
    if (!formData.material.trim()) newErrors.material = "Material is required"
    if (!formData.supplier.trim()) newErrors.supplier = "Supplier is required"
    if (!formData.quantity.trim()) newErrors.quantity = "Quantity is required"
    if (!formData.rate.trim()) newErrors.rate = "Rate is required"
    if (!formData.poType.trim()) newErrors.poType = "PO Type is required"
    if (!formData.deliveryDate) newErrors.deliveryDate = "Delivery Date is required"

    // Validate quantity and rate are positive numbers
    if (formData.quantity && Number.parseFloat(formData.quantity) <= 0) {
      newErrors.quantity = "Quantity must be greater than 0"
    }
    if (formData.rate && Number.parseFloat(formData.rate) <= 0) {
      newErrors.rate = "Rate must be greater than 0"
    }

    return newErrors
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    const formErrors = validateForm()

    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors)
      return
    }

    const totalAmount = (Number.parseFloat(formData.quantity) * Number.parseFloat(formData.rate)).toFixed(2)

    const newPO = {
      ...formData,
      id: editingPO ? editingPO.id : Date.now(),
      totalAmount: totalAmount,
      createdAt: editingPO ? editingPO.createdAt : new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    }

    let updatedPODetails
    if (editingPO) {
      updatedPODetails = poDetails.map((po) => (po.id === editingPO.id ? newPO : po))
    } else {
      updatedPODetails = [...poDetails, newPO]
    }

    savePODetails(updatedPODetails)
    resetForm()
  }

  const resetForm = () => {
    setFormData({
      poNumber: "",
      material: "",
      supplier: "",
      quantity: "",
      rate: "",
      poType: "",
      poReference: "",
      deliveryDate: "",
      narration: "",
      status: "Active",
    })
    setErrors({})
    setShowForm(false)
    setEditingPO(null)
  }

  const handleEdit = (po) => {
    setFormData(po)
    setEditingPO(po)
    setShowForm(true)
  }

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this PO?")) {
      const updatedPODetails = poDetails.filter((po) => po.id !== id)
      savePODetails(updatedPODetails)
    }
  }

  const handleStatusChange = (id, newStatus) => {
    const updatedPODetails = poDetails.map((po) =>
      po.id === id ? { ...po, status: newStatus, updatedAt: new Date().toISOString() } : po,
    )
    savePODetails(updatedPODetails)
  }

  const exportToExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(poDetails)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "PO Details")
    XLSX.writeFile(workbook, "po_details.xlsx")
  }

  return (
    <div className="po-details">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">📋</div>
          <h2>Purchase Order Details</h2>
        </div>
        <div className="header-actions">
          <button className="btn btn-export" onClick={exportToExcel}>
            📊 Export Excel
          </button>
          <button
            className="btn btn-add"
            onClick={() => {
              setFormData((prev) => ({
                ...prev,
                poNumber: generatePONumber(),
              }))
              setShowForm(true)
            }}
          >
            ➕ Add PO
          </button>
        </div>
      </div>

      {showForm && (
        <div className="modal-overlay">
          <div className="modal large-modal">
            <div className="modal-header">
              <h3>{editingPO ? "Edit Purchase Order" : "Add Purchase Order"}</h3>
              <button className="close-btn" onClick={resetForm}>
                ✕
              </button>
            </div>

            <form onSubmit={handleSubmit} className="po-form">
              <div className="form-grid">
                <div className="form-group">
                  <label>PO Number *</label>
                  <input
                    type="text"
                    value={formData.poNumber}
                    onChange={(e) => setFormData({ ...formData, poNumber: e.target.value })}
                    className={errors.poNumber ? "error" : ""}
                    placeholder="Auto-generated"
                    readOnly={!editingPO}
                  />
                  {errors.poNumber && <span className="error-text">{errors.poNumber}</span>}
                </div>

                <div className="form-group">
                  <label>Material *</label>
                  <input
                    type="text"
                    value={formData.material}
                    onChange={(e) => setFormData({ ...formData, material: e.target.value })}
                    className={errors.material ? "error" : ""}
                    placeholder="Enter material name"
                  />
                  {errors.material && <span className="error-text">{errors.material}</span>}
                </div>

                <div className="form-group">
                  <label>Supplier *</label>
                  <input
                    type="text"
                    value={formData.supplier}
                    onChange={(e) => setFormData({ ...formData, supplier: e.target.value })}
                    className={errors.supplier ? "error" : ""}
                    placeholder="Enter supplier name"
                  />
                  {errors.supplier && <span className="error-text">{errors.supplier}</span>}
                </div>

                <div className="form-group">
                  <label>Quantity (tons) *</label>
                  <input
                    type="number"
                    step="0.01"
                    value={formData.quantity}
                    onChange={(e) => setFormData({ ...formData, quantity: e.target.value })}
                    className={errors.quantity ? "error" : ""}
                    placeholder="Enter quantity"
                  />
                  {errors.quantity && <span className="error-text">{errors.quantity}</span>}
                </div>

                <div className="form-group">
                  <label>Rate (₹) *</label>
                  <input
                    type="number"
                    step="0.01"
                    value={formData.rate}
                    onChange={(e) => setFormData({ ...formData, rate: e.target.value })}
                    className={errors.rate ? "error" : ""}
                    placeholder="Enter rate per unit"
                  />
                  {errors.rate && <span className="error-text">{errors.rate}</span>}
                </div>

                <div className="form-group">
                  <label>Total Amount (₹)</label>
                  <input
                    type="text"
                    value={
                      formData.quantity && formData.rate
                        ? `₹${(Number.parseFloat(formData.quantity) * Number.parseFloat(formData.rate)).toFixed(2)}`
                        : "₹0.00"
                    }
                    readOnly
                    className="readonly"
                  />
                </div>

                <div className="form-group">
                  <label>PO Type *</label>
                  <select
                    value={formData.poType}
                    onChange={(e) => setFormData({ ...formData, poType: e.target.value })}
                    className={errors.poType ? "error" : ""}
                  >
                    <option value="">Select PO Type</option>
                    <option value="Standard">Standard</option>
                    <option value="Blanket">Blanket</option>
                    <option value="Contract">Contract</option>
                    <option value="Planned">Planned</option>
                  </select>
                  {errors.poType && <span className="error-text">{errors.poType}</span>}
                </div>

                <div className="form-group">
                  <label>PO Reference</label>
                  <input
                    type="text"
                    value={formData.poReference}
                    onChange={(e) => setFormData({ ...formData, poReference: e.target.value })}
                    placeholder="Enter PO reference"
                  />
                </div>

                <div className="form-group">
                  <label>Delivery Date *</label>
                  <input
                    type="date"
                    value={formData.deliveryDate}
                    onChange={(e) => setFormData({ ...formData, deliveryDate: e.target.value })}
                    className={errors.deliveryDate ? "error" : ""}
                  />
                  {errors.deliveryDate && <span className="error-text">{errors.deliveryDate}</span>}
                </div>

                <div className="form-group full-width">
                  <label>Narration</label>
                  <textarea
                    value={formData.narration}
                    onChange={(e) => setFormData({ ...formData, narration: e.target.value })}
                    placeholder="Enter additional notes"
                    rows="3"
                  />
                </div>

                <div className="form-group">
                  <label>Status</label>
                  <select
                    value={formData.status}
                    onChange={(e) => setFormData({ ...formData, status: e.target.value })}
                  >
                    <option value="Active">Active</option>
                    <option value="Completed">Completed</option>
                    <option value="Cancelled">Cancelled</option>
                    <option value="On Hold">On Hold</option>
                  </select>
                </div>
              </div>

              <div className="form-actions">
                <button type="button" className="btn btn-cancel" onClick={resetForm}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-save">
                  {editingPO ? "Update" : "Save"} PO
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="po-table">
        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>PO Number</th>
              <th>Material</th>
              <th>Supplier</th>
              <th>Quantity</th>
              <th>Rate</th>
              <th>Total Amount</th>
              <th>Delivery Date</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {poDetails.map((po, index) => (
              <tr key={po.id}>
                <td>{index + 1}</td>
                <td>{po.poNumber}</td>
                <td>{po.material}</td>
                <td>{po.supplier}</td>
                <td>{po.quantity} tons</td>
                <td>₹{po.rate}</td>
                <td>₹{po.totalAmount}</td>
                <td>{new Date(po.deliveryDate).toLocaleDateString()}</td>
                <td>
                  <select
                    value={po.status}
                    onChange={(e) => handleStatusChange(po.id, e.target.value)}
                    className={`status-select ${po.status.toLowerCase().replace(" ", "-")}`}
                  >
                    <option value="Active">Active</option>
                    <option value="Completed">Completed</option>
                    <option value="Cancelled">Cancelled</option>
                    <option value="On Hold">On Hold</option>
                  </select>
                </td>
                <td>
                  <div className="action-buttons">
                    <button className="btn btn-edit" onClick={() => handleEdit(po)} title="Edit PO">
                      ✏️
                    </button>
                    <button className="btn btn-delete" onClick={() => handleDelete(po.id)} title="Delete PO">
                      🗑️
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {poDetails.length === 0 && (
          <div className="no-data">
            <p>No purchase orders found</p>
          </div>
        )}
      </div>
    </div>
  )
}

export default PODetails
