"use client"

import { useState, useEffect } from "react"
import * as XLSX from "xlsx"
import axios from "axios"

const VehicleEntry = () => {
  const [vehicleEntries, setVehicleEntries] = useState([])
  const [videoFile, setVideoFile] = useState(null)
  const [videoPreview, setVideoPreview] = useState(null)
  const [loading, setLoading] = useState(false)
  const [suppliers, setSuppliers] = useState([])
  const [materials, setMaterials] = useState([])

  useEffect(() => {
    fetchVehicleEntries()
    fetchSuppliers()
    fetchMaterials()
  }, [])

  const fetchVehicleEntries = async () => {
    try {
      const response = await fetch("http://localhost:5000/vehicles")
      const data = await response.json()
      setVehicleEntries(data)
    } catch (error) {
      console.error("Error fetching vehicle entries:", error)
    }
  }

  const fetchSuppliers = async () => {
    try {
      const response = await fetch("http://localhost:5000/po")
      const data = await response.json()
      const uniqueSuppliers = [...new Set(data.map((po) => po.supplier))]
      setSuppliers(uniqueSuppliers)
    } catch (error) {
      console.error("Error fetching suppliers:", error)
    }
  }

  const fetchMaterials = async () => {
    try {
      const response = await fetch("http://localhost:5000/po")
      const data = await response.json()
      const uniqueMaterials = [...new Set(data.map((po) => po.material))]
      setMaterials(uniqueMaterials)
    } catch (error) {
      console.error("Error fetching materials:", error)
    }
  }

  const exportToExcel = () => {
    const exportData = vehicleEntries.map((entry, index) => ({
      "S.No": index + 1,
      "Inward Number": entry.inward_no,
      "Vehicle Number": entry.vehicle_number,
      "Driver Name": entry.driver_name,
      "Supplier Name": entry.supplier_name,
      "Material Type": entry.material,
      Purpose: entry.purpose,
      "Entry Time": new Date(entry.entry_time).toLocaleString(),
      Status: entry.status,
    }))

    const worksheet = XLSX.utils.json_to_sheet(exportData)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "Vehicle Entries")
    XLSX.writeFile(workbook, `vehicle_entries_${new Date().toISOString().split("T")[0]}.xlsx`)
  }

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this vehicle entry?")) {
      try {
        const response = await fetch(`http://localhost:5000/vehicles/${id}`, {
          method: "DELETE",
        })

        if (response.ok) {
          fetchVehicleEntries()
          alert("Vehicle entry deleted successfully!")
        }
      } catch (error) {
        console.error("Error deleting vehicle entry:", error)
        alert("Error deleting vehicle entry")
      }
    }
  }

  const handleVideoUpload = (e) => {
    const file = e.target.files[0]
    if (!file) return
    setVideoFile(file)
    const videoUrl = URL.createObjectURL(file)
    setVideoPreview(videoUrl)
  }

  const handleVideoProcessing = async () => {
    if (!videoFile) return alert("Please upload a video first.")
    const formData = new FormData()
    formData.append("file", videoFile)
    setLoading(true)

    try {
      const res = await axios.post("http://127.0.0.1:8000/upload/", formData)
      const data = res.data

      // If vehicle not matched — show popup but still display in table
      if (!data.matched) {
        alert("❌ Vehicle not registered! Entry will not be saved.")
      }

      // Generate inward number
      const inwardNumber = `INB${(vehicleEntries.length + 1).toString().padStart(3, "0")}`

      // Create a new vehicle entry
      const newEntry = {
        vehicle_number: data.vehicle_number || "N/A",
        driver_name: "", // Will be filled manually or from other source
        entry_time: new Date().toISOString(),
        supplier_name: "THULASI READYMIX", // Default supplier
        material: "", // Will be selected
        inward_no: inwardNumber,
        purpose: "Material Delivery",
        status: "In",
      }

      // Save to backend
      const response = await fetch("http://localhost:5000/vehicles", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newEntry),
      })

      if (response.ok) {
        fetchVehicleEntries()
        alert("Vehicle entry created successfully!")
      }
    } catch (error) {
      console.error("Error during processing:", error)
      alert("Error during vehicle verification.")
    } finally {
      setLoading(false)
    }
  }

  const handleManualEntry = async (e) => {
    e.preventDefault()
    const formData = new FormData(e.target)

    const inwardNumber = `INB${(vehicleEntries.length + 1).toString().padStart(3, "0")}`

    const newEntry = {
      vehicle_number: formData.get("vehicleNumber"),
      driver_name: formData.get("driverName"),
      entry_time: new Date().toISOString(),
      supplier_name: formData.get("supplierName"),
      material: formData.get("material"),
      inward_no: inwardNumber,
      purpose: formData.get("purpose") || "Material Delivery",
      status: "In",
    }

    try {
      const response = await fetch("http://localhost:5000/vehicles", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newEntry),
      })

      if (response.ok) {
        fetchVehicleEntries()
        e.target.reset()
        alert("Vehicle entry created successfully!")
      }
    } catch (error) {
      console.error("Error creating vehicle entry:", error)
      alert("Error creating vehicle entry")
    }
  }

  return (
    <div className="vehicle-entry">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">🚛</div>
          <div>
            <h2>Vehicle Entry Management</h2>
            <p className="section-subtitle">Manage vehicle entries and automatically populate supplier details</p>
          </div>
        </div>
        <div className="header-actions">
          <button className="btn btn-export" onClick={exportToExcel}>
            📊 Export Excel
          </button>
        </div>
      </div>

      <div className="entry-methods">
        <div className="video-feed-section">
          <h3>🎥 Video Processing Entry</h3>
          <div className="video-feed-container">
            <div className="video-feed-box">
              <video className="live-video-feed" autoPlay loop muted playsInline controls={false}>
                <source src={videoPreview || "/sample2.mp4"} type="video/mp4" />
                Your browser does not support the video tag.
              </video>
              <div className="video-overlay">
                <div className="live-indicator">
                  <span className="live-dot"></span>
                  LIVE FEED
                </div>
              </div>
            </div>

            <div className="video-controls">
              <input
                type="file"
                accept="video/*,image/*"
                onChange={handleVideoUpload}
                className="video-upload-input"
                id="video-upload"
                style={{ display: "none" }}
              />
              <label htmlFor="video-upload" className="btn btn-upload-video">
                📁 Upload Video
              </label>
              {videoFile && (
                <>
                  <span className="uploaded-file-name">📹 {videoFile.name}</span>
                  <button className="btn btn-process" onClick={handleVideoProcessing} style={{ marginLeft: "10px" }}>
                    ▶️ Process Video
                  </button>
                </>
              )}
            </div>
          </div>
          {loading && <p style={{ marginTop: "10px" }}>⏳ Checking number plates...</p>}
        </div>

        <div className="manual-entry-section">
          <h3>✍️ Manual Entry</h3>
          <form onSubmit={handleManualEntry} className="manual-entry-form">
            <div className="form-grid">
              <div className="form-group">
                <label>Vehicle Number *</label>
                <input type="text" name="vehicleNumber" required placeholder="e.g., TN34Y4556" />
              </div>
              <div className="form-group">
                <label>Driver Name</label>
                <input type="text" name="driverName" placeholder="Driver's name" />
              </div>
              <div className="form-group">
                <label>Supplier Name *</label>
                <select name="supplierName" required>
                  <option value="">Select Supplier</option>
                  {suppliers.map((supplier, index) => (
                    <option key={index} value={supplier}>
                      {supplier}
                    </option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Material *</label>
                <select name="material" required>
                  <option value="">Select Material</option>
                  {materials.map((material, index) => (
                    <option key={index} value={material}>
                      {material}
                    </option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Purpose</label>
                <input type="text" name="purpose" placeholder="Purpose of visit" defaultValue="Material Delivery" />
              </div>
            </div>
            <button type="submit" className="btn btn-submit">
              ➕ Add Vehicle Entry
            </button>
          </form>
        </div>
      </div>

      <div className="entries-table">
        <div className="table-header">
          <h3>Vehicle Entries</h3>
          <div className="table-stats">
            <div className="stat-card">
              <span className="stat-number">{vehicleEntries.length}</span>
              <span className="stat-label">Total Entries</span>
            </div>
            <div className="stat-card">
              <span className="stat-number">{vehicleEntries.filter((e) => e.status === "In").length}</span>
              <span className="stat-label">Currently In</span>
            </div>
          </div>
        </div>

        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>S.No</th>
                <th>Inward Number</th>
                <th>Vehicle Number</th>
                <th>Driver Name</th>
                <th>Supplier Name</th>
                <th>Material Type</th>
                <th>Purpose</th>
                <th>Entry Time</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {vehicleEntries.map((entry, index) => (
                <tr key={entry.id}>
                  <td>{index + 1}</td>
                  <td>
                    <span className="inward-number">{entry.inward_no}</span>
                  </td>
                  <td>
                    <span className="vehicle-number">{entry.vehicle_number}</span>
                  </td>
                  <td>{entry.driver_name || "N/A"}</td>
                  <td>
                    <span className="supplier-name">{entry.supplier_name}</span>
                  </td>
                  <td>
                    <span className="material">{entry.material || "N/A"}</span>
                  </td>
                  <td>{entry.purpose}</td>
                  <td>
                    <span className="entry-time">
                      {new Date(entry.entry_time).toLocaleDateString()}
                      <br />
                      <small>{new Date(entry.entry_time).toLocaleTimeString()}</small>
                    </span>
                  </td>
                  <td>
                    <span className={`status-badge ${entry.status.toLowerCase()}`}>{entry.status}</span>
                  </td>
                  <td>
                    <div className="action-buttons">
                      <button className="btn btn-delete" onClick={() => handleDelete(entry.id)} title="Delete Entry">
                        🗑️
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {vehicleEntries.length === 0 && (
            <div className="no-data">
              <div className="no-data-icon">🚛</div>
              <h3>No Vehicle Entries Found</h3>
              <p>Add vehicle entries using video processing or manual entry above</p>
            </div>
          )}
        </div>
      </div>

      <style jsx>{`
        .vehicle-entry {
          padding: 20px;
          max-width: 1600px;
