"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import XLSX from "xlsx"

const PODetails = () => {
  const [poDetails, setPODetails] = useState([])
  const [csvData, setCsvData] = useState([])
  const [csvHeaders, setCsvHeaders] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [editingPO, setEditingPO] = useState(null)
  const [uploadStatus, setUploadStatus] = useState("")
  const [isUploading, setIsUploading] = useState(false)
  const [availableFiles, setAvailableFiles] = useState([])
  const [selectedFile, setSelectedFile] = useState("")
  const [formData, setFormData] = useState({
    poNumber: "",
    supplier: "",
    material: "",
    quantity: "",
    rate: "",
    deliveryDate: "",
    narration: "",
    status: "Active",
    poType: "",
  })
  const [errors, setErrors] = useState({})

  useEffect(() => {
    fetchPODetails()
    loadAvailableCSVFiles()
  }, [])

  const fetchPODetails = async () => {
    try {
      const res = await axios.get("http://localhost:5000/po")
      setPODetails(res.data)
    } catch (error) {
      console.error("Error fetching PO details:", error)
    }
  }

  const loadAvailableCSVFiles = async () => {
    try {
      const response = await fetch("http://localhost:8001/list-csv-files/")
      const result = await response.json()
      if (result.status === "success") {
        setAvailableFiles(result.files)
        // Auto-load the latest file if available
        if (result.files.length > 0) {
          const latestFile = result.files[result.files.length - 1]
          setSelectedFile(latestFile)
          loadCSVData(latestFile)
        }
      }
    } catch (error) {
      console.log("No CSV files available yet")
    }
  }

  const loadCSVData = async (filename) => {
    try {
      const response = await fetch(`http://localhost:8001/get-csv-data/${filename}`)
      const result = await response.json()
      if (result.status === "success") {
        setCsvHeaders(result.headers)
        setCsvData(result.data)
      }
    } catch (error) {
      console.error("Error loading CSV data:", error)
    }
  }

  const handleJsonUpload = async (event) => {
    const file = event.target.files[0]
    if (!file) return

    if (!file.name.endsWith(".json")) {
      alert("Please select a JSON file")
      return
    }

    setIsUploading(true)
    setUploadStatus("Uploading and converting JSON to CSV...")

    const formData = new FormData()
    formData.append("file", file)

    try {
      const response = await fetch("http://localhost:8001/upload-json/", {
        method: "POST",
        body: formData,
      })

      const result = await response.json()

      if (result.status === "success") {
        setUploadStatus("‚úÖ JSON converted to CSV successfully!")

        // Refresh available files and load the new CSV
        await loadAvailableCSVFiles()

        // Load the newly created CSV
        loadCSVData(result.filename)
        setSelectedFile(result.filename)

        setTimeout(() => setUploadStatus(""), 3000)
      } else {
        setUploadStatus(`‚ùå Error: ${result.error}`)
        setTimeout(() => setUploadStatus(""), 5000)
      }
    } catch (error) {
      console.error("Upload error:", error)
      setUploadStatus("‚ùå Upload failed. Please check if the JSON converter server is running on port 8001.")
      setTimeout(() => setUploadStatus(""), 5000)
    } finally {
      setIsUploading(false)
      event.target.value = ""
    }
  }

  const handleFileSelection = (event) => {
    const filename = event.target.value
    setSelectedFile(filename)
    if (filename) {
      loadCSVData(filename)
    } else {
      setCsvData([])
      setCsvHeaders([])
    }
  }

  const clearCsvData = () => {
    setCsvData([])
    setCsvHeaders([])
    setSelectedFile("")
    setUploadStatus("CSV data cleared from view")
    setTimeout(() => setUploadStatus(""), 2000)
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    const formErrors = validateForm()
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors)
      return
    }

    const payload = {
      ...formData,
      quantity: Number.parseFloat(formData.quantity),
      rate: Number.parseFloat(formData.rate),
      totalAmount: Number.parseFloat(formData.quantity) * Number.parseFloat(formData.rate),
    }

    try {
      if (editingPO) {
        await axios.put(`http://localhost:5000/po/${editingPO.id}`, payload)
      } else {
        await axios.post("http://localhost:5000/po", payload)
      }

      fetchPODetails()
      resetForm()
    } catch (error) {
      console.error("Error saving PO:", error)
      alert("Error saving PO. Please try again.")
    }
  }

  const handleDelete = async (id) => {
    if (window.confirm("Delete this PO?")) {
      try {
        await axios.delete(`http://localhost:5000/po/${id}`)
        fetchPODetails()
      } catch (error) {
        console.error("Error deleting PO:", error)
        alert("Error deleting PO. Please try again.")
      }
    }
  }

  const handleEdit = (po) => {
    setFormData(po)
    setEditingPO(po)
    setShowForm(true)
  }

  const resetForm = () => {
    setFormData({
      poNumber: "",
      supplier: "",
      material: "",
      quantity: "",
      rate: "",
      deliveryDate: "",
      narration: "",
      status: "Active",
      poType: "",
    })
    setErrors({})
    setEditingPO(null)
    setShowForm(false)
  }

  const validateForm = () => {
    const newErrors = {}
    if (!formData.poNumber) newErrors.poNumber = "Required"
    if (!formData.supplier) newErrors.supplier = "Required"
    if (!formData.material) newErrors.material = "Required"
    if (!formData.quantity || formData.quantity <= 0) newErrors.quantity = "Invalid"
    if (!formData.rate || formData.rate <= 0) newErrors.rate = "Invalid"
    if (!formData.deliveryDate) newErrors.deliveryDate = "Required"
    if (!formData.poType) newErrors.poType = "Required"
    return newErrors
  }

  const exportToExcel = () => {
    // Export PO details to Excel (existing functionality)
    const ws = XLSX.utils.json_to_sheet(poDetails)
    const wb = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(wb, ws, "PO")
    XLSX.writeFile(wb, "po_details.xlsx")
  }

  return (
    <div className="po-wrapper">
      <div className="po-header">
        <h2>Purchase Order Details</h2>
        <div>
          <button className="btn export" onClick={exportToExcel}>
            Export Excel
          </button>
          <button className="btn add" onClick={() => setShowForm(true)}>
            Add PO
          </button>
        </div>
      </div>

      <table className="po-table">
        <thead>
          <tr>
            <th>S.No</th>
            <th>PO Number</th>
            <th>Supplier</th>
            <th>Material</th>
            <th>Qty</th>
            <th>Rate</th>
            <th>Amount</th>
            <th>Delivery</th>
            <th>Type</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {poDetails.map((po, i) => (
            <tr key={po.id}>
              <td>{i + 1}</td>
              <td>{po.poNumber}</td>
              <td>{po.supplier}</td>
              <td>{po.material}</td>
              <td>{po.quantity}</td>
              <td>‚Çπ{po.rate}</td>
              <td>‚Çπ{po.totalAmount}</td>
              <td>{new Date(po.deliveryDate).toLocaleDateString()}</td>
              <td>{po.poType}</td>
              <td>{po.status}</td>
              <td>
                <button className="btn edit" onClick={() => handleEdit(po)}>
                  ‚úè
                </button>
                <button className="btn delete" onClick={() => handleDelete(po.id)}>
                  üóë
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* JSON to CSV Conversion Section */}
      <div className="csv-section" style={{ marginTop: "40px" }}>
        <div className="po-header">
          <h3>JSON to CSV Converter</h3>
          <div>
            <input
              type="file"
              accept=".json"
              onChange={handleJsonUpload}
              style={{ display: "none" }}
              id="json-upload"
              disabled={isUploading}
            />
            <label htmlFor="json-upload" className={`btn add ${isUploading ? "disabled" : ""}`}>
              {isUploading ? "Converting..." : "üìÅ Upload JSON"}
            </label>

            {availableFiles.length > 0 && (
              <select
                value={selectedFile}
                onChange={handleFileSelection}
                className="file-select"
                style={{ marginLeft: "10px", padding: "8px" }}
              >
                <option value="">Select CSV File</option>
                {availableFiles.map((file, index) => (
                  <option key={index} value={file}>
                    {file}
                  </option>
                ))}
              </select>
            )}

            {csvData.length > 0 && (
              <button className="btn delete" onClick={clearCsvData} style={{ marginLeft: "10px" }}>
                Clear View
              </button>
            )}
          </div>
        </div>

        {uploadStatus && (
          <div className={`upload-status ${uploadStatus.includes("‚ùå") ? "error" : "success"}`}>{uploadStatus}</div>
        )}

        {csvData.length > 0 && (
          <div style={{ marginTop: "20px" }}>
            <h4>CSV Data: {selectedFile}</h4>
            <div
              style={{
                maxHeight: "400px",
                overflowY: "auto",
                overflowX: "auto",
                border: "1px solid #ddd",
                borderRadius: "4px",
              }}
            >
              <table className="po-table">
                <thead>
                  <tr>
                    <th>S.No</th>
                    {csvHeaders.map((header, index) => (
                      <th key={index}>{header}</th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {csvData.map((row, rowIndex) => (
                    <tr key={rowIndex}>
                      <td>{rowIndex + 1}</td>
                      {row.map((cell, cellIndex) => (
                        <td key={cellIndex}>{cell || ""}</td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {csvData.length === 0 && !isUploading && (
          <div
            className="no-data"
            style={{
              marginTop: "20px",
              textAlign: "center",
              color: "#666",
              padding: "40px",
              background: "#f8f9fa",
              borderRadius: "8px",
              border: "2px dashed #dee2e6",
            }}
          >
            <p>üìä No CSV data available.</p>
            <p>Upload a JSON file to convert and view data in table format.</p>
            {availableFiles.length > 0 && <p>Or select an existing CSV file from the dropdown above.</p>}
          </div>
        )}
      </div>

      {showForm && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>{editingPO ? "Edit PO" : "Add PO"}</h3>
            <form onSubmit={handleSubmit} className="po-form">
              <input
                placeholder="PO Number"
                value={formData.poNumber}
                onChange={(e) => setFormData({ ...formData, poNumber: e.target.value })}
              />
              <input
                placeholder="Supplier"
                value={formData.supplier}
                onChange={(e) => setFormData({ ...formData, supplier: e.target.value })}
              />
              <input
                placeholder="Material"
                value={formData.material}
                onChange={(e) => setFormData({ ...formData, material: e.target.value })}
              />
              <input
                type="number"
                placeholder="Quantity"
                value={formData.quantity}
                onChange={(e) => setFormData({ ...formData, quantity: e.target.value })}
              />
              <input
                type="number"
                placeholder="Rate"
                value={formData.rate}
                onChange={(e) => setFormData({ ...formData, rate: e.target.value })}
              />
              <input
                type="date"
                value={formData.deliveryDate}
                onChange={(e) => setFormData({ ...formData, deliveryDate: e.target.value })}
              />
              <textarea
                placeholder="Narration"
                value={formData.narration}
                onChange={(e) => setFormData({ ...formData, narration: e.target.value })}
              />

              <select value={formData.poType} onChange={(e) => setFormData({ ...formData, poType: e.target.value })}>
                <option value="">Select PO Type</option>
                <option value="Local">Local</option>
                <option value="Import">Import</option>
              </select>

              <select value={formData.status} onChange={(e) => setFormData({ ...formData, status: e.target.value })}>
                <option value="Active">Active</option>
                <option value="Completed">Completed</option>
                <option value="Cancelled">Cancelled</option>
                <option value="Pending">Pending</option>
              </select>

              <div className="form-buttons">
                <button type="button" className="btn cancel" onClick={resetForm}>
                  Cancel
                </button>
                <button type="submit" className="btn save">
                  Save PO
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <style jsx>{`
        .csv-section {
          border-top: 2px solid #eee;
          padding-top: 20px;
        }

        .upload-status {
          padding: 10px;
          border-radius: 4px;
          margin: 10px 0;
          font-weight: 500;
        }

        .upload-status.success {
          background-color: #d4edda;
          color: #155724;
          border: 1px solid #c3e6cb;
        }

        .upload-status.error {
          background-color: #f8d7da;
          color: #721c24;
          border: 1px solid #f5c6cb;
        }

        .btn.disabled {
          opacity: 0.6;
          cursor: not-allowed;
        }

        .file-select {
          border: 1px solid #ddd;
          border-radius: 4px;
          background: white;
        }
      `}</style>
    </div>
  )
}

export default PODetails
