sourcesContent:
"use client";

import { useState, useEffect } from "react";

const CreateBatchSlip = ({ onNavigate }) => {
  const [batchSlips, setBatchSlips] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const [formData, setFormData] = useState({
    // Header Information
    plantSerialNumber: "3494",
    batchDate: new Date().toISOString().split("T")[0],
    batchStartTime: "",
    batchEndTime: "",
    batchNumber: "",
    customer: "",
    site: "",
    recipeCode: "",
    recipeName: "",
    truckNumber: "",
    truckDriver: "",
    orderNumber: "",
    batcherName: "",

    // Quantities
    orderedQuantity: "",
    productionQuantity: "",
    adjManualQuantity: "",
    withThisLoad: 0,
    mixerCapacity: "",
    batchSize: "",

    // Material Data - 20 rows as shown in image
    materialData: Array(20)
      .fill()
      .map(() => ({
        sand: "145.00",
        mm40: "75.00",
        mm20: "150.00",
        mm0: "0.00",
        cem1: "25.00",
        cem2: "25.00",
        cem3: "25.00",
        water: "45.00",
        admix1: "0.38",
      })),

    // Totals
    totalSand: "1500.00",
    totalMm40: "3000.00",
    totalMm20: "0.00",
    totalCem1: "500.00",
    totalCem2: "500.00",
    totalCem3: "500.00",
    totalWater: "900.00",
    totalAdmix1: "7.50",

    // Client Information for Invoice
    clientName: "",
    clientAddress: "",
    clientEmail: "",
  clientPhone: "+91",
    clientGSTIN: "",

    // Invoice Details
    description: "Concrete M30",
    hsn: "6810",
    rate: "4000.00",
    quantity: "15.00",
    unit: "MÂ³",
  });

  const [errors, setErrors] = useState({});

  // Generate batch number automatically
  useEffect(() => {
    if (!formData.batchNumber) {
      setFormData((prev) => ({
        ...prev,
        batchNumber: generateBatchNumber(),
      }));
    }
  }, []);

  useEffect(() => {
    fetchInvoices();
  }, []);

  const generateBatchNumber = () => {
    const date = new Date();
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const day = String(date.getDate()).padStart(2, "0");
    const sequence = String(Math.floor(Math.random() * 1000) + 1).padStart(
      3,
      "0"
    );
    return `${year}${month}${day}${sequence}`;

  };

  const fetchInvoices = async () => {
    try {
      const response = await fetch("http://localhost:5000/invoices");
      const data = await response.json();
      setBatchSlips(data); // Using same state for invoices
    } catch (error) {
      console.error("Error fetching invoices:", error);
    }
  };

  const sendBatchSlip = (batchSlip) => {
    if (isLoading) return;
    sendToWhatsApp("batchslip", batchSlip);
  };

  // Dynamic import for PDF libraries to avoid SSR issues
  const loadPDFLibraries = async () => {
    try {
      const jsPDF = (await import("jspdf")).default;
      const autoTable = (await import("jspdf-autotable")).default;
      return { jsPDF, autoTable };
    } catch (error) {
      console.error("Failed to load PDF libraries:", error);
      throw new Error("PDF libraries not available");
    }
  };

  const generateInvoicePDF = async (invoice) => {
    try {
      const { jsPDF, autoTable } = await loadPDFLibraries();
      const doc = new jsPDF();

      // Header
      doc.setFontSize(16);
      doc.setFont(undefined, "bold");
      doc.text("RR CONSTRUCTIONS", 20, 20);
      doc.text("Tax Invoice", 150, 20);

      doc.setFontSize(10);
      doc.setFont(undefined, "normal");
      doc.text("GSTIN: 33AAGFT4474P1Z1", 20, 30);
      doc.text(`Invoice No: ${invoice.invoiceNumber}`, 150, 30);
      doc.text("Excel College Campus, NH 91, Mathura", 20, 35);
      doc.text(
        `Date: ${new Date(invoice.createdAt).toLocaleDateString()}`,
        150,
        35
      );
      doc.text("Road, Haryana - 121102", 20, 40);
      doc.text(`Batch Number: ${invoice.batchNumber}`, 150, 40);
      doc.text("GST/NSN: 33AAGFT4474P1Z1", 20, 45);
      doc.text("Terms of Delivery: As per Order &", 150, 45);
      doc.text("Biller: INFRA LP", 20, 50);
      doc.text("Cheque", 150, 50);
      doc.text("State Name: Tamil Nadu, India", 20, 55);

      // Bill To section
      doc.setFont(undefined, "bold");
      doc.text("Bill To:", 20, 70);
      doc.text("Dispatch Site:", 150, 70);
      doc.setFont(undefined, "normal");
      doc.text(invoice.clientName, 20, 80);
      doc.text("N/A", 150, 80);
      doc.text(invoice.clientAddress, 20, 85);
      doc.text("Bill of Lading/Ref No: N/A", 150, 85);
      doc.text(`GSTIN: ${invoice.clientGSTIN || "N/A"}`, 20, 90);

      // Table using autoTable
      const tableData = [
  [
    invoice.description,
    invoice.hsn,
    invoice.quantity,
    `â‚¹${invoice.rate}`,    // âœ… Fixed
    invoice.unit,
    `â‚¹${invoice.total}`,   // âœ… Fixed
  ],
];


      autoTable(doc, {
        startY: 100,
        head: [["Description", "HSN", "Quantity", "Rate", "Per", "Amount"]],
        body: tableData,
        theme: "grid",
      });

      // Totals
      const finalY = doc.lastAutoTable.finalY + 10;
      doc.text(`Total: â‚¹${invoice.total}`, 150, finalY);
      doc.text(`Output-CGST @ 9%: â‚¹${invoice.cgst}`, 150, finalY + 10);
      doc.text(`Output-SGST @ 9%: â‚¹${invoice.sgst}`, 150, finalY + 20);
      doc.setFont(undefined, "bold");
      doc.text(`Grand Total: â‚¹${invoice.grandTotal}`, 150, finalY + 30);

      // Amount in words
      doc.setFont(undefined, "normal");
      doc.text(
        `Amount Chargeable (in words): ${invoice.amountInWords}`,
        20,
        finalY + 50
      );

      // Footer
      doc.text("Subject to the Tirupati Jurisdiction", 20, finalY + 70);
      doc.text("This is a Computer Generated Invoice", 150, finalY + 70);
      doc.text("Authorised Signatory", 20, finalY + 80);

      return doc;
    } catch (error) {
      console.error("Error generating invoice PDF:", error);
      throw error;
    }
  };

  const generateBatchSlipPDF = async (batchSlip) => {
    try {
      const { jsPDF } = await loadPDFLibraries();
      const doc = new jsPDF();

      // Header - Company Name (centered)
      doc.setFontSize(14);
      doc.setFont(undefined, "bold");
      doc.text("RR CONSTRUCTIONS", 105, 20, { align: "center" });

      doc.setFontSize(9);
      doc.setFont(undefined, "normal");
      doc.text("MCI 70 Control System Ver 3.1", 105, 27, { align: "center" });
      doc.text("SCHWING Stetter", 105, 32, { align: "center" });

      // Title Section with border
      doc.setFontSize(10);
      doc.setFont(undefined, "bold");

      // Draw title box
      doc.rect(20, 40, 120, 8);
      doc.rect(140, 40, 50, 8);
      doc.text("Docket / Batch Report / Autographic Record", 25, 46);
      doc.text("Plant Serial", 145, 44);
      doc.text(`Number: ${batchSlip.plantSerialNumber || "3494"}`, 145, 47);

      // Details Section - Two columns with borders
      let yPos = 55;

      // Left column details
      const leftDetails = [
        ["Batch Date", batchSlip.batchDate || "2025-05-06"],
        ["Batch Start Time", batchSlip.batchStartTime || "08:00"],
        ["Batch End Time", batchSlip.batchEndTime || "08:30"],
        ["Batch Number / Docket Number", batchSlip.batchNumber || "1234"],
        ["Customer", batchSlip.customer || "Client A"],
        ["Site", batchSlip.site || "Client A"],
        ["Recipe Code", batchSlip.recipeCode || "M10"],
        ["Recipe Name", batchSlip.recipeName || "Default Recipe"],
        ["Truck Number", batchSlip.truckNumber || "tm123564"],
        ["Truck Driver", batchSlip.truckDriver || "murugesan"],
        ["Order Number", batchSlip.orderNumber || "1"],
        ["Batcher Name", batchSlip.batcherName || "ss"],
      ];

      // Right column details
      const rightDetails = [
  ["Ordered Quantity", `${batchSlip.orderedQuantity || "10.00"} MÂ³`],
  ["Production Quantity", `${batchSlip.productionQuantity || "10.00"} MÂ³`],
  ["Adj/Manual Quantity", `${batchSlip.adjManualQuantity || "0.00"} MÂ³`],
  ["With This Load", `${batchSlip.withThisLoad || "0.00"} MÂ³`],
  ["Mixer Capacity", `${batchSlip.mixerCapacity || "0.50"} MÂ³`],
  ["Batch Size", `${batchSlip.batchSize || "0.50"} MÂ³`],
];


      doc.setFontSize(8);
      doc.setFont(undefined, "normal");

      // Draw left column
      for (let i = 0; i < leftDetails.length; i++) {
        const detail = leftDetails[i];
        const y = yPos + i * 6;
        doc.rect(20, y, 60, 6);
        doc.rect(80, y, 60, 6);
        doc.text(detail[0] + " :", 22, y + 4);
        doc.text(detail[1], 82, y + 4);
      }

      // Draw right column
      for (let i = 0; i < rightDetails.length; i++) {
        const detail = rightDetails[i];
        const y = yPos + i * 6;
        doc.rect(140, y, 35, 6);
        doc.rect(175, y, 15, 6);
        doc.text(detail[0] + " :", 142, y + 4);
        doc.text(detail[1], 177, y + 4);
      }

      // Material Table
      yPos = 130;

      // Table headers
      const headers = [
        "Batch Size in MÂ³",
        "SAND",
        "40 MM",
        "0",
        "CEM-1",
        "CEM-2",
        "CEM-3",
        "WATER",
        "0 ADMIX1",
      ];
      const colWidths = [25, 18, 18, 12, 18, 18, 18, 18, 23];

      doc.setFontSize(7);
      doc.setFont(undefined, "bold");

      // Draw header row
      let currentX = 20;
      for (let i = 0; i < headers.length; i++) {
        doc.rect(currentX, yPos, colWidths[i], 8);
        doc.text(headers[i], currentX + 1, yPos + 5);
        currentX += colWidths[i];
      }

      // Data rows (20 rows as shown in image)
      doc.setFont(undefined, "normal");
      const rowData = [
        "145.00",
        "75.00",
        "150.00",
        "0.00",
        "25.00",
        "25.00",
        "25.00",
        "45.00",
        "0.38",
      ];

      for (let row = 0; row < 20; row++) {
        yPos += 8;
        currentX = 20;

        for (let col = 0; col < rowData.length; col++) {
          doc.rect(currentX, yPos, colWidths[col], 8);
          doc.text(rowData[col], currentX + 2, yPos + 5);
          currentX += colWidths[col];
        }
      }

      // Total Set Weight row
      yPos += 8;
      currentX = 20;
      doc.setFont(undefined, "bold");

      const totalSetData = [
        "Total Set Weight",
        "1500.00",
        "3000.00",
        "0.00",
        "500.00",
        "500.00",
        "500.00",
        "900.00",
        "7.50",
      ];

      for (let i = 0; i < totalSetData.length; i++) {
        doc.rect(currentX, yPos, colWidths[i], 8);
        doc.text(totalSetData[i], currentX + 1, yPos + 5);
        currentX += colWidths[i];
      }

      // Add some spacing
      yPos += 15;

      // Total Actual row
      currentX = 20;
      const totalActualData = [
        "Total Actual",
        "1500.00",
        "3000.00",
        "0.00",
        "500.00",
        "500.00",
        "500.00",
        "900.00",
        "7.50",
      ];

      for (let i = 0; i < totalActualData.length; i++) {
        doc.rect(currentX, yPos, colWidths[i], 8);
        doc.text(totalActualData[i], currentX + 1, yPos + 5);
        currentX += colWidths[i];
      }

      // Footer - Batcher Name
      doc.setFont(undefined, "normal");
      doc.setFontSize(9);
    doc.text("Batcher Name :", 20, yPos + 20);

      doc.text(batchSlip.batcherName || "ss", 20, yPos + 25);

      return doc;
    } catch (error) {
      console.error("Error generating batch slip PDF:", error);
      throw error;
    }
  };

  // Generate text fallback for when PDF fails
  const generateInvoiceText = (invoice) => {
    return `ðŸ§¾ INVOICE DETAILS

Company: RR CONSTRUCTIONS
GSTIN: 33AAGFT4474P1Z1
Address: Excel College Campus, NH 91, Mathura Road, Haryana - 121102

Invoice No: ${invoice.invoiceNumber}
Date: ${new Date(invoice.createdAt).toLocaleDateString()}
Batch Number: ${invoice.batchNumber}

Bill To:
${invoice.clientName}
${invoice.clientAddress}
GSTIN: ${invoice.clientGSTIN || "N/A"}

Item Details:
Description: ${invoice.description}
HSN: ${invoice.hsn}
Quantity: ${invoice.quantity}
Rate: â‚¹${invoice.rate}
Unit: ${invoice.unit}
Amount: â‚¹${invoice.total}

Tax Calculation:
Subtotal: â‚¹${invoice.total}
CGST @ 9%: â‚¹${invoice.cgst}
SGST @ 9%: â‚¹${invoice.sgst}
Grand Total: â‚¹${invoice.grandTotal}

Amount in Words: ${invoice.amountInWords}

Subject to Tirupati Jurisdiction
This is a Computer Generated Invoice`;
  };

  const generateBatchSlipText = (batchSlip) => {
    return `ðŸ“‹ BATCH SLIP DETAILS

Company: RR CONSTRUCTIONS
MCI 70 Control System Ver 3.1
SCHWING Stetter

Batch Information:
Batch Number: ${batchSlip.batchNumber}
Batch Date: ${batchSlip.batchDate || "2025-05-06"}
Customer: ${batchSlip.customer}
Recipe Code: ${batchSlip.recipeCode}

Production Details:
Ordered Quantity: ${batchSlip.orderedQuantity || "10.00"} MÂ³
Production Quantity: ${batchSlip.productionQuantity || "10.00"} MÂ³
Batch Size: ${batchSlip.batchSize || "0.50"} MÂ³

Truck Information:
Truck Number: ${batchSlip.truckNumber}
Truck Driver: ${batchSlip.truckDriver}
Batcher Name: ${batchSlip.batcherName}

Plant Serial Number: ${batchSlip.plantSerialNumber || "3494"}
Order Number: ${batchSlip.orderNumber || "1"}`;
  };

  // WhatsApp sending function using Twilio
  const sendToWhatsApp = async (type, data) => {
    setIsLoading(true);
    try {
      console.log("Starting WhatsApp send process...");

      if (!formData.clientPhone || formData.clientPhone.length < 10) {
        throw new Error(
          "Please enter a valid phone number with country code (e.g., +919876543210)"
        );
      }

      let pdfBase64 = null;
      let messageContent = null;

      // Try to generate PDF first
      try {
        console.log("Attempting to generate PDF...");
        const doc =
          type === "invoice"
            ? await generateInvoicePDF(data)
            : await generateBatchSlipPDF(data);
        const pdfBlob = doc.output("blob");
        console.log("PDF generated successfully, size:", pdfBlob.size);

        // Convert to base64
        const reader = new FileReader();
        const base64Promise = new Promise((resolve, reject) => {
          reader.onloadend = () => resolve(reader.result.split(",")[1]);
          reader.onerror = reject;
        });
        reader.readAsDataURL(pdfBlob);
        pdfBase64 = await base64Promise;
        console.log("PDF converted to base64, length:", pdfBase64.length);
      } catch (pdfError) {
        console.warn(
          "PDF generation failed, falling back to text:",
          pdfError.message
        );
        // Generate text fallback
        messageContent =
          type === "invoice"
            ? generateInvoiceText(data)
            : generateBatchSlipText(data);
      }

      // Send to backend API
      console.log("Sending request to API...");
      const response = await fetch("http://localhost:8000/api/send-whatsapp", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          type: type,
          data: data,
          pdfBase64: pdfBase64,
          messageContent: messageContent,
          phoneNumber: formData.clientPhone,
        }),
      });

      console.log("API response status:", response.status);

      // Check if response is JSON
      const contentType = response.headers.get("content-type");
      if (!contentType || !contentType.includes("application/json")) {
        const textResponse = await response.text();
        console.error("Non-JSON response:", textResponse.substring(0, 500));
        throw new Error(
          "Server returned HTML instead of JSON. Check your API route."
        );
      }

      const result = await response.json();
      console.log("API response:", result);

      if (response.ok && result.success) {
        alert(
          `${
            type === "invoice" ? "Invoice" : "Batch slip"
          } sent to WhatsApp successfully!\nMessage ID: ${result.messageSid}`
        );
      } else {
        throw new Error(result.error || "Failed to send message");
      }
    } catch (error) {
      console.error("Error sending to WhatsApp:", error);
      alert(`Failed to send to WhatsApp: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors((prev) => ({
        ...prev,
        [name]: "",
      }));
    }
  };

  const handleMaterialChange = (index, field, value) => {
    const updatedMaterialData = [...formData.materialData];
    updatedMaterialData[index] = {
      ...updatedMaterialData[index],
      [field]: value,
    };

    setFormData((prev) => ({
      ...prev,
      materialData: updatedMaterialData,
    }));

    // Recalculate totals
    calculateTotals(updatedMaterialData);
  };

  const calculateTotals = (materialData) => {
    const totals = {
      totalSand: 0,
      totalMm40: 0,
      totalMm20: 0,
      totalCem1: 0,
      totalCem2: 0,
      totalCem3: 0,
      totalWater: 0,
      totalAdmix1: 0,
    };

    materialData.forEach((row) => {
      totals.totalSand += Number.parseFloat(row.sand) || 0;
      totals.totalMm40 += Number.parseFloat(row.mm40) || 0;
      totals.totalMm20 += Number.parseFloat(row.mm20) || 0;
      totals.totalCem1 += Number.parseFloat(row.cem1) || 0;
      totals.totalCem2 += Number.parseFloat(row.cem2) || 0;
      totals.totalCem3 += Number.parseFloat(row.cem3) || 0;
      totals.totalWater += Number.parseFloat(row.water) || 0;
      totals.totalAdmix1 += Number.parseFloat(row.admix1) || 0;
    });

    setFormData((prev) => ({
      ...prev,
      ...Object.fromEntries(
        Object.entries(totals).map(([key, value]) => [key, value.toFixed(2)])
      ),
    }));
  };

  const validateForm = () => {
    const newErrors = {};
    const requiredFields = [
      "batchDate",
      "customer",
      "recipeCode",
      "recipeName",
      "truckNumber",
      "truckDriver",
      "batcherName",
      "clientName",
      "clientAddress",
      "clientEmail",
    ];

    requiredFields.forEach((field) => {
      if (!formData[field] || formData[field].trim() === "") {
        newErrors[field] = "This field is required";
      }
    });

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      const batchSlipData = {
        plantSerialNumber: formData.plantSerialNumber,
        batchDate: formData.batchDate,
        batchStartTime: formData.batchStartTime,
        batchEndTime: formData.batchEndTime,
        batchNumber: formData.batchNumber,
        customer: formData.customer,
        site: formData.site,
        recipeCode: formData.recipeCode,
        recipeName: formData.recipeName,
        truckNumber: formData.truckNumber,
        truckDriver: formData.truckDriver,
        orderNumber: formData.orderNumber,
        batcherName: formData.batcherName,
        orderedQuantity: formData.orderedQuantity,
        productionQuantity: formData.productionQuantity,
        adjManualQuantity: formData.adjManualQuantity,
        withThisLoad: formData.withThisLoad,
