"use client"

import { useState, useEffect, useCallback } from "react"
import * as XLSX from "xlsx"

const SupplierDetail = () => {
  const [supplierDetails, setSupplierDetails] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [pdfFile, setPdfFile] = useState(null)
  const [validationLoading, setValidationLoading] = useState({})
  const [formData, setFormData] = useState({
    poNumber: "",
    poBalanceQty: "",
    poRate: "",
    inwardNo: "",
    vehicleNo: "",
    dateTime: "",
    supplierName: "",
    material: "",
    uom: "",
    supplierBillQty: "",
    supplierBillRate: "",
    receivedAmount: "",
    amount: "",
    difference: "",
    status: "Pending",
  })
  const [errors, setErrors] = useState({})

  useEffect(() => {
    fetchSupplierDetails()

    // Set current date and time
    const now = new Date()
    setFormData((prev) => ({
      ...prev,
      dateTime: now.toISOString().slice(0, 16),
    }))
  }, [])

  const fetchSupplierDetails = async () => {
    try {
      const response = await fetch("http://localhost:5000/supplier")
      const data = await response.json()
      setSupplierDetails(data)
    } catch (error) {
      console.error("Error fetching supplier details:", error)
    }
  }

  const handlePdfUpload = (e) => {
    const file = e.target.files[0]
    if (file && file.type === "application/pdf") {
      setPdfFile(file)
    } else {
      alert("Please upload a PDF file only")
    }
  }

  const calculateAmount = useCallback(() => {
    const qty = Number.parseFloat(formData.supplierBillQty) || 0
    const rate = Number.parseFloat(formData.supplierBillRate) || 0
    return (qty * rate).toFixed(2)
  }, [formData.supplierBillQty, formData.supplierBillRate])

  const calculateDifference = useCallback(() => {
    const receivedAmount = Number.parseFloat(formData.receivedAmount) || 0
    const amount = Number.parseFloat(formData.amount) || 0
    return (amount - receivedAmount).toFixed(2)
  }, [formData.receivedAmount, formData.amount])

  useEffect(() => {
    const amount = calculateAmount()
    const difference = calculateDifference()

    setFormData((prev) => ({
      ...prev,
      amount: amount,
      difference: difference,
    }))
  }, [calculateAmount, calculateDifference])

  const validateForm = () => {
    const newErrors = {}

    if (!formData.poNumber.trim()) newErrors.poNumber = "PO Number is required"
    if (!formData.poBalanceQty.trim()) newErrors.poBalanceQty = "PO Balance Qty is required"
    if (!formData.poRate.trim()) newErrors.poRate = "PO Rate is required"
    if (!formData.inwardNo.trim()) newErrors.inwardNo = "Inward No is required"
    if (!formData.vehicleNo.trim()) newErrors.vehicleNo = "Vehicle No is required"
    if (!formData.dateTime) newErrors.dateTime = "Date & Time is required"
    if (!formData.supplierName.trim()) newErrors.supplierName = "Supplier Name is required"
    if (!formData.material.trim()) newErrors.material = "Material is required"
    if (!formData.uom.trim()) newErrors.uom = "UOM is required"
    if (!formData.supplierBillQty.trim()) newErrors.supplierBillQty = "Supplier Bill Qty is required"
    if (!formData.supplierBillRate.trim()) newErrors.supplierBillRate = "Supplier Bill Rate is required"
    if (!formData.receivedAmount.trim()) newErrors.receivedAmount = "Received Amount is required"

    return newErrors
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    const formErrors = validateForm()

    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors)
      return
    }

    try {
      const response = await fetch("http://localhost:5000/supplier", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      })

      const result = await response.json()

      if (response.ok) {
        fetchSupplierDetails()
        resetForm()

        // Show validation result
        if (result.validation_result) {
          const validation = result.validation_result
          let message = `Supplier detail added successfully!\n\nWeighbridge Validation:\n`
          message += `Status: ${validation.validation_status}\n`
          message += `Message: ${validation.validation_message}\n`

          if (validation.weighbridge_qty !== null) {
            message += `Supplier Bill Qty: ${validation.supplier_bill_qty}\n`
            message += `Weighbridge Qty: ${validation.weighbridge_qty}\n`
            message += `Difference: ${validation.difference}\n`
            message += `Percentage Difference: ${validation.percentage_difference}%`
          }

          alert(message)
        } else {
          alert("Supplier detail added successfully!")
        }
      } else {
        alert(`Error: ${result.error || "Failed to add supplier detail"}`)
      }
    } catch (error) {
      console.error("Error:", error)
      alert("Error adding supplier detail")
    }
  }

  const resetForm = () => {
    const now = new Date()
    setFormData({
      poNumber: "",
      poBalanceQty: "",
      poRate: "",
      inwardNo: "",
      vehicleNo: "",
      dateTime: now.toISOString().slice(0, 16),
      supplierName: "",
      material: "",
      uom: "",
      supplierBillQty: "",
      supplierBillRate: "",
      receivedAmount: "",
      amount: "",
      difference: "",
      status: "Pending",
    })
    setErrors({})
    setPdfFile(null)
    setShowForm(false)
  }

  const handleApproval = async (id, action) => {
    try {
      const response = await fetch(`http://localhost:5000/supplier/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ status: action }),
      })

      if (response.ok) {
        fetchSupplierDetails()
      }
    } catch (error) {
      console.error("Error updating status:", error)
    }
  }

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this supplier detail?")) {
      try {
        const response = await fetch(`http://localhost:5000/supplier/${id}`, {
          method: "DELETE",
        })

        if (response.ok) {
          fetchSupplierDetails()
        }
      } catch (error) {
        console.error("Error deleting supplier detail:", error)
      }
    }
  }

  const handleWeighbridgeValidation = async (id) => {
    setValidationLoading((prev) => ({ ...prev, [id]: true }))

    try {
      const response = await fetch(`http://localhost:5000/supplier/${id}/validate-weighbridge`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
      })

      const result = await response.json()

      if (response.ok) {
        fetchSupplierDetails()

        // Show validation result
        const validation = result.validation_result
        let message = `Weighbridge Validation Complete!\n\n`
        message += `Status: ${validation.validation_status}\n`
        message += `Message: ${validation.validation_message}\n`

        if (validation.weighbridge_qty !== null) {
          message += `Supplier Bill Qty: ${validation.supplier_bill_qty}\n`
          message += `Weighbridge Qty: ${validation.weighbridge_qty}\n`
          message += `Difference: ${validation.difference}\n`
          message += `Percentage Difference: ${validation.percentage_difference}%`
        }

        alert(message)
      } else {
        alert(`Validation Error: ${result.error}`)
      }
    } catch (error) {
      console.error("Error validating weighbridge:", error)
      alert("Error performing weighbridge validation")
    } finally {
      setValidationLoading((prev) => ({ ...prev, [id]: false }))
    }
  }

  const exportToExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(supplierDetails)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "Supplier Details")
    XLSX.writeFile(workbook, "supplier_details.xlsx")
  }

  const getValidationStatusColor = (status) => {
    switch (status) {
      case "validated":
        return "#28a745"
      case "minor_discrepancy":
        return "#ffc107"
      case "major_discrepancy":
        return "#dc3545"
      case "no_weighbridge_data":
        return "#6c757d"
      case "no_quantity_data":
        return "#6c757d"
      default:
        return "#6c757d"
    }
  }

  const getValidationStatusIcon = (status) => {
    switch (status) {
      case "validated":
        return "✅"
      case "minor_discrepancy":
        return "⚠️"
      case "major_discrepancy":
        return "❌"
      case "no_weighbridge_data":
        return "❓"
      case "no_quantity_data":
        return "❓"
      default:
        return "⏳"
    }
  }

  return (
    <div className="supplier-detail">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">📄</div>
          <h2>Upload Supplier Detail</h2>
        </div>
        <div className="header-actions">
          <button className="btn btn-export" onClick={exportToExcel}>
            📊 Export Excel
          </button>
          <button className="btn btn-add" onClick={() => setShowForm(true)}>
            ➕ Add Supplier Detail
          </button>
        </div>
      </div>

      {showForm && (
        <div className="modal-overlay">
          <div className="modal large-modal">
            <div className="modal-header">
              <h3>Add Supplier Detail</h3>
              <button className="close-btn" onClick={resetForm}>
                ✕
              </button>
            </div>

            <form onSubmit={handleSubmit} className="supplier-form">
              <div className="form-grid">
                <div className="form-group">
                  <label>PO Number *</label>
                  <input
                    type="text"
                    value={formData.poNumber}
                    onChange={(e) => setFormData({ ...formData, poNumber: e.target.value })}
                    className={errors.poNumber ? "error" : ""}
                    placeholder="Enter PO Number"
                  />
                  {errors.poNumber && <span className="error-text">{errors.poNumber}</span>}
                </div>

                <div className="form-group">
                  <label>PO Balance Qty *</label>
                  <input
                    type="number"
                    step="0.01"
                    value={formData.poBalanceQty}
                    onChange={(e) => setFormData({ ...formData, poBalanceQty: e.target.value })}
                    className={errors.poBalanceQty ? "error" : ""}
                    placeholder="Enter balance quantity"
                  />
                  {errors.poBalanceQty && <span className="error-text">{errors.poBalanceQty}</span>}
                </div>

                <div className="form-group">
                  <label>PO Rate (₹) *</label>
                  <input
                    type="number"
                    step="0.01"
                    value={formData.poRate}
                    onChange={(e) => setFormData({ ...formData, poRate: e.target.value })}
                    className={errors.poRate ? "error" : ""}
                    placeholder="Enter PO rate"
                  />
                  {errors.poRate && <span className="error-text">{errors.poRate}</span>}
                </div>

                <div className="form-group">
                  <label>Inward No *</label>
                  <input
                    type="text"
                    value={formData.inwardNo}
                    onChange={(e) => setFormData({ ...formData, inwardNo: e.target.value })}
                    className={errors.inwardNo ? "error" : ""}
                    placeholder="Enter inward number"
                  />
                  {errors.inwardNo && <span className="error-text">{errors.inwardNo}</span>}
                </div>

                <div className="form-group">
                  <label>Vehicle No *</label>
                  <input
                    type="text"
                    value={formData.vehicleNo}
                    onChange={(e) => setFormData({ ...formData, vehicleNo: e.target.value.toUpperCase() })}
                    className={errors.vehicleNo ? "error" : ""}
                    placeholder="Enter vehicle number"
                  />
                  {errors.vehicleNo && <span className="error-text">{errors.vehicleNo}</span>}
                </div>

                <div className="form-group">
                  <label>Date & Time *</label>
                  <input
                    type="datetime-local"
                    value={formData.dateTime}
                    onChange={(e) => setFormData({ ...formData, dateTime: e.target.value })}
                    className={errors.dateTime ? "error" : ""}
                  />
                  {errors.dateTime && <span className="error-text">{errors.dateTime}</span>}
                </div>

                <div className="form-group">
                  <label>Supplier Name *</label>
                  <input
                    type="text"
                    value={formData.supplierName}
                    onChange={(e) => setFormData({ ...formData, supplierName: e.target.value })}
                    className={errors.supplierName ? "error" : ""}
                    placeholder="Enter supplier name"
                  />
                  {errors.supplierName && <span className="error-text">{errors.supplierName}</span>}
                </div>

                <div className="form-group">
                  <label>Material *</label>
                  <input
                    type="text"
                    value={formData.material}
                    onChange={(e) => setFormData({ ...formData, material: e.target.value })}
                    className={errors.material ? "error" : ""}
                    placeholder="Enter material type"
                  />
                  {errors.material && <span className="error-text">{errors.material}</span>}
                </div>

                <div className="form-group">
                  <label>UOM (Units of Measurement) *</label>
                  <select
                    value={formData.uom}
                    onChange={(e) => setFormData({ ...formData, uom: e.target.value })}
                    className={errors.uom ? "error" : ""}
                  >
                    <option value="">Select UOM</option>
                    <option value="Tons">Tons</option>
                    <option value="Kg">Kg</option>
                    <option value="Pieces">Pieces</option>
                    <option value="Cubic Meter">Cubic Meter</option>
                    <option value="Square Meter">Square Meter</option>
                    <option value="Liters">Liters</option>
                  </select>
                  {errors.uom && <span className="error-text">{errors.uom}</span>}
                </div>

                <div className="form-group">
                  <label>Upload PDF</label>
                  <input type="file" accept=".pdf" onChange={handlePdfUpload} className="file-input" />
                  {pdfFile && <span className="file-name">📄 {pdfFile.name}</span>}
                </div>

                <div className="form-group">
                  <label>Supplier Bill Qty *</label>
                  <input
                    type="number"
                    step="0.01"
                    value={formData.supplierBillQty}
                    onChange={(e) => setFormData({ ...formData, supplierBillQty: e.target.value })}
                    className={errors.supplierBillQty ? "error" : ""}
                    placeholder="Enter bill quantity"
                  />
                  {errors.supplierBillQty && <span className="error-text">{errors.supplierBillQty}</span>}
                </div>

                <div className="form-group">
                  <label>Supplier Bill Rate (₹) *</label>
                  <input
                    type="number"
                    step="0.01"
                    value={formData.supplierBillRate}
                    onChange={(e) => setFormData({ ...formData, supplierBillRate: e.target.value })}
                    className={errors.supplierBillRate ? "error" : ""}
                    placeholder="Enter bill rate"
                  />
                  {errors.supplierBillRate && <span className="error-text">{errors.supplierBillRate}</span>}
                </div>

                <div className="form-group">
                  <label>Received Amount (₹) *</label>
                  <input
                    type="number"
                    step="0.01"
                    value={formData.receivedAmount}
                    onChange={(e) => setFormData({ ...formData, receivedAmount: e.target.value })}
                    className={errors.receivedAmount ? "error" : ""}
                    placeholder="Enter received amount"
                  />
                  {errors.receivedAmount && <span className="error-text">{errors.receivedAmount}</span>}
                </div>

                <div className="form-group">
                  <label>Amount (₹)</label>
                  <input type="number" step="0.01" value={formData.amount} readOnly className="readonly" />
                </div>

                <div className="form-group">
                  <label>Difference (₹)</label>
                  <input type="number" step="0.01" value={formData.difference} readOnly className="readonly" />
                </div>
              </div>

              <div className="form-actions">
                <button type="button" className="btn btn-cancel" onClick={resetForm}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-save">
                  Save Detail
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="details-table">
        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>PO Number</th>
              <th>Inward No</th>
              <th>Vehicle No</th>
              <th>Supplier</th>
              <th>Material</th>
              <th>Bill Qty</th>
              <th>Bill Rate</th>
              <th>Amount</th>
              <th>Difference</th>
              <th>Weighbridge Validation</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {supplierDetails.map((detail, index) => (
              <tr key={detail.id}>
                <td>{index + 1}</td>
                <td>{detail.poNumber}</td>
                <td>{detail.inwardNo}</td>
                <td>{detail.vehicleNo}</td>
                <td>{detail.supplierName}</td>
                <td>{detail.material}</td>
                <td>
                  {detail.supplierBillQty} {detail.uom}
                </td>
                <td>₹{detail.supplierBillRate}</td>
                <td>₹{detail.amount}</td>
                <td className={Number.parseFloat(detail.difference) >= 0 ? "positive" : "negative"}>
                  ₹{detail.difference}
                </td>
                <td>
                  <div className="validation-cell">
                    <div
                      className="validation-status"
                      style={{ color: getValidationStatusColor(detail.validation_status) }}
                      title={detail.validation_message || "No validation performed"}
                    >
                      {getValidationStatusIcon(detail.validation_status)} {detail.validation_status || "pending"}
                    </div>
                    {detail.weighbridge_qty && (
                      <div className="validation-details">
                        <small>
                          WB: {detail.weighbridge_qty} | Diff: {detail.weighbridge_difference} (
                          {detail.weighbridge_percentage_diff}%)
                        </small>
                      </div>
                    )}
                  </div>
                </td>
                <td>
                  <span className={`status ${detail.status.toLowerCase()}`}>{detail.status}</span>
                </td>
                <td>
                  <div className="action-buttons">
                    <button
                      className="btn btn-validate"
                      onClick={() => handleWeighbridgeValidation(detail.id)}
                      disabled={validationLoading[detail.id]}
                      title="Validate with Weighbridge"
                    >
                      {validationLoading[detail.id] ? "⏳" : "⚖️"}
                    </button>
                    {detail.status === "Pending" && (
                      <>
                        <button
                          className="btn btn-approve"
                          onClick={() => handleApproval(detail.id, "Approved")}
                          title="Approve"
                        >
                          ✅
                        </button>
                        <button
                          className="btn btn-reject"
                          onClick={() => handleApproval(detail.id, "Rejected")}
                          title="Reject"
                        >
                          ❌
                        </button>
                      </>
                    )}
                    <button className="btn btn-delete" onClick={() => handleDelete(detail.id)} title="Delete">
                      🗑
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {supplierDetails.length === 0 && (
          <div className="no-data">
            <p>No supplier details found</p>
          </div>
        )}
      </div>

      <style jsx>{`
        .validation-cell {
          text-align: center;
          min-width: 150px;
        }

        .validation-status {
          font-weight: bold;
          margin-bottom: 2px;
        }

        .validation-details {
          font-size: 10px;
          color: #666;
        }

        .btn-validate {
          background-color: #17a2b8;
          color: white;
          border: none;
          padding: 4px 8px;
          border-radius: 4px;
          cursor: pointer;
          margin-right: 4px;
        }

        .btn-validate:hover {
          background-color: #138496;
        }

        .btn-validate:disabled {
          background-color: #6c757d;
          cursor: not-allowed;
        }

        .positive {
          color: #28a745;
        }

        .negative {
          color: #dc3545;
        }
      `}</style>
    </div>
  )
}

export default SupplierDetail
