          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          margin-bottom: 30px;
          padding-bottom: 20px;
          border-bottom: 2px solid #e9ecef;
        }

        .section-title {
          display: flex;
          align-items: center;
          gap: 15px;
        }

        .section-icon {
          font-size: 32px;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          padding: 15px;
          border-radius: 12px;
          color: white;
        }

        .section-title h2 {
          margin: 0;
          color: #333;
          font-size: 28px;
        }

        .section-subtitle {
          margin: 5px 0 0 0;
          color: #666;
          font-size: 14px;
        }

        .auto-fill-info {
          margin-bottom: 30px;
        }

        .info-card {
          background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
          padding: 20px;
          border-radius: 12px;
          border-left: 4px solid #667eea;
        }

        .info-card h3 {
          margin: 0 0 15px 0;
          color: #333;
          font-size: 18px;
        }

        .auto-fill-steps {
          display: flex;
          gap: 30px;
          align-items: center;
        }

        .step {
          display: flex;
          align-items: center;
          gap: 10px;
          color: #666;
        }

        .step-number {
          background: #667eea;
          color: white;
          width: 24px;
          height: 24px;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 12px;
          font-weight: bold;
        }

        .form-container {
          background: white;
          padding: 30px;
          border-radius: 12px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
          margin-bottom: 30px;
        }

        .form-header {
          margin-bottom: 25px;
          text-align: center;
        }

        .form-header h3 {
          margin: 0 0 5px 0;
          color: #333;
          font-size: 22px;
        }

        .form-header p {
          margin: 0;
          color: #666;
          font-size: 14px;
        }

        .form-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
          gap: 20px;
          margin-bottom: 30px;
        }

        .form-group {
          display: flex;
          flex-direction: column;
          gap: 8px;
        }

        .form-group.highlight {
          background: #f8f9ff;
          padding: 15px;
          border-radius: 8px;
          border: 2px solid #667eea;
        }

        .form-group label {
          font-weight: 600;
          color: #333;
          font-size: 14px;
        }

        .form-group input {
          padding: 12px 16px;
          border: 2px solid #e1e5e9;
          border-radius: 8px;
          font-size: 14px;
          transition: border-color 0.3s ease;
        }

        .form-group input:focus {
          outline: none;
          border-color: #667eea;
        }

        .form-group input.error {
          border-color: #e74c3c;
        }

        .form-group input.readonly {
          background-color: #f8f9fa;
          cursor: not-allowed;
          color: #666;
        }

        .form-group small {
          color: #666;
          font-size: 12px;
          font-style: italic;
        }

        .loading-text {
          color: #667eea;
          font-weight: 500;
        }

        .error-text {
          color: #e74c3c;
          font-size: 12px;
        }

        .file-input {
          padding: 8px 12px !important;
        }

        .file-name {
          display: block;
          margin-top: 5px;
          color: #28a745;
          font-size: 12px;
          font-weight: 500;
        }

        .positive {
          color: #28a745;
          font-weight: 600;
        }

        .negative {
          color: #dc3545;
          font-weight: 600;
        }

        .form-actions {
          display: flex;
          gap: 15px;
          justify-content: center;
          padding-top: 20px;
          border-top: 1px solid #e9ecef;
        }

        .btn {
          padding: 12px 24px;
          border: none;
          border-radius: 8px;
          font-size: 14px;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease;
          display: flex;
          align-items: center;
          gap: 8px;
        }

        .btn-cancel {
          background: #6c757d;
          color: white;
        }

        .btn-cancel:hover {
          background: #5a6268;
        }

        .btn-save {
          background: linear-gradient(135deg, #28a745 0%, #20c997 100%);
          color: white;
        }

        .btn-save:hover:not(:disabled) {
          transform: translateY(-2px);
        }

        .btn-save:disabled {
          opacity: 0.6;
          cursor: not-allowed;
        }

        .btn-export {
          background: linear-gradient(135deg, #17a2b8 0%, #20c997 100%);
          color: white;
        }

        .btn-export:hover {
          transform: translateY(-2px);
        }

        .details-table {
          background: white;
          border-radius: 12px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
          overflow: hidden;
        }

        .table-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 20px 30px;
          background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
          border-bottom: 1px solid #dee2e6;
        }

        .table-header h3 {
