"use client"

import { useState, useEffect, useCallback } from "react"
import * as XLSX from "xlsx"

const SupplierDetail = () => {
  const [supplierDetails, setSupplierDetails] = useState([])
  const [autoFillLoading, setAutoFillLoading] = useState(false)
  const [billUploading, setBillUploading] = useState(false)
  const [formData, setFormData] = useState({
    poNumber: "",
    poBalanceQty: "",
    inwardNo: "",
    vehicleNo: "",
    dateTime: "",
    supplierName: "",
    material: "",
    uom: "tons",
    receivedQty: "",
    receivedBy: "",
    supplierBillQty: "",
    poRate: "",
    supplierBillRate: "",
    supplierBillFile: "",
    difference: "",
    status: "Pending",
  })
  const [errors, setErrors] = useState({})
  const [uploadedFile, setUploadedFile] = useState(null)

  useEffect(() => {
    fetchSupplierDetails()
  }, [])

  const fetchSupplierDetails = async () => {
    try {
      const response = await fetch("http://localhost:5000/supplier")
      const data = await response.json()
      setSupplierDetails(data)
    } catch (error) {
      console.error("Error fetching supplier details:", error)
    }
  }

  const handleVehicleNumberChange = async (vehicleNo) => {
    if (!vehicleNo.trim()) return

    setAutoFillLoading(true)
    try {
      const response = await fetch(`http://localhost:5000/supplier/auto-fill/${vehicleNo}`)
      if (response.ok) {
        const autoFillData = await response.json()
        setFormData((prev) => ({
          ...prev,
          ...autoFillData,
        }))
      } else {
        console.log("No auto-fill data found for this vehicle")
      }
    } catch (error) {
      console.error("Error fetching auto-fill data:", error)
    } finally {
      setAutoFillLoading(false)
    }
  }

  const handleFileUpload = async (e) => {
    const file = e.target.files[0]
    if (!file) return

    if (!formData.vehicleNo) {
      alert("Please enter vehicle number first")
      return
    }

    setBillUploading(true)
    setUploadedFile(file)

    const uploadFormData = new FormData()
    uploadFormData.append("file", file)
    uploadFormData.append("vehicle_number", formData.vehicleNo)

    try {
      const response = await fetch("http://localhost:5000/supplier/upload-bill", {
        method: "POST",
        body: uploadFormData,
      })

      const result = await response.json()

      if (response.ok && result.success) {
        // Get extracted bill data
        const billResponse = await fetch(`http://localhost:5000/supplier/bill-data/${formData.vehicleNo}`)
        if (billResponse.ok) {
          const billData = await billResponse.json()
          setFormData((prev) => ({
            ...prev,
            supplierBillQty: billData.supplierBillQty || "",
            supplierBillRate: billData.supplierBillRate || "",
            supplierBillFile: result.filename,
          }))
        }
        alert("Bill uploaded and processed successfully!")
      } else {
        alert(`Upload failed: ${result.message || result.error}`)
      }
    } catch (error) {
      console.error("Error uploading bill:", error)
      alert("Error uploading bill")
    } finally {
      setBillUploading(false)
    }
  }

  const calculateDifference = useCallback(() => {
    const poRate = Number.parseFloat(formData.poRate) || 0
    const supplierBillRate = Number.parseFloat(formData.supplierBillRate) || 0
    return (poRate - supplierBillRate).toFixed(2)
  }, [formData.poRate, formData.supplierBillRate])

  useEffect(() => {
    const difference = calculateDifference()
    setFormData((prev) => ({
      ...prev,
      difference: difference,
    }))
  }, [calculateDifference])

  const validateForm = () => {
    const newErrors = {}
    const requiredFields = [
      "poNumber",
      "poBalanceQty",
      "inwardNo",
      "vehicleNo",
      "dateTime",
      "supplierName",
      "material",
      "receivedQty",
      "receivedBy",
      "poRate",
    ]

    requiredFields.forEach((field) => {
      if (!formData[field] || !formData[field].toString().trim()) {
        newErrors[field] = `${field.replace(/([A-Z])/g, " $1").replace(/^./, (str) => str.toUpperCase())} is required`
      }
    })

    return newErrors
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    const formErrors = validateForm()

    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors)
      return
    }

    // Check if received quantity matches supplier bill quantity
    const receivedQty = Number.parseFloat(formData.receivedQty) || 0
    const supplierBillQty = Number.parseFloat(formData.supplierBillQty) || 0

    if (receivedQty !== supplierBillQty && supplierBillQty > 0) {
      const confirmMessage = `⚠️ QUANTITY MISMATCH DETECTED!

Received Quantity (Weighbridge): ${receivedQty} tons
Supplier Bill Quantity: ${supplierBillQty} tons
Difference: ${Math.abs(receivedQty - supplierBillQty)} tons

This indicates a discrepancy between the actual received quantity and the billed quantity. Please verify the quantities before proceeding.

Do you want to continue anyway?`

      if (!window.confirm(confirmMessage)) {
        return
      }
    }

    try {
      const response = await fetch("http://localhost:5000/supplier", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      })

      const result = await response.json()

      if (response.ok) {
        fetchSupplierDetails()
        resetForm()
        alert("Supplier detail added successfully!")
      } else {
        alert(`Error: ${result.error || "Failed to add supplier detail"}`)
      }
    } catch (error) {
      console.error("Error:", error)
      alert("Error adding supplier detail")
    }
  }

  const resetForm = () => {
    setFormData({
      poNumber: "",
      poBalanceQty: "",
      inwardNo: "",
      vehicleNo: "",
      dateTime: "",
      supplierName: "",
      material: "",
      uom: "tons",
      receivedQty: "",
      receivedBy: "",
      supplierBillQty: "",
      poRate: "",
      supplierBillRate: "",
      supplierBillFile: "",
      difference: "",
      status: "Pending",
    })
    setErrors({})
    setUploadedFile(null)
  }

  const handleApproval = async (id, action) => {
    try {
      const response = await fetch(`http://localhost:5000/supplier/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ status: action }),
      })

      if (response.ok) {
        fetchSupplierDetails()
        alert(`Supplier detail ${action.toLowerCase()} successfully!`)
      }
    } catch (error) {
      console.error("Error updating status:", error)
    }
  }

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this supplier detail?")) {
      try {
        const response = await fetch(`http://localhost:5000/supplier/${id}`, {
          method: "DELETE",
        })

        if (response.ok) {
          fetchSupplierDetails()
          alert("Supplier detail deleted successfully!")
        }
      } catch (error) {
        console.error("Error deleting supplier detail:", error)
      }
    }
  }

  const exportToExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(supplierDetails)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "Supplier Details")
    XLSX.writeFile(workbook, "supplier_details.xlsx")
  }

  const handleViewBill = (filename) => {
    if (filename) {
      // In a real application, you would serve files from a proper file server
      window.open(`http://localhost:5000/uploads/${filename}`, "_blank")
    } else {
      alert("No bill file available")
    }
  }

  return (
    <div className="supplier-detail">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">📄</div>
          <h2>Supplier Detail Management</h2>
          <p className="section-subtitle">
            Automatically populated from vehicle entry, PO details, and weighbridge data
          </p>
        </div>
        <div className="header-actions">
          <button className="btn btn-export" onClick={exportToExcel}>
            📊 Export Excel
          </button>
        </div>
      </div>

      <div className="auto-fill-info">
        <div className="info-card">
          <h3>🔄 Auto-Fill Process</h3>
          <div className="auto-fill-steps">
            <div className="step">
              <span className="step-number">1</span>
              <span>Enter Vehicle Number</span>
            </div>
            <div className="step">
              <span className="step-number">2</span>
              <span>Upload Supplier Bill</span>
            </div>
            <div className="step">
              <span className="step-number">3</span>
              <span>Review & Submit</span>
            </div>
          </div>
        </div>
      </div>

      <div className="form-container">
        <div className="form-header">
          <h3>Add New Supplier Detail</h3>
          <p>Enter vehicle number to auto-populate data from various sources</p>
        </div>

        <form onSubmit={handleSubmit} className="supplier-form">
          <div className="form-grid">
            <div className="form-group highlight">
              <label>Vehicle No *</label>
              <input
                type="text"
                value={formData.vehicleNo}
                onChange={(e) => {
                  const value = e.target.value.toUpperCase()
                  setFormData({ ...formData, vehicleNo: value })
                  if (value.length >= 6) {
                    handleVehicleNumberChange(value)
                  }
                }}
                className={errors.vehicleNo ? "error" : ""}
                placeholder="Enter vehicle number (e.g., TN52J9597)"
              />
              {autoFillLoading && <small className="loading-text">🔄 Loading auto-fill data...</small>}
              {errors.vehicleNo && <span className="error-text">{errors.vehicleNo}</span>}
            </div>

            <div className="form-group">
              <label>PO Number *</label>
              <input
                type="text"
                value={formData.poNumber}
                readOnly
                className="readonly"
                placeholder="Auto-filled from PO details"
              />
              <small>📋 From PO Details Table</small>
            </div>

            <div className="form-group">
              <label>PO Balance Qty *</label>
              <input
                type="number"
                step="0.01"
                value={formData.poBalanceQty}
                readOnly
                className="readonly"
                placeholder="Auto-calculated"
              />
              <small>📊 Calculated from PO & Previous Deliveries</small>
            </div>

            <div className="form-group">
              <label>Inward No *</label>
              <input
                type="text"
                value={formData.inwardNo}
                readOnly
                className="readonly"
                placeholder="Auto-filled from vehicle entry"
              />
              <small>🚛 From Vehicles Table</small>
            </div>

            <div className="form-group">
              <label>Date & Time *</label>
              <input type="datetime-local" value={formData.dateTime} readOnly className="readonly" />
              <small>⏰ From Vehicle Entry Time</small>
            </div>

            <div className="form-group">
              <label>Supplier Name *</label>
              <input
                type="text"
                value={formData.supplierName}
                readOnly
                className="readonly"
                placeholder="Auto-filled from PO"
              />
              <small>🏢 From PO Details Table</small>
            </div>

            <div className="form-group">
              <label>Material *</label>
              <input
                type="text"
                value={formData.material}
                readOnly
                className="readonly"
                placeholder="Auto-filled from PO"
              />
              <small>📦 From PO Details Table</small>
            </div>

            <div className="form-group">
              <label>UOM</label>
              <input type="text" value={formData.uom} readOnly className="readonly" />
              <small>⚖️ Always "tons"</small>
            </div>

            <div className="form-group">
              <label>Received Qty *</label>
              <input
                type="number"
                step="0.01"
                value={formData.receivedQty}
                readOnly
                className="readonly"
                placeholder="Auto-filled from weighbridge"
              />
              <small>⚖️ From Weighbridge Data Table</small>
            </div>

            <div className="form-group">
              <label>Received By *</label>
              <input
                type="text"
                value={formData.receivedBy}
                readOnly
                className="readonly"
                placeholder="Auto-filled from login"
              />
              <small>👤 From Login Table</small>
            </div>

            <div className="form-group">
              <label>PO Rate (₹) *</label>
              <input
                type="number"
                step="0.01"
                value={formData.poRate}
                readOnly
                className="readonly"
                placeholder="Auto-filled from PO"
              />
              <small>💰 From PO Details Table</small>
            </div>

            <div className="form-group highlight">
              <label>Upload Supplier Bill *</label>
              <input
                type="file"
                accept=".pdf,.jpg,.jpeg,.png"
                onChange={handleFileUpload}
                className="file-input"
                disabled={billUploading || !formData.vehicleNo}
              />
              {billUploading && <small className="loading-text">🔄 Processing bill with AI extractor...</small>}
              {uploadedFile && <span className="file-name">📄 {uploadedFile.name}</span>}
              <small>🤖 Processed by AI Extractor Service</small>
            </div>

            <div className="form-group">
              <label>Supplier Bill Qty</label>
              <input
                type="number"
                step="0.01"
                value={formData.supplierBillQty}
                readOnly
                className="readonly"
                placeholder="Auto-filled from bill extraction"
              />
              <small>🤖 From Invoice Items Table (AI Extracted)</small>
            </div>

            <div className="form-group">
              <label>Supplier Bill Rate (₹)</label>
              <input
                type="number"
                step="0.01"
                value={formData.supplierBillRate}
                readOnly
                className="readonly"
                placeholder="Auto-filled from bill extraction"
              />
              <small>🤖 From Invoice Items Table (AI Extracted)</small>
            </div>

            <div className="form-group">
              <label>Difference (₹)</label>
              <input
                type="number"
                step="0.01"
                value={formData.difference}
                readOnly
                className={`readonly ${Number.parseFloat(formData.difference) >= 0 ? "positive" : "negative"}`}
              />
              <small>📊 PO Rate - Supplier Bill Rate</small>
            </div>
          </div>

          <div className="form-actions">
            <button type="button" className="btn btn-cancel" onClick={resetForm}>
              Cancel
            </button>
            <button type="submit" className="btn btn-save" disabled={billUploading || autoFillLoading}>
              Save Supplier Detail
            </button>
          </div>
        </form>
      </div>

      <div className="details-table">
        <div className="table-header">
          <h3>Supplier Details</h3>
          <div className="table-stats">
            <span className="stat">Total: {supplierDetails.length}</span>
            <span className="stat pending">
              Pending: {supplierDetails.filter((d) => d.status === "Pending").length}
            </span>
            <span className="stat approved">
              Approved: {supplierDetails.filter((d) => d.status === "Approved").length}
            </span>
            <span className="stat rejected">
              Rejected: {supplierDetails.filter((d) => d.status === "Rejected").length}
            </span>
          </div>
        </div>

        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>S.No</th>
                <th>PO Number</th>
                <th>PO Balance Qty</th>
                <th>Inward No</th>
                <th>Vehicle No</th>
                <th>Supplier</th>
                <th>Material</th>
                <th>Received Qty</th>
                <th>Received By</th>
                <th>Supplier Bill Qty</th>
                <th>PO Rate</th>
                <th>Supplier Bill Rate</th>
                <th>Difference</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {supplierDetails.map((detail, index) => (
                <tr key={detail.id}>
                  <td>{index + 1}</td>
                  <td>{detail.poNumber}</td>
                  <td>
                    {detail.poBalanceQty} {detail.uom}
                  </td>
                  <td>{detail.inwardNo}</td>
                  <td>{detail.vehicleNo}</td>
                  <td>{detail.supplierName}</td>
                  <td>{detail.material}</td>
                  <td>
                    {detail.receivedQty} {detail.uom}
                  </td>
                  <td>{detail.receivedBy}</td>
                  <td className={detail.receivedQty !== detail.supplierBillQty ? "quantity-mismatch" : ""}>
                    {detail.supplierBillQty} {detail.uom}
                    {detail.receivedQty !== detail.supplierBillQty && detail.supplierBillQty > 0 && (
                      <span className="mismatch-warning" title="Quantity mismatch detected!">
                        ⚠️
                      </span>
                    )}
                  </td>
                  <td>₹{detail.poRate}</td>
                  <td>₹{detail.supplierBillRate}</td>
                  <td className={Number.parseFloat(detail.difference) >= 0 ? "positive" : "negative"}>
                    ₹{detail.difference}
                  </td>
                  <td>
                    <span className={`status ${detail.status.toLowerCase()}`}>{detail.status}</span>
                  </td>
                  <td>
                    <div className="action-buttons">
                      {detail.status === "Pending" && (
                        <>
                          <button
                            className="btn btn-approve"
                            onClick={() => handleApproval(detail.id, "Approved")}
                            title="Approve"
                          >
                            ✅
                          </button>
                          <button
                            className="btn btn-reject"
                            onClick={() => handleApproval(detail.id, "Rejected")}
                            title="Reject"
                          >
                            ❌
                          </button>
                        </>
                      )}
                      <button
                        className="btn btn-view"
                        onClick={() => handleViewBill(detail.supplierBillFile)}
                        title="View Bill"
                      >
                        👁️
                      </button>
                      <button className="btn btn-delete" onClick={() => handleDelete(detail.id)} title="Delete">
                        🗑
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {supplierDetails.length === 0 && (
            <div className="no-data">
              <div className="no-data-icon">📄</div>
              <h3>No Supplier Details Found</h3>
              <p>Enter a vehicle number above to start adding supplier details</p>
            </div>
          )}
        </div>
      </div>

      <style jsx>{`
        .supplier-detail {
          padding: 20px;
          max-width: 1400px;
          margin: 0 auto;
        }

        .section-header {
          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          margin-bottom: 30px;
          padding-bottom: 20px;
          border-bottom: 2px solid #e9ecef;
        }

        .section-title {
          display: flex;
          align-items: center;
          gap: 15px;
        }

        .section-icon {
          font-size: 32px;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          padding: 15px;
          border-radius: 12px;
          color: white;
        }

        .section-title h2 {
          margin: 0;
          color: #333;
          font-size: 28px;
        }

        .section-subtitle {
          margin: 5px 0 0 0;
          color: #666;
          font-size: 14px;
        }

        .auto-fill-info {
          margin-bottom: 30px;
        }

        .info-card {
          background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
          padding: 20px;
          border-radius: 12px;
          border-left: 4px solid #667eea;
        }

        .info-card h3 {
          margin: 0 0 15px 0;
          color: #333;
          font-size: 18px;
        }

        .auto-fill-steps {
          display: flex;
          gap: 30px;
          align-items: center;
        }

        .step {
          display: flex;
          align-items: center;
          gap: 10px;
          color: #666;
        }

        .step-number {
          background: #667eea;
          color: white;
          width: 24px;
          height: 24px;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 12px;
          font-weight: bold;
        }

        .form-container {
          background: white;
          padding: 30px;
          border-radius: 12px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
          margin-bottom: 30px;
        }

        .form-header {
          margin-bottom: 25px;
          text-align: center;
        }

        .form-header h3 {
          margin: 0 0 5px 0;
          color: #333;
          font-size: 22px;
        }

        .form-header p {
          margin: 0;
          color: #666;
          font-size: 14px;
        }

        .form-grid {
