"use client"

import { useState, useEffect } from "react"
import * as XLSX from "xlsx"

const RoleManagement = () => {
  const [roles, setRoles] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [editingRole, setEditingRole] = useState(null)
  const [formData, setFormData] = useState({
    roleName: "",
    permissions: {
      employees: { add: false, update: false, delete: false },
      branches: { add: false, update: false, delete: false },
      projects: { add: false, update: false, delete: false },
      vehicles: { add: false, update: false, delete: false },
      suppliers: { add: false, update: false, delete: false },
      po: { add: false, update: false, delete: false },
      grn: { add: false, update: false, delete: false },
      logs: { add: false, update: false, delete: false },
    },
  })
  const [errors, setErrors] = useState({})

  const modules = [
    { key: "employees", name: "Employee Management" },
    { key: "branches", name: "Branch Management" },
    { key: "projects", name: "Project Management" },
    { key: "vehicles", name: "Vehicle Entry" },
    { key: "suppliers", name: "Supplier Details" },
    { key: "po", name: "Purchase Orders" },
    { key: "grn", name: "GRN Management" },
    { key: "logs", name: "System Logs" },
  ]

  useEffect(() => {
    const savedRoles = localStorage.getItem("roles")
    if (savedRoles) {
      setRoles(JSON.parse(savedRoles))
    }
  }, [])

  const saveRoles = (roleList) => {
    localStorage.setItem("roles", JSON.stringify(roleList))
    setRoles(roleList)
  }

  const validateForm = () => {
    const newErrors = {}

    if (!formData.roleName.trim()) {
      newErrors.roleName = "Role name is required"
    }

    return newErrors
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    const formErrors = validateForm()

    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors)
      return
    }

    const newRole = {
      ...formData,
      id: editingRole ? editingRole.id : Date.now(),
      createdAt: editingRole ? editingRole.createdAt : new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    }

    let updatedRoles
    if (editingRole) {
      updatedRoles = roles.map((role) => (role.id === editingRole.id ? newRole : role))
    } else {
      updatedRoles = [...roles, newRole]
    }

    saveRoles(updatedRoles)
    resetForm()
  }

  const resetForm = () => {
    setFormData({
      roleName: "",
      permissions: {
        employees: { add: false, update: false, delete: false },
        branches: { add: false, update: false, delete: false },
        projects: { add: false, update: false, delete: false },
        vehicles: { add: false, update: false, delete: false },
        suppliers: { add: false, update: false, delete: false },
        po: { add: false, update: false, delete: false },
        grn: { add: false, update: false, delete: false },
        logs: { add: false, update: false, delete: false },
      },
    })
    setErrors({})
    setShowForm(false)
    setEditingRole(null)
  }

  const handleEdit = (role) => {
    setFormData(role)
    setEditingRole(role)
    setShowForm(true)
  }

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this role?")) {
      const updatedRoles = roles.filter((role) => role.id !== id)
      saveRoles(updatedRoles)
    }
  }

  const handlePermissionChange = (module, action, value) => {
    setFormData((prev) => ({
      ...prev,
      permissions: {
        ...prev.permissions,
        [module]: {
          ...prev.permissions[module],
          [action]: value,
        },
      },
    }))
  }

  const exportToExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(roles)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "Roles")
    XLSX.writeFile(workbook, "roles.xlsx")
  }

  return (
    <div className="role-management">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">üëî</div>
          <h2>Role Management</h2>
        </div>
        <div className="header-actions">
          <button className="btn btn-export" onClick={exportToExcel}>
            üìä Export Excel
          </button>
          <button className="btn btn-add" onClick={() => setShowForm(true)}>
            ‚ûï Add Role
          </button>
        </div>
      </div>

      {showForm && (
        <div className="modal-overlay">
          <div className="modal large-modal">
            <div className="modal-header">
              <h3>{editingRole ? "Edit Role" : "Add New Role"}</h3>
              <button className="close-btn" onClick={resetForm}>
                ‚úï
              </button>
            </div>

            <form onSubmit={handleSubmit} className="role-form">
              <div className="form-grid">
                <div className="form-group">
                  <label>Role Name *</label>
                  <input
                    type="text"
                    value={formData.roleName}
                    onChange={(e) => setFormData({ ...formData, roleName: e.target.value })}
                    className={errors.roleName ? "error" : ""}
                    placeholder="Enter role name"
                  />
                  {errors.roleName && <span className="error-text">{errors.roleName}</span>}
                </div>
              </div>

              <div className="permissions-section">
                <h4>Permissions</h4>
                <div className="permissions-table">
                  <table>
                    <thead>
                      <tr>
                        <th>Module</th>
                        <th>Add</th>
                        <th>Update</th>
                        <th>Delete</th>
                      </tr>
                    </thead>
                    <tbody>
                      {modules.map((module) => (
                        <tr key={module.key}>
                          <td>{module.name}</td>
                          <td>
                            <input
                              type="checkbox"
                              checked={formData.permissions[module.key]?.add || false}
                              onChange={(e) => handlePermissionChange(module.key, "add", e.target.checked)}
                            />
                          </td>
                          <td>
                            <input
                              type="checkbox"
                              checked={formData.permissions[module.key]?.update || false}
                              onChange={(e) => handlePermissionChange(module.key, "update", e.target.checked)}
                            />
                          </td>
                          <td>
                            <input
                              type="checkbox"
                              checked={formData.permissions[module.key]?.delete || false}
                              onChange={(e) => handlePermissionChange(module.key, "delete", e.target.checked)}
                            />
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>

              <div className="form-actions">
                <button type="button" className="btn btn-cancel" onClick={resetForm}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-save">
                  {editingRole ? "Update" : "Save"} Role
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="roles-table">
        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>Role Name</th>
              <th>Permissions Summary</th>
              <th>Created Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {roles.map((role, index) => (
              <tr key={role.id}>
                <td>{index + 1}</td>
                <td>{role.roleName}</td>
                <td>
                  <div className="permissions-summary">
                    {Object.entries(role.permissions).map(([module, perms]) => {
                      const activePerms = Object.entries(perms).filter(([_, value]) => value)
                      if (activePerms.length > 0) {
                        return (
                          <span key={module} className="permission-badge">
                            {modules.find((m) => m.key === module)?.name}:{" "}
                            {activePerms.map(([perm]) => perm).join(", ")}
                          </span>
                        )
                      }
                      return null
                    })}
                  </div>
                </td>
                <td>{new Date(role.createdAt).toLocaleDateString()}</td>
                <td>
                  <div className="action-buttons">
                    <button className="btn btn-edit" onClick={() => handleEdit(role)} title="Edit Role">
                      ‚úèÔ∏è
                    </button>
                    <button className="btn btn-delete" onClick={() => handleDelete(role.id)} title="Delete Role">
                      üóëÔ∏è
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {roles.length === 0 && (
          <div className="no-data">
            <p>No roles found</p>
          </div>
        )}
      </div>
    </div>
  )
}

export default RoleManagement
