"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import * as XLSX from "xlsx"

const PODetails = () => {
  const [poDetails, setPODetails] = useState([])
  const [excelData1, setExcelData1] = useState([])
  const [excelHeaders1, setExcelHeaders1] = useState([])
  const [excelData2, setExcelData2] = useState([])
  const [excelHeaders2, setExcelHeaders2] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [editingPO, setEditingPO] = useState(null)
  const [formData, setFormData] = useState({
    poNumber: "",
    supplier: "",
    material: "",
    quantity: "",
    rate: "",
    deliveryDate: "",
    narration: "",
    status: "Active",
    poType: "",
  })
  const [errors, setErrors] = useState({})

  useEffect(() => {
  loadExcelDataFromStorage()
}, [])

const loadExcelDataFromStorage = () => {
  const data1 = localStorage.getItem("excelData1")
  const headers1 = localStorage.getItem("excelHeaders1")
  const data2 = localStorage.getItem("excelData2")
  const headers2 = localStorage.getItem("excelHeaders2")

  if (data1 && headers1) {
    setExcelData1(JSON.parse(data1))
    setExcelHeaders1(JSON.parse(headers1))
  }

  if (data2 && headers2) {
    setExcelData2(JSON.parse(data2))
    setExcelHeaders2(JSON.parse(headers2))
  }
}


  const saveExcelDataToStorage = (data, headers) => {
    localStorage.setItem("poExcelData", JSON.stringify(data))
    localStorage.setItem("poExcelHeaders", JSON.stringify(headers))
  }

  const fetchPODetails = async () => {
    const res = await axios.get("http://localhost:5000/po")
    setPODetails(res.data)
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    const formErrors = validateForm()
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors)
      return
    }

    const payload = {
      ...formData,
      quantity: Number.parseFloat(formData.quantity),
      rate: Number.parseFloat(formData.rate),
      totalAmount: Number.parseFloat(formData.quantity) * Number.parseFloat(formData.rate),
    }

    if (editingPO) {
      await axios.put(`http://localhost:5000/po/${editingPO.id}`, payload)
    } else {
      await axios.post("http://localhost:5000/po", payload)
    }

    fetchPODetails()
    resetForm()
  }

  const handleDelete = async (id) => {
    if (window.confirm("Delete this PO?")) {
      await axios.delete(`http://localhost:5000/po/${id}`)
      fetchPODetails()
    }
  }

  const handleEdit = (po) => {
    setFormData(po)
    setEditingPO(po)
    setShowForm(true)
  }

  const resetForm = () => {
    setFormData({
      poNumber: "",
      supplier: "",
      material: "",
      quantity: "",
      rate: "",
      deliveryDate: "",
      narration: "",
      status: "Active",
      poType: "",
    })
    setErrors({})
    setEditingPO(null)
    setShowForm(false)
  }

  const validateForm = () => {
    const newErrors = {}
    if (!formData.poNumber) newErrors.poNumber = "Required"
    if (!formData.supplier) newErrors.supplier = "Required"
    if (!formData.material) newErrors.material = "Required"
    if (!formData.quantity || formData.quantity <= 0) newErrors.quantity = "Invalid"
    if (!formData.rate || formData.rate <= 0) newErrors.rate = "Invalid"
    if (!formData.deliveryDate) newErrors.deliveryDate = "Required"
    if (!formData.poType) newErrors.poType = "Required"
    return newErrors
  }

  const exportToExcel = () => {
    const ws = XLSX.utils.json_to_sheet(poDetails)
    const wb = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(wb, ws, "PO")
    XLSX.writeFile(wb, "po_details.xlsx")
  }

  const handleExcelUpload1 = (e) => {
  const file = e.target.files[0]
  if (file) {
    const reader = new FileReader()
    reader.onload = (event) => {
      const data = new Uint8Array(event.target.result)
      const workbook = XLSX.read(data, { type: "array" })
      const sheetName = workbook.SheetNames[0]
      const worksheet = workbook.Sheets[sheetName]
      const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 })
      const headers = jsonData[0]
      const rows = jsonData.slice(1)
      setExcelHeaders1(headers)
      setExcelData1(rows)
      localStorage.setItem("excelData1", JSON.stringify(rows))
      localStorage.setItem("excelHeaders1", JSON.stringify(headers))
    }
    reader.readAsArrayBuffer(file)
  }
}

const handleExcelUpload2 = (e) => {
  const file = e.target.files[0]
  if (file) {
    const reader = new FileReader()
    reader.onload = (event) => {
      const data = new Uint8Array(event.target.result)
      const workbook = XLSX.read(data, { type: "array" })
      const sheetName = workbook.SheetNames[0]
      const worksheet = workbook.Sheets[sheetName]
      const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 })
      const headers = jsonData[0]
      const rows = jsonData.slice(1)
      setExcelHeaders2(headers)
      setExcelData2(rows)
      localStorage.setItem("excelData2", JSON.stringify(rows))
      localStorage.setItem("excelHeaders2", JSON.stringify(headers))
    }
    reader.readAsArrayBuffer(file)
  }
}

  const clearExcelData1 = () => {
  setExcelData1([])
  setExcelHeaders1([])
  localStorage.removeItem("excelData1")
  localStorage.removeItem("excelHeaders1")
}

const clearExcelData2 = () => {
  setExcelData2([])
  setExcelHeaders2([])
  localStorage.removeItem("excelData2")
  localStorage.removeItem("excelHeaders2")
}


  return (
    <div className="po-wrapper">
      <div className="po-header">
        <h2>Purchase Order Details</h2>
        <div>
          <button className="btn export" onClick={exportToExcel}>
            Export Excel
          </button>
          <button className="btn add" onClick={() => setShowForm(true)}>
            Add PO
          </button>
        </div>
      </div>

      <table className="po-table">
        <thead>
          <tr>
            <th>S.No</th>
            <th>PO Number</th>
            <th>Supplier</th>
            <th>Material</th>
            <th>Qty</th>
            <th>Rate</th>
            <th>Amount</th>
            <th>Delivery</th>
            <th>Type</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {poDetails.map((po, i) => (
            <tr key={po.id}>
              <td>{i + 1}</td>
              <td>{po.poNumber}</td>
              <td>{po.supplier}</td>
              <td>{po.material}</td>
              <td>{po.quantity}</td>
              <td>₹{po.rate}</td>
              <td>₹{po.totalAmount}</td>
              <td>{new Date(po.deliveryDate).toLocaleDateString()}</td>
              <td>{po.poType}</td>
              <td>{po.status}</td>
              <td>
                <button className="btn edit" onClick={() => handleEdit(po)}>
                  ✏
                </button>
                <button className="btn delete" onClick={() => handleDelete(po.id)}>
                  🗑
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Excel Data Section */}
      <div className="excel-section" style={{ marginTop: "40px" }}>
        <div className="po-header">
          <h3>Excel Data Import</h3>
          <div>
            <input
              type="file"
              accept=".xlsx,.xls"
              onChange={handleExcelUpload}
              style={{ display: "none" }}
              id="excel-upload"
            />
            <label htmlFor="excel-upload" className="btn add">
              Load Data
            </label>
            {excelData.length > 0 && (
              <button className="btn delete" onClick={clearExcelData}>
                Clear Data
              </button>
            )}
          </div>
        </div>

        {excelData.length > 0 && (
          <div style={{ maxHeight: "400px", overflowY: "auto", overflowX: "auto" }}>
            <table className="po-table">
              <thead>
                <tr>
                  <th>S.No</th>
                  {excelHeaders.map((header, index) => (
                    <th key={index}>{header}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {excelData.map((row, rowIndex) => (
                  <tr key={rowIndex}>
                    <td>{rowIndex + 1}</td>
                    {row.map((cell, cellIndex) => (
                      <td key={cellIndex}>{cell || ""}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Excel Data Section */}
      <div className="excel-section" style={{ marginTop: "40px" }}>
        <div className="po-header">
          <h3>Excel Data Import</h3>
          <div>
            <input
              type="file"
              accept=".xlsx,.xls"
              onChange={handleExcelUpload}
              style={{ display: "none" }}
              id="excel-upload"
            />
            <label htmlFor="excel-upload" className="btn add">
              Load Data
            </label>
            {excelData.length > 0 && (
              <button className="btn delete" onClick={clearExcelData}>
                Clear Data
              </button>
            )}
          </div>
        </div>

        {excelData.length > 0 && (
          <div style={{ maxHeight: "400px", overflowY: "auto", overflowX: "auto" }}>
            <table className="po-table">
              <thead>
                <tr>
                  <th>S.No</th>
                  {excelHeaders.map((header, index) => (
                    <th key={index}>{header}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {excelData.map((row, rowIndex) => (
                  <tr key={rowIndex}>
                    <td>{rowIndex + 1}</td>
                    {row.map((cell, cellIndex) => (
                      <td key={cellIndex}>{cell || ""}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
      {showForm && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>{editingPO ? "Edit PO" : "Add PO"}</h3>
            <form onSubmit={handleSubmit} className="po-form">
              <input
                placeholder="PO Number"
                value={formData.poNumber}
                onChange={(e) => setFormData({ ...formData, poNumber: e.target.value })}
              />
              <input
                placeholder="Supplier"
                value={formData.supplier}
                onChange={(e) => setFormData({ ...formData, supplier: e.target.value })}
              />
              <input
                placeholder="Material"
                value={formData.material}
                onChange={(e) => setFormData({ ...formData, material: e.target.value })}
              />
              <input
                type="number"
                placeholder="Quantity"
                value={formData.quantity}
                onChange={(e) => setFormData({ ...formData, quantity: e.target.value })}
              />
              <input
                type="number"
                placeholder="Rate"
                value={formData.rate}
                onChange={(e) => setFormData({ ...formData, rate: e.target.value })}
              />
              <input
                type="date"
                value={formData.deliveryDate}
                onChange={(e) => setFormData({ ...formData, deliveryDate: e.target.value })}
              />
              <textarea
                placeholder="Narration"
                value={formData.narration}
                onChange={(e) => setFormData({ ...formData, narration: e.target.value })}
              />

              <select value={formData.poType} onChange={(e) => setFormData({ ...formData, poType: e.target.value })}>
                <option value="">Select PO Type</option>
                <option value="Local">Local</option>
                <option value="Import">Import</option>
              </select>

              <select value={formData.status} onChange={(e) => setFormData({ ...formData, status: e.target.value })}>
                <option value="Active">Active</option>
                <option value="Completed">Completed</option>
                <option value="Cancelled">Cancelled</option>
                <option value="Pending">Pending</option>
              </select>

              <div className="form-buttons">
                <button type="button" className="btn cancel" onClick={resetForm}>
                  Cancel
                </button>
                <button type="submit" className="btn save">
                  Save PO
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  )
}

export default PODetails
