"use client"

import { useState, useEffect, useCallback } from "react"
import * as XLSX from "xlsx"

const SupplierDetail = () => {
  const [supplierDetails, setSupplierDetails] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [supplierBillFile, setSupplierBillFile] = useState(null)
  const [formData, setFormData] = useState({
    poNumber: "",
    poBalanceQty: "",
    poRate: "",
    inwardNo: "",
    vehicleNo: "",
    dateTime: "",
    supplierName: "",
    material: "",
    uom: "",
    receivedBy: "",
    supplierBillQty: "",
    supplierBillRate: "",
    receivedAmount: "",
    amount: "",
    difference: "",
    status: "Pending",
  })
  const [errors, setErrors] = useState({})

  // Mock data for testing
  const mockSupplierDetails = [
    {
      id: 1,
      sno: 1,
      poNumber: "PO202312001",
      poBalanceQty: "50.00",
      poRate: "5000.00",
      inwardNo: "INW20231201001",
      vehicleNo: "MH12AB1234",
      dateTime: "2023-12-01T09:30",
      supplierName: "ABC Construction Materials",
      material: "Portland Cement",
      uom: "Tons",
      receivedBy: "John Smith",
      supplierBillFile: "supplier_bill_001.pdf",
      supplierBillQty: "48.50",
      supplierBillRate: "5100.00",
      receivedAmount: "247350.00",
      amount: "247350.00",
      difference: "0.00",
      status: "Approved",
      createdAt: "2023-12-01T09:30:00.000Z",
    },
    {
      id: 2,
      sno: 2,
      poNumber: "PO202312002",
      poBalanceQty: "100.00",
      poRate: "45000.00",
      inwardNo: "INW20231201002",
      vehicleNo: "MH14CD5678",
      dateTime: "2023-12-01T11:15",
      supplierName: "XYZ Steel Suppliers",
      material: "TMT Steel Bars",
      uom: "Tons",
      receivedBy: "Sarah Johnson",
      supplierBillFile: "supplier_bill_002.pdf",
      supplierBillQty: "95.75",
      supplierBillRate: "46000.00",
      receivedAmount: "4404500.00",
      amount: "4404500.00",
      difference: "0.00",
      status: "Pending",
      createdAt: "2023-12-01T11:15:00.000Z",
    },
    {
      id: 3,
      sno: 3,
      poNumber: "PO202312003",
      poBalanceQty: "200.00",
      poRate: "1500.00",
      inwardNo: "INW20231201003",
      vehicleNo: "GJ05EF9012",
      dateTime: "2023-12-01T14:45",
      supplierName: "PQR Sand & Gravel",
      material: "River Sand",
      uom: "Cubic Meter",
      receivedBy: "Mike Wilson",
      supplierBillFile: null,
      supplierBillQty: "195.00",
      supplierBillRate: "1550.00",
      receivedAmount: "302250.00",
      amount: "302250.00",
      difference: "0.00",
      status: "Rejected",
      createdAt: "2023-12-01T14:45:00.000Z",
    },
    {
      id: 4,
      sno: 4,
      poNumber: "PO202312004",
      poBalanceQty: "75.00",
      poRate: "8500.00",
      inwardNo: "INW20231202001",
      vehicleNo: "KA09MN3456",
      dateTime: "2023-12-02T10:20",
      supplierName: "DEF Brick Works",
      material: "Red Bricks",
      uom: "Thousands",
      receivedBy: "Emily Davis",
      supplierBillFile: "supplier_bill_004.jpg",
      supplierBillQty: "72.50",
      supplierBillRate: "8600.00",
      receivedAmount: "623500.00",
      amount: "623500.00",
      difference: "0.00",
      status: "Pending",
      createdAt: "2023-12-02T10:20:00.000Z",
    },
    {
      id: 5,
      sno: 5,
      poNumber: "PO202312005",
      poBalanceQty: "150.00",
      poRate: "2200.00",
      inwardNo: "INW20231202002",
      vehicleNo: "TN11XY7890",
      dateTime: "2023-12-02T15:30",
      supplierName: "GHI Aggregates",
      material: "Blue Metal",
      uom: "Tons",
      receivedBy: "Robert Brown",
      supplierBillFile: "supplier_bill_005.pdf",
      supplierBillQty: "148.75",
      supplierBillRate: "2250.00",
      receivedAmount: "334687.50",
      amount: "334687.50",
      difference: "0.00",
      status: "Approved",
      createdAt: "2023-12-02T15:30:00.000Z",
    },
  ]

  useEffect(() => {
    const savedDetails = localStorage.getItem("supplierDetails")
    if (savedDetails) {
      setSupplierDetails(JSON.parse(savedDetails))
    } else {
      // Initialize with mock data if no saved data
      setSupplierDetails(mockSupplierDetails)
      localStorage.setItem("supplierDetails", JSON.stringify(mockSupplierDetails))
    }

    // Set current date and time
    const now = new Date()
    setFormData((prev) => ({
      ...prev,
      dateTime: now.toISOString().slice(0, 16),
    }))
  }, [])

  const saveSupplierDetails = (details) => {
    localStorage.setItem("supplierDetails", JSON.stringify(details))
    setSupplierDetails(details)
  }

  const handleSupplierBillUpload = (e) => {
    const file = e.target.files[0]
    if (file && (file.type === "application/pdf" || file.type.startsWith("image/"))) {
      setSupplierBillFile(file)
    } else {
      alert("Please upload a PDF or image file only")
    }
  }

  const handleFileUpload = (id) => {
    const input = document.createElement("input")
    input.type = "file"
    input.accept = ".pdf,.jpg,.jpeg,.png,.gif"
    input.onchange = (e) => {
      const file = e.target.files[0]
      if (file && (file.type === "application/pdf" || file.type.startsWith("image/"))) {
        const updatedDetails = supplierDetails.map((detail) =>
          detail.id === id ? { ...detail, supplierBillFile: file.name, updatedAt: new Date().toISOString() } : detail,
        )
        saveSupplierDetails(updatedDetails)
        alert(`File "${file.name}" uploaded successfully!`)
      } else {
        alert("Please upload a PDF or image file only")
      }
    }
    input.click()
  }

  const calculateAmount = useCallback(() => {
    const qty = Number.parseFloat(formData.supplierBillQty) || 0
    const rate = Number.parseFloat(formData.supplierBillRate) || 0
    return (qty * rate).toFixed(2)
  }, [formData.supplierBillQty, formData.supplierBillRate])

  const calculateDifference = useCallback(() => {
    const receivedAmount = Number.parseFloat(formData.receivedAmount) || 0
    const amount = Number.parseFloat(formData.amount) || 0
    return (amount - receivedAmount).toFixed(2)
  }, [formData.receivedAmount, formData.amount])

  useEffect(() => {
    const amount = calculateAmount()
    const difference = calculateDifference()

    setFormData((prev) => ({
      ...prev,
      amount: amount,
      difference: difference,
    }))
  }, [calculateAmount, calculateDifference])

  const validateForm = () => {
    const newErrors = {}

    if (!formData.poNumber.trim()) newErrors.poNumber = "PO Number is required"
    if (!formData.poBalanceQty.trim()) newErrors.poBalanceQty = "PO Balance Qty is required"
    if (!formData.poRate.trim()) newErrors.poRate = "PO Rate is required"
    if (!formData.inwardNo.trim()) newErrors.inwardNo = "Inward No is required"
    if (!formData.vehicleNo.trim()) newErrors.vehicleNo = "Vehicle No is required"
    if (!formData.dateTime) newErrors.dateTime = "Date & Time is required"
    if (!formData.supplierName.trim()) newErrors.supplierName = "Supplier Name is required"
    if (!formData.material.trim()) newErrors.material = "Material is required"
    if (!formData.uom.trim()) newErrors.uom = "UOM is required"
    if (!formData.receivedBy.trim()) newErrors.receivedBy = "Received By is required"
    if (!formData.supplierBillQty.trim()) newErrors.supplierBillQty = "Supplier Bill Qty is required"
    if (!formData.supplierBillRate.trim()) newErrors.supplierBillRate = "Supplier Bill Rate is required"
    if (!formData.receivedAmount.trim()) newErrors.receivedAmount = "Received Amount is required"

    return newErrors
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    const formErrors = validateForm()

    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors)
      return
    }

    const newDetail = {
      ...formData,
      id: Date.now(),
      sno: supplierDetails.length + 1,
      supplierBillFile: supplierBillFile ? supplierBillFile.name : null,
      createdAt: new Date().toISOString(),
    }

    const updatedDetails = [...supplierDetails, newDetail]
    saveSupplierDetails(updatedDetails)
    resetForm()
  }

  const resetForm = () => {
    const now = new Date()
    setFormData({
      poNumber: "",
      poBalanceQty: "",
      poRate: "",
      inwardNo: "",
      vehicleNo: "",
      dateTime: now.toISOString().slice(0, 16),
      supplierName: "",
      material: "",
      uom: "",
      receivedBy: "",
      supplierBillQty: "",
      supplierBillRate: "",
      receivedAmount: "",
      amount: "",
      difference: "",
      status: "Pending",
    })
    setErrors({})
    setSupplierBillFile(null)
    setShowForm(false)
  }

  const handleApproval = (id, action) => {
    const updatedDetails = supplierDetails.map((detail) =>
      detail.id === id ? { ...detail, status: action, updatedAt: new Date().toISOString() } : detail,
    )
    saveSupplierDetails(updatedDetails)
  }

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this supplier detail?")) {
      const updatedDetails = supplierDetails.filter((detail) => detail.id !== id)
      saveSupplierDetails(updatedDetails)
    }
  }

  const handleView = (detail) => {
    alert(`
      Supplier Detail Information:

      PO Number: ${detail.poNumber}
      Supplier: ${detail.supplierName}
      Material: ${detail.material}
      Quantity: ${detail.supplierBillQty} ${detail.uom}
      Rate: ₹${detail.supplierBillRate}
      Amount: ₹${detail.amount}
      Received By: ${detail.receivedBy}
      Status: ${detail.status}
    `)
  }

  const loadSuppliersFromExcel = () => {
    const input = document.createElement("input")
    input.type = "file"
    input.accept = ".xlsx,.xls"
    input.onchange = (e) => {
      const file = e.target.files[0]
      if (
        file &&
        (file.name === "supplier_details.xlsx" ||
          file.type.includes("spreadsheet") ||
          file.name.endsWith(".xlsx") ||
          file.name.endsWith(".xls"))
      ) {
        const reader = new FileReader()
        reader.onload = (event) => {
          try {
            const data = new Uint8Array(event.target.result)
            const workbook = XLSX.read(data, { type: "array" })
            const sheetName = workbook.SheetNames[0]
            const worksheet = workbook.Sheets[sheetName]
            const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 })

            if (jsonData.length > 1) {
              const headers = jsonData[0]
              const parsedData = []

              for (let i = 1; i < jsonData.length; i++) {
                const row = jsonData[i]
                if (row && row.length > 0 && row[1]) {
                  // Check if row has data and PO Number exists
                  const record = {
                    id: Date.now() + i,
                    sno: row[0] || i,
                    poNumber: row[1] || "",
                    poBalanceQty: row[2] || "",
                    poRate: row[3] || "",
                    inwardNo: row[4] || "",
                    vehicleNo: row[5] || "",
                    dateTime: row[6]
                      ? new Date(row[6]).toISOString().slice(0, 16)
                      : new Date().toISOString().slice(0, 16),
                    supplierName: row[7] || "",
                    material: row[8] || "",
                    uom: row[9] || "",
                    receivedBy: row[10] || "",
                    supplierBillFile: row[11] === "No file" || !row[11] ? null : row[11],
                    supplierBillQty: row[12] || "",
                    supplierBillRate: row[13] || "",
                    receivedAmount: row[14] || "",
                    amount: row[15] || "",
                    difference: row[16] || "",
                    status: row[17] || "Pending",
                    createdAt: row[18] ? new Date(row[18]).toISOString() : new Date().toISOString(),
                  }
                  parsedData.push(record)
                }
              }

              if (parsedData.length > 0) {
                setSupplierDetails(parsedData)
                localStorage.setItem("supplierDetails", JSON.stringify(parsedData))
                alert(`✅ Successfully loaded ${parsedData.length} supplier records from "${file.name}"!`)
              } else {
                alert("❌ No valid supplier data found in the Excel file.")
              }
            } else {
              alert("❌ The Excel file appears to be empty or has no data rows.")
            }
          } catch (error) {
            alert("❌ Error reading Excel file. Please check the file format and try again.")
            console.error("Excel parsing error:", error)
          }
        }
        reader.readAsArrayBuffer(file)
      } else {
        alert("⚠️ Please select the 'supplier_details.xlsx' file or a valid Excel file.")
      }
    }

    // Trigger file selection
    input.click()
  }

  const exportToExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(supplierDetails)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "Supplier Details")
    XLSX.writeFile(workbook, "supplier_details.xlsx")
  }

  const exportToCSV = () => {
    // Define CSV headers
    const headers = [
      "S.No",
      "PO Number",
      "PO Balance Qty",
      "PO Rate",
      "Inward No",
      "Vehicle No",
      "Date & Time",
      "Supplier Name",
      "Materials/Item",
      "UOM",
      "Received By",
      "Supplier Bill File",
      "Bill Qty",
      "Bill Rate",
      "Received Amount",
      "Amount",
      "Difference",
      "Status",
      "Created At",
    ]

    // Convert data to CSV format
    const csvData = supplierDetails.map((detail, index) => [
      index + 1,
      detail.poNumber,
      detail.poBalanceQty,
      detail.poRate,
      detail.inwardNo,
      detail.vehicleNo,
      new Date(detail.dateTime).toLocaleString(),
      detail.supplierName,
      detail.material,
      detail.uom,
      detail.receivedBy,
      detail.supplierBillFile || "No file",
      detail.supplierBillQty,
      detail.supplierBillRate,
      detail.receivedAmount,
      detail.amount,
      detail.difference,
      detail.status,
      new Date(detail.createdAt).toLocaleString(),
    ])

    // Combine headers and data
    const csvContent = [headers, ...csvData].map((row) => row.map((field) => `"${field}"`).join(",")).join("\n")

    // Create and download CSV file
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" })
    const link = document.createElement("a")
    const url = URL.createObjectURL(blob)
    link.setAttribute("href", url)
    link.setAttribute("download", "supplier_details.csv")
    link.style.visibility = "hidden"
    document.body.appendChild(link)
    link.click()
    document.body.removeChild(link)
  }

  return (
    <div className="supplier-detail">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">📄</div>
          <h2>Supplier Details Management</h2>
        </div>
        <div className="header-actions">
          <button className="btn btn-csv" onClick={loadSuppliersFromExcel}>
            📋 Suppliers
          </button>
          <button className="btn btn-export-csv" onClick={exportToCSV}>
            📄 Export CSV
          </button>
          <button className="btn btn-export" onClick={exportToExcel}>
            📊 Export Excel
          </button>
          <button className="btn btn-add" onClick={() => setShowForm(true)}>
            ➕ Add Supplier Detail
          </button>
        </div>
      </div>

      {showForm && (
        <div className="modal-overlay">
          <div className="modal large-modal">
            <div className="modal-header">
              <h3>Add Supplier Detail</h3>
              <button className="close-btn" onClick={resetForm}>
                ✕
              </button>
            </div>

            <form onSubmit={handleSubmit} className="supplier-form">
              <div className="form-grid">
                <div className="form-group">
                  <label>PO Number *</label>
                  <input
                    type="text"
                    value={formData.poNumber}
                    onChange={(e) => setFormData({ ...formData, poNumber: e.target.value })}
                    className={errors.poNumber ? "error" : ""}
                    placeholder="Enter PO Number"
                  />
                  {errors.poNumber && <span className="error-text">{errors.poNumber}</span>}
                </div>

                <div className="form-group">
                  <label>PO Balance Qty *</label>
                  <input
                    type="number"
                    step="0.01"
                    value={formData.poBalanceQty}
                    onChange={(e) => setFormData({ ...formData, poBalanceQty: e.target.value })}
                    className={errors.poBalanceQty ? "error" : ""}
                    placeholder="Enter balance quantity"
                  />
                  {errors.poBalanceQty && <span className="error-text">{errors.poBalanceQty}</span>}
                </div>

                <div className="form-group">
                  <label>PO Rate (₹) *</label>
                  <input
                    type="number"
                    step="0.01"
                    value={formData.poRate}
                    onChange={(e) => setFormData({ ...formData, poRate: e.target.value })}
                    className={errors.poRate ? "error" : ""}
                    placeholder="Enter PO rate"
                  />
                  {errors.poRate && <span className="error-text">{errors.poRate}</span>}
                </div>

                <div className="form-group">
                  <label>Inward No *</label>
                  <input
                    type="text"
                    value={formData.inwardNo}
                    onChange={(e) => setFormData({ ...formData, inwardNo: e.target.value })}
                    className={errors.inwardNo ? "error" : ""}
                    placeholder="Enter inward number"
                  />
                  {errors.inwardNo && <span className="error-text">{errors.inwardNo}</span>}
                </div>

                <div className="form-group">
                  <label>Vehicle No *</label>
                  <input
                    type="text"
                    value={formData.vehicleNo}
                    onChange={(e) => setFormData({ ...formData, vehicleNo: e.target.value.toUpperCase() })}
                    className={errors.vehicleNo ? "error" : ""}
                    placeholder="Enter vehicle number"
                  />
                  {errors.vehicleNo && <span className="error-text">{errors.vehicleNo}</span>}
                </div>

                <div className="form-group">
                  <label>Date & Time *</label>
                  <input
                    type="datetime-local"
                    value={formData.dateTime}
                    onChange={(e) => setFormData({ ...formData, dateTime: e.target.value })}
                    className={errors.dateTime ? "error" : ""}
                  />
                  {errors.dateTime && <span className="error-text">{errors.dateTime}</span>}
                </div>

                <div className="form-group">
                  <label>Supplier Name *</label>
                  <input
                    type="text"
                    value={formData.supplierName}
                    onChange={(e) => setFormData({ ...formData, supplierName: e.target.value })}
                    className={errors.supplierName ? "error" : ""}
                    placeholder="Enter supplier name"
                  />
                  {errors.supplierName && <span className="error-text">{errors.supplierName}</span>}
                </div>

                <div className="form-group">
                  <label>Materials / Item *</label>
                  <input
                    type="text"
                    value={formData.material}
                    onChange={(e) => setFormData({ ...formData, material: e.target.value })}
                    className={errors.material ? "error" : ""}
                    placeholder="Enter material/item type"
                  />
                  {errors.material && <span className="error-text">{errors.material}</span>}
                </div>

                <div className="form-group">
                  <label>UOM (Units of Measurement) *</label>
                  <select
                    value={formData.uom}
                    onChange={(e) => setFormData({ ...formData, uom: e.target.value })}
                    className={errors.uom ? "error" : ""}
                  >
                    <option value="">Select UOM</option>
                    <option value="Tons">Tons</option>
                    <option value="Kg">Kg</option>
                    <option value="Pieces">Pieces</option>
                    <option value="Thousands">Thousands</option>
                    <option value="Cubic Meter">Cubic Meter</option>
                    <option value="Square Meter">Square Meter</option>
                    <option value="Liters">Liters</option>
                    <option value="Bags">Bags</option>
                  </select>
                  {errors.uom && <span className="error-text">{errors.uom}</span>}
                </div>

                <div className="form-group">
                  <label>Received By *</label>
                  <input
                    type="text"
                    value={formData.receivedBy}
                    onChange={(e) => setFormData({ ...formData, receivedBy: e.target.value })}
                    className={errors.receivedBy ? "error" : ""}
                    placeholder="Enter receiver name"
                  />
                  {errors.receivedBy && <span className="error-text">{errors.receivedBy}</span>}
                </div>

                <div className="form-group">
                  <label>Upload Supplier Bill</label>
                  <input
                    type="file"
                    accept=".pdf,.jpg,.jpeg,.png,.gif"
                    onChange={handleSupplierBillUpload}
                    className="file-input"
                  />
                  {supplierBillFile && <span className="file-name">📄 {supplierBillFile.name}</span>}
                </div>

                <div className="form-group">
                  <label>Supplier Bill Qty *</label>
                  <input
                    type="number"
                    step="0.01"
                    value={formData.supplierBillQty}
                    onChange={(e) => setFormData({ ...formData, supplierBillQty: e.target.value })}
                    className={errors.supplierBillQty ? "error" : ""}
                    placeholder="Enter bill quantity"
                  />
                  {errors.supplierBillQty && <span className="error-text">{errors.supplierBillQty}</span>}
                </div>

                <div className="form-group">
                  <label>Supplier Bill Rate (₹) *</label>
                  <input
                    type="number"
                    step="0.01"
                    value={formData.supplierBillRate}
                    onChange={(e) => setFormData({ ...formData, supplierBillRate: e.target.value })}
                    className={errors.supplierBillRate ? "error" : ""}
                    placeholder="Enter bill rate"
                  />
                  {errors.supplierBillRate && <span className="error-text">{errors.supplierBillRate}</span>}
                </div>

                <div className="form-group">
                  <label>Received Amount (₹) *</label>
                  <input
                    type="number"
                    step="0.01"
                    value={formData.receivedAmount}
                    onChange={(e) => setFormData({ ...formData, receivedAmount: e.target.value })}
                    className={errors.receivedAmount ? "error" : ""}
                    placeholder="Enter received amount"
                  />
                  {errors.receivedAmount && <span className="error-text">{errors.receivedAmount}</span>}
                </div>

                <div className="form-group">
                  <label>Amount (₹)</label>
                  <input type="number" step="0.01" value={formData.amount} readOnly className="readonly" />
                </div>

                <div className="form-group">
                  <label>Difference (₹)</label>
                  <input type="number" step="0.01" value={formData.difference} readOnly className="readonly" />
                </div>
              </div>

              <div className="form-actions">
                <button type="button" className="btn btn-cancel" onClick={resetForm}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-save">
                  Save Detail
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="details-table supplier-details-wide">
        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>PO Number</th>
              <th>PO Balance Qty</th>
              <th>PO Rate</th>
              <th>Inward No</th>
              <th>Vehicle No</th>
              <th>Date & Time</th>
              <th>Supplier Name</th>
              <th>Materials/Item</th>
              <th>UOM</th>
              <th>Received By</th>
              <th>Supplier Bill</th>
              <th>Bill Qty</th>
              <th>Bill Rate</th>
              <th>Received Amount</th>
              <th>Amount</th>
              <th>Difference</th>
              <th>Approval/Reject</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {supplierDetails.map((detail, index) => (
              <tr key={detail.id}>
                <td>{index + 1}</td>
                <td>{detail.poNumber}</td>
                <td>{detail.poBalanceQty}</td>
                <td>₹{detail.poRate}</td>
                <td>{detail.inwardNo}</td>
                <td>{detail.vehicleNo}</td>
                <td>{new Date(detail.dateTime).toLocaleString()}</td>
                <td>{detail.supplierName}</td>
                <td>{detail.material}</td>
                <td>{detail.uom}</td>
                <td>{detail.receivedBy}</td>
                <td>
                  <div className="supplier-bill-upload">
                    {detail.supplierBillFile ? (
                      <div className="file-uploaded">
                        <span className="file-icon">📄</span>
                        <span className="file-name-display">{detail.supplierBillFile}</span>
                        <button
                          className="btn btn-upload-new"
                          onClick={() => handleFileUpload(detail.id)}
                          title="Upload New File"
                        >
                          🔄
                        </button>
                      </div>
                    ) : (
                      <div className="file-not-uploaded">
                        <span className="no-file-text">No file</span>
                        <button
                          className="btn btn-upload"
                          onClick={() => handleFileUpload(detail.id)}
                          title="Choose File & Upload"
                        >
                          📁 Upload
                        </button>
                      </div>
                    )}
                  </div>
                </td>
                <td>{detail.supplierBillQty}</td>
                <td>₹{detail.supplierBillRate}</td>
                <td>₹{detail.receivedAmount}</td>
                <td>₹{detail.amount}</td>
                <td className={Number.parseFloat(detail.difference) >= 0 ? "positive" : "negative"}>
                  ₹{detail.difference}
                </td>
                <td>
                  <span className={`status ${detail.status.toLowerCase()}`}>{detail.status}</span>
                </td>
                <td>
                  <div className="action-buttons">
                    <button className="btn btn-view" onClick={() => handleView(detail)} title="View Details">
                      👁️
                    </button>
                    {detail.status === "Pending" && (
                      <>
                        <button
                          className="btn btn-approve"
                          onClick={() => handleApproval(detail.id, "Approved")}
                          title="Accept"
                        >
                          ✅
                        </button>
                        <button
                          className="btn btn-reject"
                          onClick={() => handleApproval(detail.id, "Rejected")}
                          title="Reject"
                        >
                          ❌
                        </button>
                      </>
                    )}
                    <button className="btn btn-delete" onClick={() => handleDelete(detail.id)} title="Delete">
                      🗑️
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {supplierDetails.length === 0 && (
          <div className="no-data">
            <p>No supplier details found</p>
          </div>
        )}
      </div>
    </div>
  )
}

export default SupplierDetail
