"use client"

import { useState, useEffect } from "react"
import * as XLSX from "xlsx"

const Logs = () => {
  const [logs, setLogs] = useState([])
  const [filteredLogs, setFilteredLogs] = useState([])
  const [branches, setBranches] = useState([])
  const [filters, setFilters] = useState({
    dateFrom: "",
    dateTo: "",
    branch: "All",
    user: "",
    module: "All",
    action: "All",
  })

  const modules = [
    "Employee Management",
    "Branch Management",
    "Project Management",
    "Role Management",
    "Vehicle Entry",
    "Supplier Details",
    "PO Details",
    "GRN Management",
    "System",
  ]

  const actions = ["Add", "Update", "Delete", "Inaction", "Login", "Logout", "View", "Export"]

  useEffect(() => {
    fetchLogs()
    fetchBranches()
  }, [])

  const fetchLogs = async () => {
    try {
      const response = await fetch("http://localhost:5000/logs")
      const data = await response.json()
      setLogs(data)
      setFilteredLogs(data)
    } catch (error) {
      console.error("Error fetching logs:", error)
    }
  }

  const fetchBranches = async () => {
    try {
      const response = await fetch("http://localhost:5000/branches")
      const data = await response.json()
      setBranches(data)
    } catch (error) {
      console.error("Error fetching branches:", error)
    }
  }

  useEffect(() => {
    let filtered = logs

    // Filter by date range
    if (filters.dateFrom) {
      filtered = filtered.filter((log) => new Date(log.timestamp) >= new Date(filters.dateFrom))
    }
    if (filters.dateTo) {
      filtered = filtered.filter((log) => new Date(log.timestamp) <= new Date(filters.dateTo))
    }

    // Filter by branch
    if (filters.branch !== "All") {
      filtered = filtered.filter((log) => log.branch_name === filters.branch)
    }

    // Filter by user
    if (filters.user) {
      filtered = filtered.filter((log) => log.action_by.toLowerCase().includes(filters.user.toLowerCase()))
    }

    // Filter by module
    if (filters.module !== "All") {
      filtered = filtered.filter((log) => log.module_name === filters.module)
    }

    // Filter by action
    if (filters.action !== "All") {
      filtered = filtered.filter((log) => log.action_performed === filters.action)
    }

    setFilteredLogs(filtered)
  }, [filters, logs])

  const handleFilterChange = (field, value) => {
    setFilters((prev) => ({
      ...prev,
      [field]: value,
    }))
  }

  const clearFilters = () => {
    setFilters({
      dateFrom: "",
      dateTo: "",
      branch: "All",
      user: "",
      module: "All",
      action: "All",
    })
  }

  const exportToExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(filteredLogs)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "System Logs")
    XLSX.writeFile(workbook, "system_logs.xlsx")
  }

  const formatDateTime = (timestamp) => {
    return new Date(timestamp).toLocaleString("en-IN", {
      year: "numeric",
      month: "short",
      day: "2-digit",
      hour: "2-digit",
      minute: "2-digit",
      second: "2-digit",
    })
  }

  const getActionIcon = (action) => {
    switch (action) {
      case "Add":
        return "➕"
      case "Update":
        return "✏️"
      case "Delete":
        return "🗑️"
      case "Login":
        return "🔐"
      case "Logout":
        return "🚪"
      case "View":
        return "👁️"
      case "Export":
        return "📊"
      default:
        return "📝"
    }
  }

  const getActionColor = (action) => {
    switch (action) {
      case "Add":
        return "success"
      case "Update":
        return "warning"
      case "Delete":
        return "danger"
      case "Login":
        return "info"
      case "Logout":
        return "secondary"
      default:
        return "primary"
    }
  }

  return (
    <div className="logs-management">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">📋</div>
          <h2>System Logs</h2>
        </div>
        <div className="header-actions">
          <button className="btn btn-export" onClick={exportToExcel}>
            📊 Export Excel
          </button>
          <button className="btn btn-clear" onClick={clearFilters}>
            🔄 Clear Filters
          </button>
        </div>
      </div>

      <div className="filters-section">
        <div className="filters-grid">
          <div className="filter-group">
            <label>From Date</label>
            <input
              type="date"
              value={filters.dateFrom}
              onChange={(e) => handleFilterChange("dateFrom", e.target.value)}
            />
          </div>

          <div className="filter-group">
            <label>To Date</label>
            <input type="date" value={filters.dateTo} onChange={(e) => handleFilterChange("dateTo", e.target.value)} />
          </div>

          <div className="filter-group">
            <label>Branch</label>
            <select value={filters.branch} onChange={(e) => handleFilterChange("branch", e.target.value)}>
              <option value="All">All Branches</option>
              {branches.map((branch) => (
                <option key={branch.id} value={branch.branch_name}>
                  {branch.branch_name}
                </option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>User</label>
            <input
              type="text"
              placeholder="Search by user..."
              value={filters.user}
              onChange={(e) => handleFilterChange("user", e.target.value)}
            />
          </div>

          <div className="filter-group">
            <label>Module</label>
            <select value={filters.module} onChange={(e) => handleFilterChange("module", e.target.value)}>
              <option value="All">All Modules</option>
              {modules.map((module) => (
                <option key={module} value={module}>
                  {module}
                </option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>Action</label>
            <select value={filters.action} onChange={(e) => handleFilterChange("action", e.target.value)}>
              <option value="All">All Actions</option>
              {actions.map((action) => (
                <option key={action} value={action}>
                  {action}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      <div className="logs-stats">
        <div className="stat-item">
          <span className="stat-label">Total Logs:</span>
          <span className="stat-value">{filteredLogs.length}</span>
        </div>
        <div className="stat-item">
          <span className="stat-label">Date Range:</span>
          <span className="stat-value">
            {filters.dateFrom || "All"} - {filters.dateTo || "All"}
          </span>
        </div>
      </div>

      <div className="logs-table">
        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>Date & Time</th>
              <th>Branch</th>
              <th>Module</th>
              <th>Action</th>
              <th>User</th>
              <th>Details</th>
              <th>IP Address</th>
            </tr>
          </thead>
          <tbody>
            {filteredLogs.map((log, index) => (
              <tr key={log.id}>
                <td>{index + 1}</td>
                <td className="datetime-cell">{formatDateTime(log.timestamp)}</td>
                <td>{log.branch_name}</td>
                <td>
                  <span className="module-badge">{log.module_name}</span>
                </td>
                <td>
                  <span className={`action-badge ${getActionColor(log.action_performed)}`}>
                    {getActionIcon(log.action_performed)} {log.action_performed}
                  </span>
                </td>
                <td className="user-cell">{log.action_by}</td>
                <td className="details-cell">{log.action_on}</td>
                <td className="ip-cell">{log.ip_address}</td>
              </tr>
            ))}
          </tbody>
        </table>

        {filteredLogs.length === 0 && (
          <div className="no-data">
            <div className="no-data-icon">📋</div>
            <p>No logs found matching the selected filters</p>
          </div>
        )}
      </div>
    </div>
  )
}

export default Logs
