"use client"

import { useState, useEffect } from "react"
import * as XLSX from "xlsx"

const UpdatedGRN = () => {
  const [grnData, setGrnData] = useState([])
  const [filteredData, setFilteredData] = useState([])
  const [searchTerm, setSearchTerm] = useState("")
  const [selectedStatus, setSelectedStatus] = useState("")
  const [selectedSupplier, setSelectedSupplier] = useState("")
  const [excelData, setExcelData] = useState([])
  const [showExcelTable, setShowExcelTable] = useState(false)

  useEffect(() => {
    fetchGRNData()
    loadExcelDataFromStorage()
  }, [])

  useEffect(() => {
    filterData()
  }, [grnData, searchTerm, selectedStatus, selectedSupplier])

  const loadExcelDataFromStorage = () => {
    try {
      const savedData = localStorage.getItem("grnExcelData")
      if (savedData) {
        const parsedData = JSON.parse(savedData)
        setExcelData(parsedData)
        setShowExcelTable(true)
      }
    } catch (error) {
      console.error("Error loading Excel data from storage:", error)
    }
  }

  const saveExcelDataToStorage = (data) => {
    try {
      localStorage.setItem("grnExcelData", JSON.stringify(data))
    } catch (error) {
      console.error("Error saving Excel data to storage:", error)
    }
  }

  const fetchGRNData = async () => {
    try {
      const response = await fetch("http://localhost:5000/grn")
      if (response.ok) {
        const data = await response.json()
        setGrnData(data)
      }
    } catch (error) {
      console.error("Error fetching GRN data:", error)
    }
  }

  const filterData = () => {
    let filtered = grnData

    if (searchTerm) {
      filtered = filtered.filter(
        (item) =>
          item.grn_number?.toLowerCase().includes(searchTerm.toLowerCase()) ||
          item.supplier_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
          item.po_number?.toLowerCase().includes(searchTerm.toLowerCase()) ||
          item.item_description?.toLowerCase().includes(searchTerm.toLowerCase()),
      )
    }

    if (selectedStatus) {
      filtered = filtered.filter((item) => item.status === selectedStatus)
    }

    if (selectedSupplier) {
      filtered = filtered.filter((item) => item.supplier_name === selectedSupplier)
    }

    setFilteredData(filtered)
  }

  const handleFileUpload = (event) => {
    const file = event.target.files[0]
    if (file) {
      const reader = new FileReader()
      reader.onload = (e) => {
        try {
          const data = new Uint8Array(e.target.result)
          const workbook = XLSX.read(data, { type: "array" })
          const sheetName = workbook.SheetNames[0]
          const worksheet = workbook.Sheets[sheetName]
          const jsonData = XLSX.utils.sheet_to_json(worksheet)

          setExcelData(jsonData)
          setShowExcelTable(true)
          saveExcelDataToStorage(jsonData)
          alert("Excel file uploaded successfully!")
        } catch (error) {
          console.error("Error reading Excel file:", error)
          alert("Error reading Excel file")
        }
      }
      reader.readAsArrayBuffer(file)
    }
  }

  const exportToExcel = () => {
    const dataToExport = filteredData.length > 0 ? filteredData : grnData
    const worksheet = XLSX.utils.json_to_sheet(dataToExport)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "GRN Data")
    XLSX.writeFile(workbook, "grn_data.xlsx")
  }

  const clearExcelData = () => {
    setExcelData([])
    setShowExcelTable(false)
    localStorage.removeItem("grnExcelData")
    alert("Excel data cleared!")
  }

  const uniqueStatuses = [...new Set(grnData.map((item) => item.status).filter(Boolean))]
  const uniqueSuppliers = [...new Set(grnData.map((item) => item.supplier_name).filter(Boolean))]

  return (
    <div className="updated-grn">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">üì¶</div>
          <h2>Goods Receipt Note (GRN)</h2>
        </div>
        <div className="header-actions">
          <input
            type="text"
            placeholder="Search GRN..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <select
            value={selectedSupplier}
            onChange={(e) => setSelectedSupplier(e.target.value)}
            className="filter-select"
          >
            <option value="">All Suppliers</option>
            {uniqueSuppliers.map((supplier, index) => (
              <option key={index} value={supplier}>
                {supplier}
              </option>
            ))}
          </select>
          <select value={selectedStatus} onChange={(e) => setSelectedStatus(e.target.value)} className="filter-select">
            <option value="">All Status</option>
            {uniqueStatuses.map((status, index) => (
              <option key={index} value={status}>
                {status}
              </option>
            ))}
          </select>
          <input
            type="file"
            accept=".xlsx,.xls"
            onChange={handleFileUpload}
            style={{ display: "none" }}
            id="grn-excel-upload"
          />
          <label htmlFor="grn-excel-upload" className="btn btn-upload">
            üìÅ Upload Excel
          </label>
          <button className="btn btn-export" onClick={exportToExcel}>
            üìä Export Excel
          </button>
          {showExcelTable && (
            <button className="btn btn-clear" onClick={clearExcelData}>
              üóëÔ∏è Clear Excel
            </button>
          )}
        </div>
      </div>

      <div className="grn-table">
        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>GRN Number</th>
              <th>PO Number</th>
              <th>Supplier Name</th>
              <th>Item Description</th>
              <th>Ordered Qty</th>
              <th>Received Qty</th>
              <th>Unit Price</th>
              <th>Total Value</th>
              <th>Status</th>
              <th>Received Date</th>
              <th>Quality Check</th>
            </tr>
          </thead>
          <tbody>
            {filteredData.map((item, index) => (
              <tr key={item.id || index}>
                <td>{index + 1}</td>
                <td>{item.grn_number}</td>
                <td>{item.po_number}</td>
                <td>{item.supplier_name}</td>
                <td>{item.item_description}</td>
                <td>{item.ordered_quantity}</td>
                <td>{item.received_quantity}</td>
                <td>‚Çπ{item.unit_price}</td>
                <td>‚Çπ{item.total_value}</td>
                <td>
                  <span className={`status ${item.status?.toLowerCase()}`}>{item.status}</span>
                </td>
                <td>{item.received_date ? new Date(item.received_date).toLocaleDateString() : ""}</td>
                <td>
                  <span className={`quality-check ${item.quality_check?.toLowerCase()}`}>{item.quality_check}</span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {filteredData.length === 0 && (
          <div className="no-data">
            <p>No GRN data found</p>
          </div>
        )}
      </div>

      {showExcelTable && excelData.length > 0 && (
        <div className="excel-data-section">
          <h3>Uploaded Excel Data</h3>
          <div style={{ maxHeight: "400px", overflowY: "auto", border: "1px solid #ddd", borderRadius: "4px" }}>
            <table style={{ width: "100%", borderCollapse: "collapse" }}>
              <thead style={{ position: "sticky", top: 0, backgroundColor: "#f8f9fa", zIndex: 1 }}>
                <tr>
                  {excelData.length > 0 &&
                    Object.keys(excelData[0]).map((key, index) => (
                      <th
                        key={index}
                        style={{
                          padding: "8px 12px",
                          borderBottom: "2px solid #dee2e6",
                          backgroundColor: "#f8f9fa",
                          fontWeight: "600",
                          textAlign: "left",
                          fontSize: "14px",
                        }}
                      >
                        {key}
                      </th>
                    ))}
                </tr>
              </thead>
              <tbody>
                {excelData.map((row, index) => (
                  <tr key={index} style={{ borderBottom: "1px solid #dee2e6" }}>
                    {Object.values(row).map((value, cellIndex) => (
                      <td
                        key={cellIndex}
                        style={{
                          padding: "8px 12px",
                          fontSize: "13px",
                          lineHeight: "1.2",
                        }}
                      >
                        {value}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  )
}

export default UpdatedGRN
