"use client"

import { useState, useEffect } from "react"
import * as XLSX from "xlsx"

const SupplierDetail = ({ user }) => {
  const [suppliers, setSuppliers] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [editingSupplier, setEditingSupplier] = useState(null)
  const [searchTerm, setSearchTerm] = useState("")
  const [formData, setFormData] = useState({
    supplierName: "",
    contactPerson: "",
    phoneNumber: "",
    emailId: "",
    address: "",
    gstNumber: "",
    panNumber: "",
    bankDetails: "",
    materialType: "",
    receivedBy: user?.username || "",
    status: "Active",
  })
  const [errors, setErrors] = useState({})
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    fetchSuppliers()
    // Set received by to current user's username
    if (user?.username) {
      setFormData((prev) => ({ ...prev, receivedBy: user.username }))
    }
  }, [user])

  const fetchSuppliers = async () => {
    try {
      const response = await fetch("http://localhost:5000/suppliers")
      if (response.ok) {
        const data = await response.json()
        setSuppliers(data)
      }
    } catch (error) {
      console.error("Error fetching suppliers:", error)
    }
  }

  const validateForm = () => {
    const newErrors = {}

    if (!formData.supplierName.trim()) newErrors.supplierName = "Supplier name is required"
    if (!formData.contactPerson.trim()) newErrors.contactPerson = "Contact person is required"
    if (!formData.phoneNumber.trim()) newErrors.phoneNumber = "Phone number is required"
    if (!formData.emailId.trim()) newErrors.emailId = "Email is required"
    if (!formData.address.trim()) newErrors.address = "Address is required"
    if (!formData.materialType.trim()) newErrors.materialType = "Material type is required"

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
    if (formData.emailId && !emailRegex.test(formData.emailId)) {
      newErrors.emailId = "Invalid email format"
    }

    const phoneRegex = /^[0-9]{10}$/
    if (formData.phoneNumber && !phoneRegex.test(formData.phoneNumber)) {
      newErrors.phoneNumber = "Phone number must be 10 digits"
    }

    return newErrors
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    const formErrors = validateForm()

    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors)
      return
    }

    setLoading(true)

    try {
      const supplierData = {
        ...formData,
        receivedBy: user?.username || formData.receivedBy,
      }

      let response
      if (editingSupplier) {
        response = await fetch(`http://localhost:5000/suppliers/${editingSupplier.id}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(supplierData),
        })
      } else {
        response = await fetch("http://localhost:5000/suppliers", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(supplierData),
        })
      }

      if (response.ok) {
        alert(editingSupplier ? "Supplier updated successfully!" : "Supplier added successfully!")
        resetForm()
        fetchSuppliers()
      } else {
        const errorData = await response.json()
        alert(errorData.error || "Failed to save supplier")
      }
    } catch (error) {
      console.error("Error saving supplier:", error)
      alert("Error saving supplier")
    } finally {
      setLoading(false)
    }
  }

  const resetForm = () => {
    setFormData({
      supplierName: "",
      contactPerson: "",
      phoneNumber: "",
      emailId: "",
      address: "",
      gstNumber: "",
      panNumber: "",
      bankDetails: "",
      materialType: "",
      receivedBy: user?.username || "",
      status: "Active",
    })
    setErrors({})
    setShowForm(false)
    setEditingSupplier(null)
  }

  const handleEdit = (supplier) => {
    setFormData({
      supplierName: supplier.supplier_name || "",
      contactPerson: supplier.contact_person || "",
      phoneNumber: supplier.phone_number || "",
      emailId: supplier.email_id || "",
      address: supplier.address || "",
      gstNumber: supplier.gst_number || "",
      panNumber: supplier.pan_number || "",
      bankDetails: supplier.bank_details || "",
      materialType: supplier.material_type || "",
      receivedBy: supplier.received_by || user?.username || "",
      status: supplier.status || "Active",
    })
    setEditingSupplier(supplier)
    setShowForm(true)
  }

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this supplier?")) {
      try {
        const response = await fetch(`http://localhost:5000/suppliers/${id}`, {
          method: "DELETE",
        })

        if (response.ok) {
          alert("Supplier deleted successfully!")
          fetchSuppliers()
        } else {
          alert("Failed to delete supplier")
        }
      } catch (error) {
        console.error("Error deleting supplier:", error)
        alert("Error deleting supplier")
      }
    }
  }

  const exportToExcel = () => {
    const exportData = suppliers.map((supplier, index) => ({
      "S.No": index + 1,
      "Supplier Name": supplier.supplier_name,
      "Contact Person": supplier.contact_person,
      "Phone Number": supplier.phone_number,
      Email: supplier.email_id,
      "Material Type": supplier.material_type,
      "GST Number": supplier.gst_number,
      "PAN Number": supplier.pan_number,
      "Received By": supplier.received_by,
      Status: supplier.status,
      "Created Date": new Date(supplier.created_at).toLocaleDateString(),
    }))

    const worksheet = XLSX.utils.json_to_sheet(exportData)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "Suppliers")
    XLSX.writeFile(workbook, "suppliers.xlsx")
  }

  const filteredSuppliers = suppliers.filter((supplier) => {
    const term = searchTerm.toLowerCase()
    return (
      (supplier.supplier_name || "").toLowerCase().includes(term) ||
      (supplier.contact_person || "").toLowerCase().includes(term) ||
      (supplier.material_type || "").toLowerCase().includes(term)
    )
  })

  return (
    <div className="supplier-detail">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">ðŸ“„</div>
          <h2>Supplier Details</h2>
        </div>
        <div className="header-actions">
          <input
            type="text"
            placeholder="Search suppliers..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <button className="btn btn-export" onClick={exportToExcel} disabled={suppliers.length === 0}>
            ðŸ“Š Export Excel
          </button>
          <button className="btn btn-add" onClick={() => setShowForm(true)}>
            âž• Add Supplier
          </button>
        </div>
      </div>

      {showForm && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>{editingSupplier ? "Edit Supplier" : "Add New Supplier"}</h3>
              <button className="close-btn" onClick={resetForm}>
                âœ•
              </button>
            </div>

            <form onSubmit={handleSubmit} className="supplier-form">
              <div className="form-grid">
                <div className="form-group">
                  <label>Supplier Name *</label>
                  <input
                    type="text"
                    value={formData.supplierName}
                    onChange={(e) => setFormData({ ...formData, supplierName: e.target.value })}
                    className={errors.supplierName ? "error" : ""}
                    placeholder="Enter supplier name"
                  />
                  {errors.supplierName && <span className="error-text">{errors.supplierName}</span>}
                </div>

                <div className="form-group">
                  <label>Contact Person *</label>
                  <input
                    type="text"
                    value={formData.contactPerson}
                    onChange={(e) => setFormData({ ...formData, contactPerson: e.target.value })}
                    className={errors.contactPerson ? "error" : ""}
                    placeholder="Enter contact person name"
                  />
                  {errors.contactPerson && <span className="error-text">{errors.contactPerson}</span>}
                </div>

                <div className="form-group">
                  <label>Phone Number *</label>
                  <input
                    type="tel"
                    value={formData.phoneNumber}
                    onChange={(e) => setFormData({ ...formData, phoneNumber: e.target.value })}
                    className={errors.phoneNumber ? "error" : ""}
                    placeholder="Enter phone number"
                    maxLength="10"
                  />
                  {errors.phoneNumber && <span className="error-text">{errors.phoneNumber}</span>}
                </div>

                <div className="form-group">
                  <label>Email ID *</label>
                  <input
                    type="email"
                    value={formData.emailId}
                    onChange={(e) => setFormData({ ...formData, emailId: e.target.value })}
                    className={errors.emailId ? "error" : ""}
                    placeholder="Enter email address"
                  />
                  {errors.emailId && <span className="error-text">{errors.emailId}</span>}
                </div>

                <div className="form-group full-width">
                  <label>Address *</label>
                  <textarea
                    value={formData.address}
                    onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                    className={errors.address ? "error" : ""}
                    placeholder="Enter complete address"
                    rows="3"
                  />
                  {errors.address && <span className="error-text">{errors.address}</span>}
                </div>

                <div className="form-group">
                  <label>GST Number</label>
                  <input
                    type="text"
                    value={formData.gstNumber}
                    onChange={(e) => setFormData({ ...formData, gstNumber: e.target.value.toUpperCase() })}
                    placeholder="Enter GST number"
                    maxLength="15"
                  />
                </div>

                <div className="form-group">
                  <label>PAN Number</label>
                  <input
                    type="text"
                    value={formData.panNumber}
                    onChange={(e) => setFormData({ ...formData, panNumber: e.target.value.toUpperCase() })}
                    placeholder="Enter PAN number"
                    maxLength="10"
                  />
                </div>

                <div className="form-group full-width">
                  <label>Bank Details</label>
                  <textarea
                    value={formData.bankDetails}
                    onChange={(e) => setFormData({ ...formData, bankDetails: e.target.value })}
                    placeholder="Enter bank account details"
                    rows="2"
                  />
                </div>

                <div className="form-group">
                  <label>Material Type *</label>
                  <input
                    type="text"
                    value={formData.materialType}
                    onChange={(e) => setFormData({ ...formData, materialType: e.target.value })}
                    className={errors.materialType ? "error" : ""}
                    placeholder="Enter material type"
                  />
                  {errors.materialType && <span className="error-text">{errors.materialType}</span>}
                </div>

                <div className="form-group">
                  <label>Received By</label>
                  <input
                    type="text"
                    value={formData.receivedBy}
                    readOnly
                    className="readonly-field"
                    title="Auto-filled with current user"
                  />
                </div>

                <div className="form-group">
                  <label>Status</label>
                  <select
                    value={formData.status}
                    onChange={(e) => setFormData({ ...formData, status: e.target.value })}
                  >
                    <option value="Active">Active</option>
                    <option value="Inactive">Inactive</option>
                  </select>
                </div>
              </div>

              <div className="form-actions">
                <button type="button" className="btn btn-cancel" onClick={resetForm} disabled={loading}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-save" disabled={loading}>
                  {loading ? "Saving..." : editingSupplier ? "Update Supplier" : "Save Supplier"}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="suppliers-table">
        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>Supplier Name</th>
              <th>Contact Person</th>
              <th>Phone</th>
              <th>Email</th>
              <th>Material Type</th>
              <th>Received By</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredSuppliers.map((supplier, index) => (
              <tr key={supplier.id}>
                <td>{index + 1}</td>
                <td>{supplier.supplier_name}</td>
                <td>{supplier.contact_person}</td>
                <td>{supplier.phone_number}</td>
                <td>{supplier.email_id}</td>
                <td>{supplier.material_type}</td>
                <td>{supplier.received_by}</td>
                <td>
                  <span className={`status ${supplier.status.toLowerCase()}`}>{supplier.status}</span>
                </td>
                <td>
                  <div className="action-buttons">
                    <button className="btn btn-edit" onClick={() => handleEdit(supplier)} title="Edit Supplier">
                      âœï¸
                    </button>
                    <button
                      className="btn btn-delete"
                      onClick={() => handleDelete(supplier.id)}
                      title="Delete Supplier"
                    >
                      ðŸ—‘ï¸
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {filteredSuppliers.length === 0 && (
          <div className="no-data">
            <p>No suppliers found</p>
          </div>
        )}
      </div>

      <style jsx>{`
        .readonly-field {
          background-color: #f8f9fa;
          color: #6c757d;
          cursor: not-allowed;
        }
      `}</style>
    </div>
  )
}

export default SupplierDetail
