"use client"

import { useState, useEffect } from "react"
import * as XLSX from "xlsx"

const EmployeeManagement = () => {
  const [employees, setEmployees] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [editingEmployee, setEditingEmployee] = useState(null)
  const [searchTerm, setSearchTerm] = useState("")
  const [formData, setFormData] = useState({
    fullName: "",
    dateOfBirth: "",
    gender: "",
    phoneNumber: "",
    emailId: "",
    address: "",
    aadharNumber: "",
    panNumber: "",
    joiningDate: "",
    designation: "",
    department: "",
    emergencyContact: "",
    status: "Active",
  })
  const [errors, setErrors] = useState({})

  useEffect(() => {
    fetchEmployees()
  }, [])

  const normalizeEmployee = (emp) => ({
    id: emp.id,
    fullName: emp.full_name,
    dateOfBirth: emp.date_of_birth,
    gender: emp.gender,
    phoneNumber: emp.phone_number,
    emailId: emp.email_id,
    address: emp.address,
    aadharNumber: emp.aadhar_number,
    panNumber: emp.pan_number,
    joiningDate: emp.joining_date,
    designation: emp.designation,
    department: emp.department,
    emergencyContact: emp.emergency_contact,
    status: emp.status,
    createdAt: emp.created_at,
    updatedAt: emp.updated_at,
  })

  const fetchEmployees = async () => {
    try {
      const response = await fetch("http://localhost:5000/employees")
      const data = await response.json()
      const normalized = data.map(normalizeEmployee)
      setEmployees(normalized)
    } catch (error) {
      console.error("Error fetching employees:", error)
    }
  }

  const validateForm = () => {
    const newErrors = {}

    if (!formData.fullName.trim()) newErrors.fullName = "Full name is required"
    if (!formData.dateOfBirth) newErrors.dateOfBirth = "Date of birth is required"
    if (!formData.gender) newErrors.gender = "Gender is required"
    if (!formData.phoneNumber.trim()) newErrors.phoneNumber = "Phone number is required"
    if (!formData.emailId.trim()) newErrors.emailId = "Email is required"
    if (!formData.address.trim()) newErrors.address = "Address is required"
    if (!formData.aadharNumber.trim()) newErrors.aadharNumber = "Aadhar number is required"
    if (!formData.panNumber.trim()) newErrors.panNumber = "PAN number is required"
    if (!formData.joiningDate) newErrors.joiningDate = "Joining date is required"
    if (!formData.designation.trim()) newErrors.designation = "Designation is required"
    if (!formData.department.trim()) newErrors.department = "Department is required"
    if (!formData.emergencyContact.trim()) newErrors.emergencyContact = "Emergency contact is required"

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
    if (formData.emailId && !emailRegex.test(formData.emailId)) {
      newErrors.emailId = "Invalid email format"
    }

    const phoneRegex = /^[0-9]{10}$/
    if (formData.phoneNumber && !phoneRegex.test(formData.phoneNumber)) {
      newErrors.phoneNumber = "Phone number must be 10 digits"
    }

    const aadharRegex = /^[0-9]{12}$/
    if (formData.aadharNumber && !aadharRegex.test(formData.aadharNumber)) {
      newErrors.aadharNumber = "Aadhar number must be 12 digits"
    }

    const panRegex = /^[A-Z]{5}[0-9]{4}[A-Z]{1}$/
    if (formData.panNumber && !panRegex.test(formData.panNumber)) {
      newErrors.panNumber = "Invalid PAN format"
    }

    return newErrors
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    const formErrors = validateForm()
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors)
      return
    }

    const newEmployee = {
      ...formData,
      id: editingEmployee ? editingEmployee.id : Date.now(),
      created_at: editingEmployee ? editingEmployee.createdAt : new Date().toISOString(),
      updated_at: new Date().toISOString(),
    }

    try {
      if (editingEmployee) {
        await fetch(`http://localhost:5000/employees/${editingEmployee.id}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(newEmployee),
        })
      } else {
        await fetch("http://localhost:5000/employees", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(newEmployee),
        })
      }
      fetchEmployees()
      resetForm()
    } catch (error) {
      console.error("Error saving employee:", error)
    }
  }

  const resetForm = () => {
    setFormData({
      fullName: "",
      dateOfBirth: "",
      gender: "",
      phoneNumber: "",
      emailId: "",
      address: "",
      aadharNumber: "",
      panNumber: "",
      joiningDate: "",
      designation: "",
      department: "",
      emergencyContact: "",
      status: "Active",
    })
    setErrors({})
    setShowForm(false)
    setEditingEmployee(null)
  }

  const handleEdit = (employee) => {
    setFormData({ ...employee })
    setEditingEmployee(employee)
    setShowForm(true)
  }

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this employee?")) {
      try {
        await fetch(`http://localhost:5000/employees/${id}`, {
          method: "DELETE",
        })
        fetchEmployees()
      } catch (error) {
        console.error("Error deleting employee:", error)
      }
    }
  }

  const handleStatusToggle = async (id) => {
    const employee = employees.find((e) => e.id === id)
    const updatedStatus = employee.status === "Active" ? "Inactive" : "Active"
    try {
      await fetch(`http://localhost:5000/employees/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...employee, status: updatedStatus }),
      })
      fetchEmployees()
    } catch (error) {
      console.error("Error updating status:", error)
    }
  }

  const exportToExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(employees)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "Employees")
    XLSX.writeFile(workbook, "employees.xlsx")
  }

  const filteredEmployees = employees.filter((emp) => {
    const term = searchTerm.toLowerCase()
    return (
      (emp.fullName || "").toLowerCase().includes(term) ||
      (emp.emailId || "").toLowerCase().includes(term) ||
      (emp.designation || "").toLowerCase().includes(term)
    )
  })

  return (
    <div>
      {/* Your UI remains the same, assuming you provided it earlier — no changes needed in JSX */}
      {/* You may paste your UI JSX for modals and tables here as already written. */}
    </div>
  )
}

export default EmployeeManagement
