  const [showForm, setShowForm] = useState(false)
  const [videoFile, setVideoFile] = useState(null)
  const [videoPreview, setVideoPreview] = useState(null)
  const [formData, setFormData] = useState({
    inwardNumber: "",
    vehicleNumber: "",
    supplierName: "",
    materialType: "",
    date: "",
    time: "",
  })
  const [errors, setErrors] = useState({})

  useEffect(() => {
    const savedEntries = localStorage.getItem("vehicleEntries")
    if (savedEntries) {
      setVehicleEntries(JSON.parse(savedEntries))
    }

    // Set current date and time
    const now = new Date()
    setFormData((prev) => ({
      ...prev,
      date: now.toISOString().split("T")[0],
      time: now.toTimeString().split(" ")[0].substring(0, 5),
    }))
  }, [])

  const saveVehicleEntries = (entries) => {
    localStorage.setItem("vehicleEntries", JSON.stringify(entries))
    setVehicleEntries(entries)
  }

  const generateInwardNumber = () => {
    const date = new Date()
    const year = date.getFullYear()
    const month = String(date.getMonth() + 1).padStart(2, "0")
    const day = String(date.getDate()).padStart(2, "0")
    const sequence = String(vehicleEntries.length + 1).padStart(4, "0")
    return `INW${year}${month}${day}${sequence}`
  }

  const handleVideoUpload = (e) => {
    const file = e.target.files[0]
    if (file) {
      setVideoFile(file)
      const videoUrl = URL.createObjectURL(file)
      setVideoPreview(videoUrl)
    }
  }

  const validateForm = () => {
    const newErrors = {}

    if (!formData.inwardNumber.trim()) newErrors.inwardNumber = "Inward number is required"
    if (!formData.vehicleNumber.trim()) newErrors.vehicleNumber = "Vehicle number is required"
    if (!formData.supplierName.trim()) newErrors.supplierName = "Supplier name is required"
    if (!formData.materialType.trim()) newErrors.materialType = "Material type is required"
    if (!formData.date) newErrors.date = "Date is required"
    if (!formData.time) newErrors.time = "Time is required"

    return newErrors
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    const formErrors = validateForm()

    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors)
      return
    }

    const newEntry = {
      ...formData,
      id: Date.now(),
      videoFile: videoFile ? videoFile.name : null,
      createdAt: new Date().toISOString(),
    }

    const updatedEntries = [...vehicleEntries, newEntry]
    saveVehicleEntries(updatedEntries)
    resetForm()
  }

  const resetForm = () => {
    const now = new Date()
    setFormData({
      inwardNumber: "",
      vehicleNumber: "",
      supplierName: "",
      materialType: "",
      date: now.toISOString().split("T")[0],
      time: now.toTimeString().split(" ")[0].substring(0, 5),
    })
    setErrors({})
    setVideoFile(null)
    setVideoPreview(null)
    setShowForm(false)
  }

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this vehicle entry?")) {
      const updatedEntries = vehicleEntries.filter((entry) => entry.id !== id)
      saveVehicleEntries(updatedEntries)
    }
  }

  const exportToExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(vehicleEntries)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "Vehicle Entries")
    XLSX.writeFile(workbook, "vehicle_entries.xlsx")
  }

  return (
    <div className="vehicle-entry">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">🚛</div>
          <h2>Vehicle Entry</h2>
        </div>
        <div className="header-actions">
          <button className="btn btn-export" onClick={exportToExcel}>
            📊 Export Excel
          </button>
          <button
            className="btn btn-add"
            onClick={() => {
              setFormData((prev) => ({
                ...prev,
                inwardNumber: generateInwardNumber(),
              }))
              setShowForm(true)
            }}
          >
            ➕ Add Vehicle Entry
          </button>
        </div>
      </div>

      {showForm && (
        <div className="modal-overlay">
          <div className="modal large-modal">
            <div className="modal-header">
              <h3>Add Vehicle Entry</h3>
              <button className="close-btn" onClick={resetForm}>
                ✕
              </button>
            </div>

            <form onSubmit={handleSubmit} className="vehicle-form">
              <div className="video-upload-section">
                <h4>Upload Video (Live Feed)</h4>
                <div className="video-upload">
                  <input type="file" accept="video/*" onChange={handleVideoUpload} className="video-input" />
                  {videoPreview && (
                    <div className="video-preview">
                      <video controls width="300" height="200">
                        <source src={videoPreview} type="video/mp4" />
                        Your browser does not support the video tag.
                      </video>
                    </div>
                  )}
                </div>
              </div>

              <div className="form-grid">
                <div className="form-group">
                  <label>Inward Number *</label>
                  <input
                    type="text"
                    value={formData.inwardNumber}
                    onChange={(e) => setFormData({ ...formData, inwardNumber: e.target.value })}
                    className={errors.inwardNumber ? "error" : ""}
                    placeholder="Auto-generated"
                    readOnly
                  />
                  {errors.inwardNumber && <span className="error-text">{errors.inwardNumber}</span>}
                </div>

                <div className="form-group">
                  <label>Vehicle Number *</label>
                  <input
                    type="text"
                    value={formData.vehicleNumber}
                    onChange={(e) => setFormData({ ...formData, vehicleNumber: e.target.value.toUpperCase() })}
                    className={errors.vehicleNumber ? "error" : ""}
                    placeholder="Enter vehicle number"
                  />
                  {errors.vehicleNumber && <span className="error-text">{errors.vehicleNumber}</span>}
                </div>

                <div className="form-group">
                  <label>Supplier Name *</label>
                  <input
                    type="text"
                    value={formData.supplierName}
                    onChange={(e) => setFormData({ ...formData, supplierName: e.target.value })}
                    className={errors.supplierName ? "error" : ""}
                    placeholder="Enter supplier name"
                  />
                  {errors.supplierName && <span className="error-text">{errors.supplierName}</span>}
                </div>

                <div className="form-group">
                  <label>Material Type *</label>
                  <select
                    value={formData.materialType}
                    onChange={(e) => setFormData({ ...formData, materialType: e.target.value })}
                    className={errors.materialType ? "error" : ""}
                  >
                    <option value="">Select Material Type</option>
                    <option value="Cement">Cement</option>
                    <option value="Steel">Steel</option>
                    <option value="Sand">Sand</option>
                    <option value="Gravel">Gravel</option>
                    <option value="Bricks">Bricks</option>
                    <option value="Concrete">Concrete</option>
                    <option value="Other">Other</option>
                  </select>
                  {errors.materialType && <span className="error-text">{errors.materialType}</span>}
                </div>

                <div className="form-group">
                  <label>Date *</label>
                  <input
                    type="date"
                    value={formData.date}
                    onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                    className={errors.date ? "error" : ""}
                  />
                  {errors.date && <span className="error-text">{errors.date}</span>}
                </div>

                <div className="form-group">
                  <label>Time *</label>
                  <input
                    type="time"
                    value={formData.time}
                    onChange={(e) => setFormData({ ...formData, time: e.target.value })}
                    className={errors.time ? "error" : ""}
                  />
                  {errors.time && <span className="error-text">{errors.time}</span>}
                </div>
              </div>

              <div className="form-actions">
                <button type="button" className="btn btn-cancel" onClick={resetForm}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-save">
                  Save Entry
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="entries-table">
        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>Inward Number</th>
              <th>Vehicle Number</th>
              <th>Supplier Name</th>
              <th>Material Type</th>
              <th>Date & Time</th>
              <th>Video</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {vehicleEntries.map((entry, index) => (
              <tr key={entry.id}>
                <td>{index + 1}</td>
                <td>{entry.inwardNumber}</td>
                <td>{entry.vehicleNumber}</td>
                <td>{entry.supplierName}</td>
                <td>{entry.materialType}</td>
                <td>
                  {new Date(entry.date).toLocaleDateString()} {entry.time}
                </td>
                <td>
                  {entry.videoFile ? (
                    <span className="video-indicator">📹 {entry.videoFile}</span>
                  ) : (
                    <span className="no-video">No Video</span>
                  )}
                </td>
                <td>
                  <div className="action-buttons">
                    <button className="btn btn-delete" onClick={() => handleDelete(entry.id)} title="Delete Entry">
                      🗑️
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {vehicleEntries.length === 0 && (
          <div className="no-data">
            <p>No vehicle entries found</p>
          </div>
        )}
      </div>
    </div>
  )
}

export default VehicleEntry
