"use client"

import { useState, useEffect } from "react"
import * as XLSX from "xlsx"

const ProjectManagement = () => {
  const [projects, setProjects] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [editingProject, setEditingProject] = useState(null)
  const [formData, setFormData] = useState({
    projectName: "",
    address: "",
    latitude: "",
    longitude: "",
  })
  const [errors, setErrors] = useState({})

  useEffect(() => {
    const savedProjects = localStorage.getItem("projects")
    if (savedProjects) {
      setProjects(JSON.parse(savedProjects))
    }
  }, [])

  const saveProjects = (projectList) => {
    localStorage.setItem("projects", JSON.stringify(projectList))
    setProjects(projectList)
  }

  const validateForm = () => {
    const newErrors = {}

    if (!formData.projectName.trim()) newErrors.projectName = "Project name is required"
    if (!formData.address.trim()) newErrors.address = "Address is required"
    if (!formData.latitude.trim()) newErrors.latitude = "Latitude is required"
    if (!formData.longitude.trim()) newErrors.longitude = "Longitude is required"

    // Validate latitude and longitude
    const lat = Number.parseFloat(formData.latitude)
    const lng = Number.parseFloat(formData.longitude)

    if (isNaN(lat) || lat < -90 || lat > 90) {
      newErrors.latitude = "Latitude must be between -90 and 90"
    }

    if (isNaN(lng) || lng < -180 || lng > 180) {
      newErrors.longitude = "Longitude must be between -180 and 180"
    }

    return newErrors
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    const formErrors = validateForm()

    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors)
      return
    }

    const newProject = {
      ...formData,
      id: editingProject ? editingProject.id : Date.now(),
      createdAt: editingProject ? editingProject.createdAt : new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    }

    let updatedProjects
    if (editingProject) {
      updatedProjects = projects.map((project) => (project.id === editingProject.id ? newProject : project))
    } else {
      updatedProjects = [...projects, newProject]
    }

    saveProjects(updatedProjects)
    resetForm()
  }

  const resetForm = () => {
    setFormData({
      projectName: "",
      address: "",
      latitude: "",
      longitude: "",
    })
    setErrors({})
    setShowForm(false)
    setEditingProject(null)
  }

  const handleEdit = (project) => {
    setFormData(project)
    setEditingProject(project)
    setShowForm(true)
  }

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this project?")) {
      const updatedProjects = projects.filter((project) => project.id !== id)
      saveProjects(updatedProjects)
    }
  }

  const exportToExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(projects)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "Projects")
    XLSX.writeFile(workbook, "projects.xlsx")
  }

  const openInMaps = (latitude, longitude) => {
    const url = `https://www.google.com/maps?q=${latitude},${longitude}`
    window.open(url, "_blank")
  }

  return (
    <div className="project-management">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">üèóÔ∏è</div>
          <h2>Project Management</h2>
        </div>
        <div className="header-actions">
          <button className="btn btn-export" onClick={exportToExcel}>
            üìä Export Excel
          </button>
          <button className="btn btn-add" onClick={() => setShowForm(true)}>
            ‚ûï Add Project
          </button>
        </div>
      </div>

      {showForm && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>{editingProject ? "Edit Project" : "Add New Project"}</h3>
              <button className="close-btn" onClick={resetForm}>
                ‚úï
              </button>
            </div>

            <form onSubmit={handleSubmit} className="project-form">
              <div className="form-grid">
                <div className="form-group">
                  <label>Project Name *</label>
                  <input
                    type="text"
                    value={formData.projectName}
                    onChange={(e) => setFormData({ ...formData, projectName: e.target.value })}
                    className={errors.projectName ? "error" : ""}
                    placeholder="Enter project name"
                  />
                  {errors.projectName && <span className="error-text">{errors.projectName}</span>}
                </div>

                <div className="form-group full-width">
                  <label>Address *</label>
                  <textarea
                    value={formData.address}
                    onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                    className={errors.address ? "error" : ""}
                    placeholder="Enter project address"
                    rows="4"
                  />
                  {errors.address && <span className="error-text">{errors.address}</span>}
                </div>

                <div className="form-group">
                  <label>Latitude *</label>
                  <input
                    type="number"
                    step="any"
                    value={formData.latitude}
                    onChange={(e) => setFormData({ ...formData, latitude: e.target.value })}
                    className={errors.latitude ? "error" : ""}
                    placeholder="Enter latitude"
                  />
                  {errors.latitude && <span className="error-text">{errors.latitude}</span>}
                </div>

                <div className="form-group">
                  <label>Longitude *</label>
                  <input
                    type="number"
                    step="any"
                    value={formData.longitude}
                    onChange={(e) => setFormData({ ...formData, longitude: e.target.value })}
                    className={errors.longitude ? "error" : ""}
                    placeholder="Enter longitude"
                  />
                  {errors.longitude && <span className="error-text">{errors.longitude}</span>}
                </div>
              </div>

              <div className="form-actions">
                <button type="button" className="btn btn-cancel" onClick={resetForm}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-save">
                  {editingProject ? "Update" : "Save"} Project
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="projects-table">
        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>Project Name</th>
              <th>Address</th>
              <th>Coordinates</th>
              <th>Created Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {projects.map((project, index) => (
