"use client"

import { useState, useEffect } from "react"

const CreateBatchSlip = ({ onNavigate }) => {
  const [formData, setFormData] = useState({
    batchNumber: "",
    batchDate: new Date().toISOString().split("T")[0],
    customer: "",
    recipeCode: "",
    recipeName: "",
    quantity: "",
    orderedQuantity: "",
    mixerCapacity: "",
    batchSize: "",
    adjManualQuantity: "",
    withThisLoad: "",
    truckNumber: "",
    truckDriver: "",
    orderNumber: "",
    batcherName: "",
    batchStartTime: "",
    batchEndTime: "",
    plantSerialNumber: "",
  })

  const [errors, setErrors] = useState({})

  const recipeOptions = [
    { code: "M25", name: "M25 Grade Concrete" },
    { code: "M30", name: "M30 Grade Concrete" },
    { code: "M35", name: "M35 Grade Concrete" },
    { code: "M40", name: "M40 Grade Concrete" },
    { code: "M45", name: "M45 Grade Concrete" },
  ]

  // Generate batch number automatically
  useEffect(() => {
    if (!formData.batchNumber) {
      setFormData((prev) => ({
        ...prev,
        batchNumber: generateBatchSlipNo(),
      }))
    }
  }, [])

  const generateBatchSlipNo = () => {
    const date = new Date()
    const year = date.getFullYear()
    const month = String(date.getMonth() + 1).padStart(2, "0")
    const day = String(date.getDate()).padStart(2, "0")
    const sequence = String(Math.floor(Math.random() * 1000) + 1).padStart(3, "0")
    return `BS${year}${month}${day}${sequence}`
  }

  const handleInputChange = (e) => {
    const { name, value } = e.target
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }))

    // Auto-fill recipe name when recipe code is selected
    if (name === "recipeCode") {
      const selectedRecipe = recipeOptions.find((recipe) => recipe.code === value)
      setFormData((prev) => ({
        ...prev,
        recipeCode: value,
        recipeName: selectedRecipe ? selectedRecipe.name : "",
      }))
    }

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors((prev) => ({
        ...prev,
        [name]: "",
      }))
    }
  }

  const validateForm = () => {
    const newErrors = {}
    const requiredFields = [
      "batchNumber",
      "batchDate",
      "customer",
      "recipeCode",
      "quantity",
      "orderedQuantity",
      "truckNumber",
      "truckDriver",
      "batcherName",
    ]

    requiredFields.forEach((field) => {
      if (!formData[field] || formData[field].trim() === "") {
        newErrors[field] = "This field is required"
      }
    })

    // Validate numeric fields
    const numericFields = ["quantity", "orderedQuantity", "mixerCapacity", "batchSize"]
    numericFields.forEach((field) => {
      if (formData[field] && isNaN(Number.parseFloat(formData[field]))) {
        newErrors[field] = "Please enter a valid number"
      }
    })

    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSave = (e) => {
    e.preventDefault()
    if (validateForm()) {
      // Save to localStorage for passing to BatchSlipDetails
      localStorage.setItem("pendingBatchSlip", JSON.stringify(formData))

      // Navigate to BatchSlipDetails
      if (onNavigate) {
        onNavigate("BatchSlipDetails")
      }
    }
  }

  const resetForm = () => {
    setFormData({
      batchNumber: "",
      batchDate: new Date().toISOString().split("T")[0],
      customer: "",
      recipeCode: "",
      recipeName: "",
      quantity: "",
      orderedQuantity: "",
      mixerCapacity: "",
      batchSize: "",
      adjManualQuantity: "",
      withThisLoad: "",
      truckNumber: "",
      truckDriver: "",
      orderNumber: "",
      batcherName: "",
      batchStartTime: "",
      batchEndTime: "",
      plantSerialNumber: "",
    })
    setErrors({})
  }

  return (
    <div className="create-batch-slip">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">📝</div>
          <h2>Create Batch Slip</h2>
        </div>
        <div className="header-actions">
          <button
            type="button"
            className="btn btn-generate"
            onClick={() => setFormData({ ...formData, batchNumber: generateBatchSlipNo() })}
          >
            🔄 Generate Slip No
          </button>
        </div>
      </div>

      <div className="enhanced-form-container">
        <div className="form-card">
          <div className="form-card-header">
            <h3>Batch Slip Information</h3>
            <p>Fill in the details to create a new batch slip</p>
          </div>

          <form onSubmit={handleSave} className="enhanced-form">
            <div className="enhanced-form-grid">
              {/* Left Column */}
              <div className="form-column">
                <div className="form-group">
                  <label>Batch Number *</label>
                  <input
                    type="text"
                    name="batchNumber"
                    value={formData.batchNumber}
                    onChange={handleInputChange}
                    className={errors.batchNumber ? "error" : ""}
                    placeholder="Auto-generated"
                  />
                  {errors.batchNumber && <span className="error-text">{errors.batchNumber}</span>}
                </div>

                <div className="form-group">
                  <label>Batch Date *</label>
                  <input
                    type="date"
                    name="batchDate"
                    value={formData.batchDate}
                    onChange={handleInputChange}
                    className={errors.batchDate ? "error" : ""}
                  />
                  {errors.batchDate && <span className="error-text">{errors.batchDate}</span>}
                </div>

                <div className="form-group">
                  <label>Customer *</label>
                  <input
                    type="text"
                    name="customer"
                    value={formData.customer}
                    onChange={handleInputChange}
                    className={errors.customer ? "error" : ""}
                    placeholder="Enter customer name"
                  />
                  {errors.customer && <span className="error-text">{errors.customer}</span>}
                </div>

                <div className="form-group">
                  <label>Recipe Code *</label>
                  <select
                    name="recipeCode"
                    value={formData.recipeCode}
                    onChange={handleInputChange}
                    className={errors.recipeCode ? "error" : ""}
                  >
                    <option value="">Select Recipe</option>
                    {recipeOptions.map((recipe) => (
                      <option key={recipe.code} value={recipe.code}>
                        {recipe.code}
                      </option>
                    ))}
                  </select>
                  {errors.recipeCode && <span className="error-text">{errors.recipeCode}</span>}
                </div>

                <div className="form-group">
                  <label>Recipe Name</label>
                  <input
                    type="text"
                    name="recipeName"
                    value={formData.recipeName}
                    onChange={handleInputChange}
                    placeholder="Auto-filled from recipe code"
                    readOnly
                    className="readonly"
                  />
                </div>

                <div className="form-group">
                  <label>Quantity (M³) *</label>
                  <input
                    type="number"
                    step="0.01"
                    name="quantity"
                    value={formData.quantity}
                    onChange={handleInputChange}
                    className={errors.quantity ? "error" : ""}
                    placeholder="0.00"
                  />
                  {errors.quantity && <span className="error-text">{errors.quantity}</span>}
                </div>

                <div className="form-group">
                  <label>Ordered Quantity (M³) *</label>
                  <input
                    type="number"
                    step="0.01"
                    name="orderedQuantity"
                    value={formData.orderedQuantity}
                    onChange={handleInputChange}
                    className={errors.orderedQuantity ? "error" : ""}
                    placeholder="0.00"
                  />
                  {errors.orderedQuantity && <span className="error-text">{errors.orderedQuantity}</span>}
                </div>

                <div className="form-group">
                  <label>Mixer Capacity (M³)</label>
                  <input
                    type="number"
                    step="0.01"
                    name="mixerCapacity"
                    value={formData.mixerCapacity}
                    onChange={handleInputChange}
                    className={errors.mixerCapacity ? "error" : ""}
                    placeholder="0.00"
                  />
                  {errors.mixerCapacity && <span className="error-text">{errors.mixerCapacity}</span>}
                </div>

                <div className="form-group">
                  <label>Batch Size (M³)</label>
                  <input
                    type="number"
                    step="0.01"
                    name="batchSize"
                    value={formData.batchSize}
                    onChange={handleInputChange}
                    className={errors.batchSize ? "error" : ""}
                    placeholder="0.00"
                  />
                  {errors.batchSize && <span className="error-text">{errors.batchSize}</span>}
                </div>
              </div>

              {/* Right Column */}
              <div className="form-column">
                <div className="form-group">
                  <label>Adj/Manual Quantity (M³)</label>
                  <input
                    type="number"
                    step="0.01"
                    name="adjManualQuantity"
                    value={formData.adjManualQuantity}
                    onChange={handleInputChange}
                    placeholder="0.00"
                  />
                </div>

                <div className="form-group">
                  <label>With This Load (M³)</label>
                  <input
                    type="number"
                    step="0.01"
                    name="withThisLoad"
                    value={formData.withThisLoad}
                    onChange={handleInputChange}
                    placeholder="0.00"
                  />
                </div>

                <div className="form-group">
                  <label>Truck Number *</label>
                  <input
                    type="text"
                    name="truckNumber"
                    value={formData.truckNumber}
                    onChange={handleInputChange}
                    className={errors.truckNumber ? "error" : ""}
                    placeholder="Enter truck number"
                  />
                  {errors.truckNumber && <span className="error-text">{errors.truckNumber}</span>}
                </div>

                <div className="form-group">
                  <label>Truck Driver *</label>
                  <input
                    type="text"
                    name="truckDriver"
                    value={formData.truckDriver}
                    onChange={handleInputChange}
                    className={errors.truckDriver ? "error" : ""}
                    placeholder="Enter driver name"
                  />
                  {errors.truckDriver && <span className="error-text">{errors.truckDriver}</span>}
                </div>

                <div className="form-group">
                  <label>Order Number</label>
                  <input
                    type="text"
                    name="orderNumber"
                    value={formData.orderNumber}
                    onChange={handleInputChange}
                    placeholder="Enter order number"
                  />
                </div>

                <div className="form-group">
                  <label>Batcher Name *</label>
                  <input
                    type="text"
                    name="batcherName"
                    value={formData.batcherName}
                    onChange={handleInputChange}
                    className={errors.batcherName ? "error" : ""}
                    placeholder="Enter batcher name"
                  />
                  {errors.batcherName && <span className="error-text">{errors.batcherName}</span>}
                </div>

                <div className="form-group">
                  <label>Batch Start Time</label>
                  <input
                    type="time"
                    name="batchStartTime"
                    value={formData.batchStartTime}
                    onChange={handleInputChange}
                  />
                </div>

                <div className="form-group">
                  <label>Batch End Time</label>
                  <input type="time" name="batchEndTime" value={formData.batchEndTime} onChange={handleInputChange} />
                </div>

                <div className="form-group">
                  <label>Plant Serial Number</label>
                  <input
                    type="text"
                    name="plantSerialNumber"
                    value={formData.plantSerialNumber}
                    onChange={handleInputChange}
                    placeholder="Enter plant serial number"
                  />
                </div>
              </div>
            </div>

            <div className="form-actions">
              <button type="button" className="btn btn-cancel" onClick={resetForm}>
                Reset Form
              </button>
              <button type="submit" className="btn btn-save">
                Save & Continue
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}

export default CreateBatchSlip
