"use client"

import { useState, useEffect } from "react"
import * as XLSX from "xlsx"

const Logs = () => {
  const [logs, setLogs] = useState([])
  const [filteredLogs, setFilteredLogs] = useState([])
  const [branches, setBranches] = useState([])
  const [filters, setFilters] = useState({
    dateFrom: "",
    dateTo: "",
    branch: "All",
    user: "",
    module: "All",
    action: "All",
  })

  const modules = [
    "Employee Management",
    "Branch Management",
    "Project Management",
    "Role Management",
    "Vehicle Entry",
    "Supplier Details",
    "PO Details",
    "GRN Management",
    "System",
  ]

  const actions = ["Add", "Update", "Delete", "Inaction", "Login", "Logout", "View", "Export"]

  useEffect(() => {
    // Load logs and branches from localStorage
    const savedLogs = localStorage.getItem("systemLogs")
    const savedBranches = localStorage.getItem("branches")

    if (savedLogs) {
      const logData = JSON.parse(savedLogs)
      setLogs(logData)
      setFilteredLogs(logData)
    } else {
      // Generate sample logs for demonstration
      generateSampleLogs()
    }

    if (savedBranches) {
      setBranches(JSON.parse(savedBranches))
    }
  }, [])

  const generateSampleLogs = () => {
    const sampleLogs = [
      {
        id: 1,
        sno: 1,
        branchName: "Main Branch",
        moduleName: "Employee Management",
        actionPerformed: "Add",
        actionBy: "admin",
        actionOn: "John Doe Employee Record",
        timestamp: new Date(Date.now() - 86400000).toISOString(),
        ipAddress: "192.168.1.100",
      },
      {
        id: 2,
        sno: 2,
        branchName: "Main Branch",
        moduleName: "System",
        actionPerformed: "Login",
        actionBy: "admin",
        actionOn: "System Login",
        timestamp: new Date(Date.now() - 3600000).toISOString(),
        ipAddress: "192.168.1.100",
      },
      {
        id: 3,
        sno: 3,
        branchName: "Branch A",
        moduleName: "Vehicle Entry",
        actionPerformed: "Add",
        actionBy: "user1",
        actionOn: "Vehicle Entry Record",
        timestamp: new Date(Date.now() - 7200000).toISOString(),
        ipAddress: "192.168.1.101",
      },
    ]

    setLogs(sampleLogs)
    setFilteredLogs(sampleLogs)
    localStorage.setItem("systemLogs", JSON.stringify(sampleLogs))
  }

  const addLog = (logData) => {
    const newLog = {
      id: Date.now(),
      sno: logs.length + 1,
      timestamp: new Date().toISOString(),
      ipAddress: "192.168.1.100", // This would be dynamic in a real app
      ...logData,
    }

    const updatedLogs = [newLog, ...logs]
    setLogs(updatedLogs)
    localStorage.setItem("systemLogs", JSON.stringify(updatedLogs))
  }

  useEffect(() => {
    let filtered = logs

    // Filter by date range
    if (filters.dateFrom) {
      filtered = filtered.filter((log) => new Date(log.timestamp) >= new Date(filters.dateFrom))
    }

    if (filters.dateTo) {
      filtered = filtered.filter((log) => new Date(log.timestamp) <= new Date(filters.dateTo + "T23:59:59"))
    }

    // Filter by branch
    if (filters.branch !== "All") {
      filtered = filtered.filter((log) => log.branchName === filters.branch)
    }

    // Filter by user
    if (filters.user) {
      filtered = filtered.filter((log) => log.actionBy.toLowerCase().includes(filters.user.toLowerCase()))
    }

    // Filter by module
    if (filters.module !== "All") {
      filtered = filtered.filter((log) => log.moduleName === filters.module)
    }

    // Filter by action
    if (filters.action !== "All") {
      filtered = filtered.filter((log) => log.actionPerformed === filters.action)
    }

    setFilteredLogs(filtered)
  }, [logs, filters])

  const handleFilterChange = (field, value) => {
    setFilters((prev) => ({
      ...prev,
      [field]: value,
    }))
  }

  const clearFilters = () => {
    setFilters({
      dateFrom: "",
      dateTo: "",
      branch: "All",
      user: "",
      module: "All",
      action: "All",
    })
  }

  const exportToExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(filteredLogs)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "System Logs")
    XLSX.writeFile(workbook, "system_logs.xlsx")
  }

  const exportToPDF = () => {
    const printWindow = window.open("", "_blank")
    const tableHTML = document.querySelector(".logs-table").outerHTML

    printWindow.document.write(`
      <html>
        <head>
          <title>System Logs Report</title>
          <style>
            body { font-family: Arial, sans-serif; }
            table { width: 100%; border-collapse: collapse; }
            th, td { border: 1px solid #ddd; padding: 8px; text-align: left; font-size: 12px; }
            th { background-color: #f2f2f2; }
            .action-add { color: green; }
            .action-update { color: blue; }
            .action-delete { color: red; }
            .action-login { color: purple; }
          </style>
        </head>
        <body>
          <h1>System Logs Report</h1>
          <p>Generated on: ${new Date().toLocaleDateString()}</p>
          <p>Total Records: ${filteredLogs.length}</p>
          ${tableHTML}
        </body>
      </html>
    `)

    printWindow.document.close()
    printWindow.print()
  }

  const getActionClass = (action) => {
    switch (action.toLowerCase()) {
      case "add":
        return "action-add"
      case "update":
        return "action-update"
      case "delete":
        return "action-delete"
      case "login":
        return "action-login"
      case "logout":
        return "action-logout"
      default:
        return "action-default"
    }
  }

  return (
    <div className="logs">
      <div className="section-header">
        <h2>System Logs</h2>
        <div className="header-actions">
          <button className="btn btn-export" onClick={exportToExcel}>
            📊 Export Excel
          </button>
          <button className="btn btn-export" onClick={exportToPDF}>
            📄 Export PDF
          </button>
        </div>
      </div>

      <div className="filters-section">
        <div className="filters-grid">
          <div className="filter-group">
            <label>Date From</label>
            <input
              type="date"
              value={filters.dateFrom}
              onChange={(e) => handleFilterChange("dateFrom", e.target.value)}
              className="filter-input"
            />
          </div>

          <div className="filter-group">
            <label>Date To</label>
            <input
              type="date"
              value={filters.dateTo}
              onChange={(e) => handleFilterChange("dateTo", e.target.value)}
              className="filter-input"
            />
          </div>

          <div className="filter-group">
            <label>Branch</label>
            <select
              value={filters.branch}
              onChange={(e) => handleFilterChange("branch", e.target.value)}
              className="filter-select"
            >
              <option value="All">All Branches</option>
              {branches.map((branch) => (
                <option key={branch.id} value={branch.branchName}>
                  {branch.branchName}
                </option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>User</label>
            <input
              type="text"
              value={filters.user}
              onChange={(e) => handleFilterChange("user", e.target.value)}
              placeholder="Search by user"
              className="filter-input"
            />
          </div>

          <div className="filter-group">
            <label>Module</label>
            <select
              value={filters.module}
              onChange={(e) => handleFilterChange("module", e.target.value)}
              className="filter-select"
            >
              <option value="All">All Modules</option>
              {modules.map((module) => (
                <option key={module} value={module}>
                  {module}
                </option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>Action</label>
            <select
              value={filters.action}
              onChange={(e) => handleFilterChange("action", e.target.value)}
              className="filter-select"
            >
              <option value="All">All Actions</option>
              {actions.map((action) => (
                <option key={action} value={action}>
                  {action}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="filter-actions">
          <button className="btn btn-clear" onClick={clearFilters}>
            Clear Filters
          </button>
          <span className="results-count">
            Showing {filteredLogs.length} of {logs.length} records
          </span>
        </div>
      </div>

      <div className="logs-table">
        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>Branch Name</th>
              <th>Module Name</th>
              <th>Action Performed</th>
              <th>Action By</th>
              <th>Action On</th>
              <th>Timestamp</th>
              <th>IP Address</th>
            </tr>
          </thead>
          <tbody>
            {filteredLogs.map((log, index) => (
              <tr key={log.id}>
                <td>{index + 1}</td>
                <td>{log.branchName}</td>
                <td>{log.moduleName}</td>
                <td>
                  <span className={`action-badge ${getActionClass(log.actionPerformed)}`}>{log.actionPerformed}</span>
                </td>
                <td>{log.actionBy}</td>
                <td>{log.actionOn}</td>
                <td>{new Date(log.timestamp).toLocaleString()}</td>
                <td>{log.ipAddress}</td>
              </tr>
            ))}
          </tbody>
        </table>

        {filteredLogs.length === 0 && (
          <div className="no-data">
            <p>No logs found matching the selected criteria</p>
          </div>
        )}
      </div>

      <div className="logs-summary">
        <div className="summary-cards">
          <div className="summary-card">
            <h4>Total Logs</h4>
            <span className="summary-number">{logs.length}</span>
          </div>
          <div className="summary-card">
            <h4>Filtered Results</h4>
            <span className="summary-number">{filteredLogs.length}</span>
          </div>
          <div className="summary-card">
            <h4>Today's Logs</h4>
            <span className="summary-number">
              {logs.filter((log) => new Date(log.timestamp).toDateString() === new Date().toDateString()).length}
            </span>
          </div>
          <div className="summary-card">
            <h4>Active Users</h4>
            <span className="summary-number">{[...new Set(logs.map((log) => log.actionBy))].length}</span>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Logs
