"use client"

import { useState, useEffect } from "react"
import * as XLSX from "xlsx"

const InventoryManagement = () => {
  const [inventory, setInventory] = useState([])
  const [dashboardStats, setDashboardStats] = useState({
    totalItems: 0,
    totalValue: 0,
    lowStockItems: 0,
    recentEntries: 0,
  })
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState("")
  const [filterStatus, setFilterStatus] = useState("All")

  useEffect(() => {
    fetchInventoryData()
    fetchDashboardStats()
  }, [])

  const fetchInventoryData = async () => {
    try {
      setLoading(true)
      const response = await fetch("http://localhost:5000/inventory")
      if (response.ok) {
        const data = await response.json()
        setInventory(data)
      } else {
        // Fallback: create inventory from batch slips
        await createInventoryFromBatchSlips()
      }
    } catch (error) {
      console.error("Error fetching inventory:", error)
      await createInventoryFromBatchSlips()
    } finally {
      setLoading(false)
    }
  }

  const createInventoryFromBatchSlips = async () => {
    try {
      // Fetch batch slips to create inventory
      const batchResponse = await fetch("http://localhost:5000/batch-slips")
      if (batchResponse.ok) {
        const batchSlips = await batchResponse.json()

        // Group by material type and sum quantities
        const inventoryMap = {}

        batchSlips.forEach((batch) => {
          const key = `${batch.material_type}_${batch.unit}`
          if (inventoryMap[key]) {
            inventoryMap[key].quantity += batch.quantity
            inventoryMap[key].totalValue += batch.total_amount
            inventoryMap[key].lastUpdated = new Date(
              Math.max(new Date(inventoryMap[key].lastUpdated), new Date(batch.received_date)),
            )
          } else {
            inventoryMap[key] = {
              id: Object.keys(inventoryMap).length + 1,
              material_type: batch.material_type,
              quantity: batch.quantity,
              unit: batch.unit,
              rate: batch.rate,
              totalValue: batch.total_amount,
              supplier: batch.supplier_name,
              lastUpdated: new Date(batch.received_date),
              status: batch.quantity < 10 ? "Low Stock" : "In Stock",
            }
          }
        })

        setInventory(Object.values(inventoryMap))
      }
    } catch (error) {
      console.error("Error creating inventory from batch slips:", error)
      setInventory([])
    }
  }

  const fetchDashboardStats = async () => {
    try {
      // Fetch various stats from different endpoints
      const [batchResponse, vehicleResponse, supplierResponse] = await Promise.all([
        fetch("http://localhost:5000/batch-slips"),
        fetch("http://localhost:5000/vehicles"),
        fetch("http://localhost:5000/suppliers"),
      ])

      let totalItems = 0
      let totalValue = 0
      let recentEntries = 0

      if (batchResponse.ok) {
        const batchSlips = await batchResponse.json()
        totalItems = batchSlips.length
        totalValue = batchSlips.reduce((sum, batch) => sum + (batch.total_amount || 0), 0)

        // Count entries from last 7 days
        const weekAgo = new Date()
        weekAgo.setDate(weekAgo.getDate() - 7)
        recentEntries = batchSlips.filter((batch) => new Date(batch.received_date) > weekAgo).length
      }

      // Count low stock items (assuming quantity < 10 is low stock)
      const lowStockItems = inventory.filter((item) => item.quantity < 10).length

      setDashboardStats({
        totalItems,
        totalValue,
        lowStockItems,
        recentEntries,
      })
    } catch (error) {
      console.error("Error fetching dashboard stats:", error)
    }
  }

  const exportToExcel = () => {
    const exportData = inventory.map((item, index) => ({
      "S.No": index + 1,
      "Material Type": item.material_type,
      Quantity: item.quantity,
      Unit: item.unit,
      Rate: item.rate,
      "Total Value": item.totalValue,
      Supplier: item.supplier,
      Status: item.status,
      "Last Updated": new Date(item.lastUpdated).toLocaleDateString(),
    }))

    const worksheet = XLSX.utils.json_to_sheet(exportData)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "Inventory")
    XLSX.writeFile(workbook, "inventory.xlsx")
  }

  const filteredInventory = inventory.filter((item) => {
    const matchesSearch =
      item.material_type.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (item.supplier || "").toLowerCase().includes(searchTerm.toLowerCase())
    const matchesFilter = filterStatus === "All" || item.status === filterStatus
    return matchesSearch && matchesFilter
  })

  const refreshData = () => {
    fetchInventoryData()
    fetchDashboardStats()
  }

  return (
    <div className="inventory-management">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">üì¶</div>
          <h2>Inventory Management</h2>
        </div>
        <div className="header-actions">
          <button className="btn btn-refresh" onClick={refreshData} disabled={loading}>
            üîÑ Refresh
          </button>
          <button className="btn btn-export" onClick={exportToExcel} disabled={inventory.length === 0}>
            üìä Export Excel
          </button>
        </div>
      </div>

      {/* Dashboard Overview */}
      <div className="dashboard-overview">
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon">üìã</div>
            <div className="stat-info">
              <h3>Total Items</h3>
              <p className="stat-number">{loading ? "..." : dashboardStats.totalItems}</p>
              <span className="stat-change">Material types</span>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">üí∞</div>
            <div className="stat-info">
              <h3>Total Value</h3>
              <p className="stat-number">‚Çπ{loading ? "..." : dashboardStats.totalValue.toLocaleString()}</p>
              <span className="stat-change">Inventory worth</span>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">‚ö†Ô∏è</div>
            <div className="stat-info">
              <h3>Low Stock</h3>
              <p className="stat-number">{loading ? "..." : dashboardStats.lowStockItems}</p>
              <span className="stat-change negative">Items need reorder</span>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">üìà</div>
            <div className="stat-info">
              <h3>Recent Entries</h3>
              <p className="stat-number">{loading ? "..." : dashboardStats.recentEntries}</p>
              <span className="stat-change positive">Last 7 days</span>
            </div>
          </div>
        </div>
      </div>

      {/* Filters and Search */}
      <div className="inventory-controls">
        <div className="search-filter-group">
          <input
            type="text"
            placeholder="Search materials or suppliers..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <select value={filterStatus} onChange={(e) => setFilterStatus(e.target.value)} className="filter-select">
            <option value="All">All Status</option>
            <option value="In Stock">In Stock</option>
            <option value="Low Stock">Low Stock</option>
            <option value="Out of Stock">Out of Stock</option>
          </select>
        </div>
      </div>

      {/* Inventory Table */}
      <div className="inventory-table">
        {loading ? (
          <div className="loading-state">Loading inventory...</div>
        ) : (
          <table>
            <thead>
              <tr>
                <th>S.No</th>
                <th>Material Type</th>
                <th>Quantity</th>
                <th>Unit</th>
                <th>Rate</th>
                <th>Total Value</th>
                <th>Supplier</th>
                <th>Status</th>
                <th>Last Updated</th>
              </tr>
            </thead>
            <tbody>
              {filteredInventory.map((item, index) => (
                <tr key={item.id}>
                  <td>{index + 1}</td>
                  <td>{item.material_type}</td>
                  <td>{item.quantity}</td>
                  <td>{item.unit}</td>
                  <td>‚Çπ{item.rate}</td>
                  <td>‚Çπ{item.totalValue?.toLocaleString()}</td>
                  <td>{item.supplier}</td>
                  <td>
                    <span className={`status ${item.status?.toLowerCase().replace(" ", "-")}`}>{item.status}</span>
                  </td>
                  <td>{new Date(item.lastUpdated).toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        {!loading && filteredInventory.length === 0 && (
          <div className="no-data">
            <p>No inventory items found</p>
          </div>
        )}
      </div>

      <style jsx>{`
        .dashboard-overview {
          margin-bottom: 30px;
        }

        .stats-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
