"use client"

import { useState, useEffect } from "react"
import * as XLSX from "xlsx"
import axios from "axios"

const VehicleEntry = () => {
  const [vehicleEntries, setVehicleEntries] = useState([])
  const [videoFile, setVideoFile] = useState(null)
  const [videoPreview, setVideoPreview] = useState(null)
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    const savedEntries = localStorage.getItem("vehicleEntries")
    if (savedEntries) {
      setVehicleEntries(JSON.parse(savedEntries))
    }
  }, [])

  const saveVehicleEntries = (entries) => {
    localStorage.setItem("vehicleEntries", JSON.stringify(entries))
    setVehicleEntries(entries)
  }

  const exportToExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(vehicleEntries)
    const workbook = XLSX.utils.book_new()
    XLSX.utils.book_append_sheet(workbook, worksheet, "Vehicle Entries")
    XLSX.writeFile(workbook, "vehicle_entries.xlsx")
  }

  const handleDelete = (id) => {
    const updatedEntries = vehicleEntries.filter((entry) => entry.id !== id)
    saveVehicleEntries(updatedEntries)
  }

  const handleVideoUpload = (e) => {
    const file = e.target.files[0]
    if (!file) return
    setVideoFile(file)
    const videoUrl = URL.createObjectURL(file)
    setVideoPreview(videoUrl)
  }

  const handleVideoProcessing = async () => {
  if (!videoFile) return alert("Please upload a video first.")
  const formData = new FormData()
  formData.append("file", videoFile)
  setLoading(true)

  try {
    const res = await axios.post("http://127.0.0.1:8000/upload/", formData)
    const data = res.data

    // If vehicle not matched — show popup but still display in table
    if (!data.matched) {
      alert("❌ Vehicle not registered! Entry will not be saved.")
    }

    // Create a new table row object
    const newEntry = {
      id: Date.now(),
      inwardNumber: `INB${(vehicleEntries.length + 1).toString().padStart(3, "0")}`,
      vehicleNumber: data.vehicle_number || "N/A",
      supplierName: "THULASI READYMIX", // You can add dropdown/input later
      materialType: "",
      date: new Date(data.timestamp).toLocaleDateString(),
      time: new Date(data.timestamp).toLocaleTimeString(),
      videoFile: videoFile.name,
    }

    // Add it to table regardless of matched or not
    const updated = [...vehicleEntries, newEntry]
    saveVehicleEntries(updated)

  } catch (error) {
    console.error("Error during processing:", error)
    alert("Error during vehicle verification.")
  } finally {
    setLoading(false)
  }
}

  return (
    <div className="vehicle-entry">
      <div className="section-header">
        <div className="section-title">
          <div className="section-icon">🚛</div>
          <h2>Vehicle Entry</h2>
        </div>
        <div className="header-actions">
          <button className="btn btn-export" onClick={exportToExcel}>
            📊 Export Excel
          </button>
        </div>
      </div>

      <div className="video-feed-section">
        <div className="video-feed-container">
          <div className="video-feed-box">
            <video className="live-video-feed" autoPlay loop muted playsInline controls={false}>
              <source src={videoPreview || "/sample2.mp4"} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
            <div className="video-overlay">
              <div className="live-indicator">
                <span className="live-dot"></span>
                LIVE FEED
              </div>
            </div>
          </div>

          <div className="video-controls">
            <input
              type="file"
              accept="video/*,image/*"
              onChange={handleVideoUpload}
              className="video-upload-input"
              id="video-upload"
              style={{ display: "none" }}
            />
            <label htmlFor="video-upload" className="btn btn-upload-video">
              📁 Upload Video
            </label>
            {videoFile && (
              <>
                <span className="uploaded-file-name">📹 {videoFile.name}</span>
                <button className="btn btn-process" onClick={handleVideoProcessing} style={{ marginLeft: "10px" }}>
                  ▶️ Process Video
                </button>
              </>
            )}
          </div>
        </div>
        {loading && <p style={{ marginTop: "10px" }}>⏳ Checking number plates...</p>}
      </div>

      <div className="entries-table">
        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>Inward Number</th>
              <th>Vehicle Number</th>
              <th>Supplier Name</th>
              <th>Material Type</th>
              <th>Date & Time</th>
              <th>Video</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {vehicleEntries.map((entry, index) => (
              <tr key={entry.id}>
                <td>{index + 1}</td>
                <td>{entry.inwardNumber}</td>
                <td>{entry.vehicleNumber}</td>
                <td>{entry.supplierName}</td>
                <td>{entry.materialType}</td>
                <td>
                  {new Date(entry.date).toLocaleDateString()} {entry.time}
                </td>
                <td>
                  {entry.videoFile ? (
                    <span className="video-indicator">📹 {entry.videoFile}</span>
                  ) : (
                    <span className="no-video">No Video</span>
                  )}
                </td>
                <td>
                  <div className="action-buttons">
                    <button className="btn btn-delete" onClick={() => handleDelete(entry.id)} title="Delete Entry">
                      🗑️
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {vehicleEntries.length === 0 && (
          <div className="no-data">
            <p>No vehicle entries found</p>
          </div>
        )}
      </div>
    </div>
  )
}

export default VehicleEntry
